import React ,{ useState ,useEffect} from 'react'
import {Container,Row,Col,Card,CardBody,CardHeader,Button,Form,FormInput,FormGroup,FormSelect} from "shards-react";
import PageTitle from "../components/common/PageTitle";
import api from '../api/api'
import { useTranslation  } from "react-i18next";
import  Html5QrcodePlugin  from "../views/Html5QrcodePlugin";
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';

const AddSensorByAdmin = () => {

    const { t, i18n } = useTranslation();
    const history = useHistory()

    const [resultScan,setResultScan] = useState('')

   const onNewScanResult = (decodedText, decodedResult) => {
        setResultScan(decodedText)

        // Handle the result here.
      }

      const addNewSensor = () =>{
       let data = {
            code :resultScan
        }
        api.post('/admin/add-sensor',data)
        .then( response =>  {
          if(response.data.type === 'success'){
            swal(`${t('sensor_added')}`, {
                icon: "success",
            })
        }
        }).catch(err =>{
          swal({
            icon: 'error',
            title: 'Oops...',
            text: 'Error'
        })
        })
      }

  return (
    <Container fluid className="main-content-container px-4">
    {/* Page Header */}
    <Row noGutters className="page-header py-4">
      <PageTitle
        sm="4"
        title={t('add_sensor')}
        subtitle={t('add_sensor')}
        className="text-sm-left"
      />
    </Row>

    <Row>
      <Col lg="12" md="8" sm="12" className="mb-4">
        <Card small className="h-100">
          <CardHeader className="border-bottom">
          <div
            style={{
              display: "flex",
              justifyContent: "flex-start",
              width: "auto",
              float: "left"
            }}
          >
            <h6 className="m-0">{t('sensor_setup')}</h6>{" "}
            </div>
            <div
            style={{
              display: "flex",
              justifyContent: "flex-end",

            }}
          >
            <Button
              // theme="success"
              onClick={addNewSensor}
              className="mb-2 mr-1 btn btn-success"
            >
              <i class={`fa fa-check mx-2`}></i>
              {t('save')}
            </Button>
            <Button
              // theme="success"
              className="mb-2 mr-1 btn btn-danger"
              onClick={() => history.push('/admin/sensors')}

            >
              <i class={`fa fa-times mx-2`}></i>
              {t('cancel')}
            </Button>
            </div>
          </CardHeader>
          <CardBody className="pt-0">
            <div
              style={{
                display: "flex",
                marginTop: "20px",
                flexWrap: "wrap"
              }}
            >
              <Col lg="4" sm="12" md="6">
                <Form>
                  <Row form>
                    <Col md="12" className="form-group">
                      <p style={{ margin: "0px" }}>{t('sensor_code')}</p>
                      <FormInput
                            placeholder={t('sensor_code')}
                            value={resultScan}
                            onChange={e=> setResultScan(e.target.value)}
                            required

                          />
                    </Col>
                  </Row>
                  <FormGroup>
                    <p style={{ margin: "0px" }}>{t('desc')}</p>
                    <textarea
                      style={{ height: "220px" }}
                      class="form-control"
                      placeholder={t('desc')}
                    ></textarea>
                  </FormGroup>
                </Form>
              </Col>
              <Col lg="8" md="12" sm="12" className="mb-4">
              <div>
            <h3>{t('scan_qrcode_sensor')}</h3>
                <Html5QrcodePlugin 
                  fps={10}
                  qrbox={250}
                  disableFlip={true}
                  rememberLastUsedCamera={false}
                  qrCodeSuccessCallback={onNewScanResult}

                  />
              </div>
              </Col>
            </div>
          </CardBody>
        </Card>
      </Col>
    </Row>
  </Container>
  )
}

export default AddSensorByAdmin
