import React , {useState,useEffect} from 'react'
import { Container, Row, Col, CardBody, Card,CardHeader,CardFooter,FormInput } from "shards-react";
import PageTitle from "../components/common/PageTitle";
import { useTranslation } from 'react-i18next'
import LeafletMap from '../views/map';
import api from '../api/api';
import DBSensor from '../views/Sensor';
import moment from 'moment';
import { useHistory } from 'react-router-dom';
import img from '../images/avatars/default-avatar.png'

const DashboardSupplier = () => {

  const { t, i18n } = useTranslation();
  const history = useHistory()

  const [users,setUsers] = useState([])
  const [farms,setFarms] = useState([])
  const [fields,setFields] = useState([])
  const [sensors,setSensors] = useState([])
  const [SearchName,setSearchName] = useState('')

  const [draw,setDraw] = useState({
    polygon: false,
    circle: false,
    rectangle: false,
    polyline: false,
    marker: false,
    circlemarker: false
