const useSensorData = () => {
    const [sensorData, setSensorData] = useState([]);
    const [allDataSensor, setAllDataSensor] = useState([]);
    let location = useLocation();
    let userId = location.pathname.split('/')[2];
    let userUid = JSON.parse(localStorage.getItem('user')).id;
    let fieldUid = localStorage.getItem('Field');
    let url = '/sensor/sensor-update-data';

    if ((JSON.parse(localStorage.getItem('user')).role === "ROLE_SUPPLIER") && location.pathname === `/Fields/${userId}/${fieldUid}`) {
        url = '/supplier/sensor-data';
    }
    if ((JSON.parse(localStorage.getItem('user')).role === "ROLE_SUPPLIER") && location.pathname === `/Dashboard-supplier`) {
        url = '/supplier/get-sensors-data';
    }

    let data = {
        userUid: userUid,
        fieldUid: fieldUid,
        userId: userId
    };

    const applyMapping = (value, key, date, dataMappings) => {
        let mappedValue = value;
        let dateMappingSelected = "";

        // Gather all possible date mappings for the key
        const arrayDates = dataMappings.map(mapping => mapping.date[`${key}_date`]).filter(Boolean);

        // Sort dates and find the most recent valid date for the mapping
        if (arrayDates.length > 0) {
            arrayDates.push(date.slice(0, 10));
            arrayDates.sort();

            arrayDates.forEach((dateMapping, i) => {
                if (dateMapping === date.slice(0, 10) && typeof arrayDates[i - 1] !== "undefined") {
                    dateMappingSelected = arrayDates[i - 1];
                }
            });
        }

        // Apply mapping if the selected date exists
        if (dateMappingSelected) {
            dataMappings.forEach(mapping => {
                const keyData = `${key}_date`;
                const keyDataMax = `${key}_max`;
                const keyDataMin = `${key}_min`;

                if (dateMappingSelected === mapping.date[keyData]) {
                    const min = parseFloat(mapping.min[keyDataMin]);
                    const max = parseFloat(mapping.max[keyDataMax]);

                    if (value >= min && value <= max) {
                        mappedValue = (((value - min) / (max - min)) * 100).toFixed(2);
                    } else if (value < min) {
                        mappedValue = 100;
                    } else if (value > max) {
                        mappedValue = 0;
                    }
                }
            });
        }

        return mappedValue;
    };

    const getDataSensor = async () => {
        try {
            const response = await api.post(url, data);
            const sensors = response.data.sensors;
            const dataMappings = response.data.dataMapping;

            const updatedSensorData = sensors.map(sensor => {
                let mappedCharge = 0;
                const date = sensor.time;
                const vals = [sensor.mv1, sensor.mv2, sensor.mv3];

                if (sensor) {
                    const chargeMin = 73;
                    const chargeMax = 100;
                    mappedCharge = ((Number(sensor.charge) - chargeMin) / (chargeMax - chargeMin) * 100).toFixed(2);
                    mappedCharge = Math.max(0, Math.min(100, mappedCharge));
                }

                const mappedValues = vals.map((val, index) => {
                    const key = `Mv${index + 1}`;
                    return applyMapping(val, key, date, dataMappings);
                });

                return {
                    code: sensor.code,
                    sensor_id: sensor.sensor_id,
                    time: sensor.time,
                    temperature: sensor.temperature,
                    humidity: sensor.humidity,
                    pressure: sensor.pressure,
                    charge: mappedCharge,
                    signal: sensor.signal,
                    adc: sensor.adc,
                    ts: sensor.ts,
                    mv1: mappedValues[0],
                    mv2: mappedValues[1],
                    mv3: mappedValues[2],
                    originalmv1: sensor.mv1,
                    originalmv2: sensor.mv2,
                    originalmv3: sensor.mv3,
                    altitude: sensor.altitude,
                    mapping: response.data.dataMapping,
                };
            });

            setSensorData(sensors);
            setAllDataSensor(updatedSensorData);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getDataSensor();

        const intervalId = setInterval(() => {
            getDataSensor();
        }, 30000);

        return () => clearInterval(intervalId);
    }, []);

    return allDataSensor;
};

export default useSensorData;
