import React, { useEffect, useState } from "react";
import { Container, Row, Col, FormInput,Button,Card,CardBody } from "shards-react";
import { FormSelect } from "shards-react";
import "./../assets/Styles.css";
import PageTitle from "../components/common/PageTitle";
import DBSensor from "./Sensor";
import api from '../api/api';
import { useTranslation } from "react-i18next";
import LoadingSpinner from "../components/common/LoadingSpinner";
import drop from '../images/drop.png'
import drop1 from '../images/drop1.png'
import drop2 from '../images/drop2.png'
import temp from '../images/temp.png'
import humidity from '../images/humidity.png'
import press from '../images/press.png'
import useSensorData from "../utils/useSensorData";
import moment from "moment";
import { useHistory } from "react-router-dom";
import SmallStatsFields from "../components/common/SmallStatsFields";

export default function Sensors() {
  const { t, i18n } = useTranslation();
  const history = useHistory()
  const [dataSensors, setDataSensors] = useState([]);
  const [dataFields, setDataFields] = useState([]);
  const [dataCrops, setDataCrops] = useState([]);
  const [fields, setFields] = useState([]);
  const [searchCode, setSearchCode] = useState('')
  const [searchField, setSearchField] = useState('')
  const [searchState, setSearchState] = useState('')
  const [sensorData, setSensorData] = useState([]);
  const [readingtime,setReadingTime] = useState('')
  const [batteryLevel, setBatteryLevel] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [crops , setCrops] = useState([])
  const [dataSensor,setDataSensor] = useState({
    temp : '',
    humidity :'',
    pressure :'',
    humidityMV1 :'',
    humidityMV2 :'',
    humidityMV3 :'' 
  })
  const fetchDataSensors =  () => {
    api.get(`/sensor/sensors`)
      .then(response => {
        let sensorData = response.data;
        setTimeout(()=>{
          setDataSensors(sensorData);
          if(sensorData.length > 0 ){
            setSearchCode(sensorData[0].code)
          }
        },2600)
      }).catch(error=>{
        console.log(error)
      })
  };
  const fetchDataFields = async () => {
    await api.get(`/field/fields`)
      .then(response => {
        let fieldData = response.data.farms;
        setDataFields(fieldData);
      })
  };
  // const fetchDataCrops = async () => {
  //   await api.get('/crop/crops')
  //   .then(response => {
  //     let dataCrops = response.data.farms;
  //     setDataCrops(dataCrops)
  //     let Crops = [];
  //     dataCrops.map(farm => {
  //       let fields = farm.fields;
  //       if (fields) {
  //         fields.map(field => {
  //          let crops = field.crops
  //          if(crops){
  //           crops.map(crop=>{
  //             Crops.push({
  //               type : crop.type,
  //               id : crop.id,
  //               fieldId : crop.field_id
  //             })
  //           })
  //          }
  //         })
  //       }
  //     })
  //     setCrops(Crops)
  //   }).catch(err=>{
  //     console.log(err)
  //   })
  // }

  let sensorsData = useSensorData('/sensor/sensor-update-data')  

  useEffect(() => {
    fetchDataSensors();
    fetchDataFields()
    // fetchDataCrops()
  }, []);

  const getFields = () => {
    let Field = [];
    dataFields.map(farm => {
      let fields = farm.fields;
      if (fields) {
        fields.map(field => {
          Field.push({
            name: field.name,
            id: field.id,
            uid: field.uid,
            sensors : field.sensors
          })
        })
      }
    })
    setFields(Field)
  }

  useEffect(() => {
    getFields()
  }, [dataFields])


  const filteredSensors =  sensorsData.filter((sensor)=>{
    let charge = sensor.charge
    console.log(charge)
      if ((searchCode != '' && !searchState)) {
        return (
          sensor.code.toLowerCase().includes(searchCode.toLowerCase())

        )
      }
      if (searchState === 'online' ) {
        return (
          sensor.charge > 3.000 

        )
      }
      if (searchState === 'low') {
        return (
          sensor.charge <= 2.000

        )
      }
      if (searchState === 'offline') {
        let today = moment()
        return (
         moment(sensor.time.slice(0,10)).format('YYYY-MM-DD') != today.format('YYYY-MM-DD')

        )
      }
      return sensor
  }

  );

  useEffect(()=>{
    sensorsData.map(data=>{
      if(data){
        setDataSensor({
          temp : data.temperature,
          humidity : data.humidity,
          pressure: data.pressure / 1000,

        })
      }
    })
  },[sensorsData])

  const [mappingMv1,setMappingMv1] = useState("")
  const [mappingMv2,setMappingMv2] = useState("")
  const [mappingMv3,setMappingMv3] = useState("")

  const mappingMv = async (dateSensor, idSensor, vals) => {
    try {
      let dataMapping = {
        vals: vals,
        date: dateSensor,
        sensor_id: idSensor
      }
      let urlMapping = '/sensor/sensor-mapping'

      await api.post(urlMapping,dataMapping)
      .then(async response=>{
          if(response.data.type === "success"){
            setMappingMv1(response.data.valMapping[0])
            setMappingMv2(response.data.valMapping[1])
            setMappingMv3(response.data.valMapping[2])

          }
        }).catch(error=>{
          console.log(error)
        }).finally(()=>setIsLoading(false))

    } catch (error) {
      console.log(error)

    }
  }

  let smallStats3 = [
    {
      state: `20 cm (%)`,
      icon: <img style={{color:"#F6BE47"}} src={drop} alt="" />,
      label: mappingMv1,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    },
    {
      state: `40 cm (%)`,
      icon: <img src={drop2} alt="" />,
      label: mappingMv2,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    },
    {
      state: `60 cm (%)`,
      icon: <img src={drop1} alt="" />,
      label: mappingMv3,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    }
  ]
  let smallStats4 = [
    {
      state: `${t('Temp.')} (Â°C)`    ,
      icon: <img  src={temp} alt="" />,
      label: dataSensor.temp,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    },
    {
      state: `${t('Humidity')} (%)`,
      icon: <img src={humidity} alt="" />,
      label: dataSensor.humidity,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    },
    {
      state: `${t('Pression')} (kPa)`,
      icon: <img src={press} alt="" />,
      label: dataSensor.pressure ? parseFloat(dataSensor.pressure).toFixed(2) : "",
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "4", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(255,180,0,0.1)",
          borderColor: "rgb(255,180,0)",
          data: []
        }
      ]
    }
  ]

  useEffect(()=>{


    sensorsData.map(async sensors=>{
        await mappingMv(sensors.time, sensors.sensor_id, [sensors.mv1, sensors.mv2, sensors.mv3])                                 


    })

  },[sensorsData]) 
  return (
    <Container fluid className="main-content-container px-4">
      {/* Page Header */}
      <Row noGutters className="page-header d-flex justify-content-between align-items-center  py-4">
        <PageTitle
          sm="4"
          title={t('sensors')}
          subtitle={t('overview')}
          className="text-sm-left"
        />
         {/* <div>
            <Button theme="info" onClick={() => history.push('/my-history')}><i className='material-icons'>&#xe889;</i> View History</Button>
          </div> */}
      </Row>
      <Row
        style={{ display: "flex", flexDirection: "row", alignItems: "center" }}
      >
        <Col lg="6" md="8" sm="8" className="mb-4">
          <h6 className="m-0">{t('search_sensors')}</h6>
          <FormSelect
            value={searchCode}
            onChange={(e) => setSearchCode(e.target.value)}
          >
            <option value="" disabled selected>{t('sensor_code')}</option>
            {
              dataSensors.map(sensor=>{
                return <option value={sensor.code}>{sensor.code}</option>

              })
            }
            </FormSelect>
        </Col>
        <Col lg="6" md="8" sm="8" className="mb-4">
          {" "}
          <h6 className="m-0">{t('filter_state')}</h6>
          <FormSelect onChange={evt => { setSearchState(evt.target.value) }}>
            <option value='online'>{t('online')}</option>
            <option value='low'>{t('low_batt')}</option>
            <option value='offline'>{t('offline')}</option>
          </FormSelect>
        </Col>
        {/* <Col lg="4" md="8" sm="8" className="mb-4">
          {" "}
          <h6 className="m-0">{t('filter_field')}</h6>
          <FormSelect value={searchField} onChange={evt =>  setSearchField(evt.target.value)}>
            <option value="" >{t('select_field')}</option>
            {
              fields.map(field => (
                <option value={field.id}>{field.name}</option>
              ))
            }
          </FormSelect>
        </Col> */}
      </Row>
      <Row>
            <Col lg="6" md="6" sm="6" className="mb-4">
            <p style={{ margin: 0 }}>{t('sensor_stats_air')}</p>
            <Card small className="stats-small">
                <CardBody className="p-0 d-flex">
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      flexWrap: "wrap",
                      width: "100%"
                    }}
                  >
                    {smallStats4.map((stats, idx) => (
                      <>
                        <SmallStatsFields
                        // ToSensorPage={ToSensorPage}
                          style={{
                            borderColor: "blue",
                            borderWidth: 2,
                            borderStyle: "solid"
                          }}
                          id={`small-stats-${idx}`}
                          variation="1"
                          // chartData={stats.datasets}
                          // chartLabels={stats.chartLabels}
                          label={stats.label}
                          value={stats.value}
                          icon={stats.icon}
                          state={stats.state}
                        />
                        {idx != 2 ? (
                          <hr
                            style={{
                              backgroundColor: "#ebebeb",
                              width: "1px",
                              height: "75%"
                            }}
                          />
                        ) : (
                          <></>
                        )}
                      </>
                    ))}
                  </div>
                </CardBody>
            </Card>
          </Col>
          <Col lg="6" md="6" sm="6" className="mb-4">
            <p style={{ margin: 0 }}>{t('sensor_stats_soil')}</p>
            <Card small className="stats-small">
              <CardBody className="p-0 d-flex">
                <div
                  style={{
                    display: "flex",
                    flexDirection: "row",
                    flexWrap: "wrap",
                    width: "100%"
                  }}
                >
                  {smallStats3.map((stats, idx) => (
                    <>
                      <SmallStatsFields
                      // ToSensorPage={ToSensorPage}
                        style={{
                          borderColor: "blue",
                          borderWidth: 2,
                          borderStyle: "solid"
                        }}
                        id={`small-stats-${idx}`}
                        variation="1"
                        // chartData={stats.datasets}
                        // chartLabels={stats.chartLabels}
                        label={stats.label}
                        value={stats.value}
                        icon={stats.icon}
                        state={stats.state}
                      />
                      {idx != 2 ? (
                        <hr
                          style={{
                            backgroundColor: "#ebebeb",
                            width: "1px",
                            height: "75%"
                          }}
                        />
                      ) : (
                        <></>
                      )}
                    </>
                  ))}
                </div>
              </CardBody>
            </Card>
          </Col>

        </Row>


        {
          isLoading ?
          <LoadingSpinner />

            : 
            <Row>
              <DBSensor filteredSensors={filteredSensors} chargeLevel={batteryLevel} readingtime={readingtime}/>
          </Row>
        }

    </Container>
  );
}
