import moment from 'moment';
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { useTranslation } from "react-i18next";

const SensorHistoryChart = ({ data }) => {
  const [state, setState] = useState(null);
  const { t, i18n } = useTranslation();
  const [legendItems, setLegendItems] = useState([]); // State to hold legend items
  const getChartData = () => {
    let labels = [];
    let dataMv1 = [];
    let dataMv2 = [];
    let dataMv3 = [];
    let temperature = [];
    let humidity = [];
    let tempSubSoil = [];
    let altitude = [];
    let pressure = [];
    let array = []

    data &&
      data.forEach(sensorData => {
        const dateTime = moment(sensorData.time); // use the full timestamp
              const hour = dateTime.format('MMM Do HH:00'); // format date as "MMM Do HH:00"
        dataMv1.push(parseFloat(sensorData.mv1).toFixed(2));
        dataMv2.push(parseFloat(sensorData.mv2).toFixed(2));
        dataMv3.push(parseFloat(sensorData.mv3).toFixed(2));
        temperature.push(parseFloat(sensorData.temperature).toFixed(2));
        altitude.push(parseFloat(sensorData.altitude));
        pressure.push(parseFloat(sensorData.pressure).toFixed(2) / 1000);
        tempSubSoil.push(parseFloat(sensorData.ts).toFixed(2));
        humidity.push(parseFloat(sensorData.humidity).toFixed(2));
        labels.push(hour);

      });
      labels.reverse();
      dataMv1.reverse();
      dataMv2.reverse();
      dataMv3.reverse();
      temperature.reverse();
      altitude.reverse();
      pressure.reverse();
      tempSubSoil.reverse();
      humidity.reverse();
    const state = {
      labels: labels,
      datasets: [
        {
          label: 'Temperature',
          lineTension: 0.5,
          borderWidth: 2,
          data: temperature,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#f46835"
        },
        {
          label: 'Humidity',
          lineTension: 0.5,
          borderWidth: 2,
          data: humidity,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#2D40E5"
        },
        {
          label: 'Pressure',
          lineTension: 0.5,
          borderWidth: 2,
          data: pressure,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#4BC0C0"
        },
        {
          label: 'Altitude',
          lineTension: 0.5,
          borderWidth: 2,
          data: altitude,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#B7990D"
        },
        {
          label: 'Temperature subsoil',
          lineTension: 0.5,
          borderWidth: 2,
          data: tempSubSoil,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#8621e4"
        },
        {
          label: 'Niveau 1',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv1,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#FC5566"
        },
        {
          label: 'Niveau 2',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv2,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#3CB371"
        },
        {
          label: 'Niveau 3',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv3,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#1E90FF"
        },
      ]
    };
    setState(state);
  };

  useEffect(() => {
    getChartData();
  }, [data]);


     useEffect(() => {
    if (state) {
      // Generate legend items based on dataset labels
      const items = state.datasets.map((dataset, index) => ({
        text: dataset.label,
        fillStyle: dataset.borderColor,
        hidden: false,
        index,
      }));
      setLegendItems(items);
    }
  }, [state]);

  const handleCheckboxChange = (index) => {
    // Toggle visibility of dataset based on index
    const updatedState = { ...state };
    updatedState.datasets[index].hidden = !updatedState.datasets[index].hidden;
    setState(updatedState);
  };

  return (
  <>
    <Line
      data={state}
      options={{
        title: {
          display: true,
          text: `${t('Sensor History')}`,
          fontSize: 20
        },
        legend: {
          display: false ,
          position: 'bottom'
        },
        scales: {
        yAxes: [{
          ticks: {
            beginAtZero: true,
            max: 100,
            min: 0,
            stepSize: 25,
            fontColor: '#000',
            fontSize: 14,
          },
          gridLines: {
            color: 'rgba(0,0,0,0.2)',
            lineWidth: 1,
          },
          scaleOverride: true,
          scaleSteps: 4,
          scaleStepWidth: 25,
          scaleStartValue: 0
        }],
        xAxes: [{
          ticks: {
            autoSkip: false,
            maxRotation: 90,
            fontColor: '#000',
            fontSize: 10,
          },
          gridLines: {
            color: 'rgba(0,0,0,0.2)',
            lineWidth: 1,
          },
        }]
      }
      }}
    />
    <div>
        {legendItems.map((item, index) => (
          <div key={index}>
            <input
              type="checkbox"
              id={`checkbox-${index}`}
              checked={!state.datasets[index].hidden}
              onChange={() => handleCheckboxChange(index)}
            />
            <label htmlFor={`checkbox-${index}`}>{item.text}</label>
          </div>
        ))}
      </div>
    </>
  );
};

export default SensorHistoryChart;
