import React ,{useEffect,useState} from "react";
import PropTypes from "prop-types";
import {useHistory} from 'react-router-dom'
import { Container, Row, Col, CardBody, Card } from "shards-react";
import PageTitle from "../components/common/PageTitle";
import SmallStats from "../components/common/SmallStats";
import "./../assets/Styles.css";
import "./Styles.css";
import LeafletMap from "./map";
import SignalCellularNodataIcon from "@mui/icons-material/SignalCellularNodata";
import SignalCellular4BarIcon from "@mui/icons-material/SignalCellular4Bar";
import drop from '../images/drop.png'
import drop1 from '../images/drop1.png'
import drop2 from '../images/drop2.png'
import temp from '../images/temp.png'
import humidity from '../images/humidity.png'
import press from '../images/press.png'
import api from '../api/api';
import { useTranslation } from "react-i18next";
import FilterFields from './FilterFields'
import swal from "sweetalert";
import useSensorData from "../utils/useSensorData";
import SmallStatsFields from "../components/common/SmallStatsFields";



const Overview = (props) => {
  const [farms,setFarms] = useState([]);
  const [draw,setDraw] = useState({
         polygon: false,
        circle: false,
        rectangle: false,
        polyline: false,
        marker: false,
        circlemarker: false
  })
  const history = useHistory();
  // const [offer,setOffer] = useState(null)

  // let user = JSON.parse(localStorage.getItem("user"));
  // let offerType = user.offer_type;
  const { t, i18n } = useTranslation();

  const [fieldStats , setFS] = useState([])
  const [sensorStats , setSensorStats] = useState([])

  const [userMapDetails , setUserMapDetails] = useState("#")

  const [layerFarm , setLayerFarm] = useState([])
  const [mapConfig,setMapConfig] = useState({
    zoom:"",
    center:[],
    fromAction: false,
  })
  const getFieldStats = async () => {
    const response = await api.get('/dashboard/fields');
    setFS(response.data.farms); 
    setUserMapDetails(response.data.user_map_details.map_link_details)
  }

  const getSensorsStats = async () => {
    const response = await api.get('/dashboard/sensors');                            
      setSensorStats(response.data.sensors);
    }
    const [crops , setCrops] =useState([])
    const [dataCrops, setDataCrops] = useState([]);

    const fetchDataCrops = async () => {
        await api.get('/crop/crops')
        .then(response => {
          let dataCrops = response.data.farms;
          setDataCrops(dataCrops)
          let Crops = [];
          dataCrops.map(farm => {
            let fields = farm.fields;
            if (fields) {
              fields.map(field => {
               let crops = field.crops
               if(crops){
                crops.map(crop=>{
                  Crops.push({
                    type : crop.type,
                    id : crop.id,
                    fieldId : crop.field_id
                  })
                })
               }
              })
            }
          })
          setCrops(Crops)
        }).catch(err=>{
          console.log(err)
        })
      }

     const getLayerFarm = async () =>{
      await api.get('/farm/farms').then(res =>{
        const DataFarm = res.data.farms;
        setLayerFarm(DataFarm);
      })
     } 

    useEffect(()=>{
      // getLastDataSensor()
      getLayerFarm()
      getFieldStats(); 
      getSensorsStats();
      // fetchDataCrops()
    },[])
    let critical = 0;
    let full = 0;
    let opt = 0;

    fieldStats.map(item => {
      item.fields.map(field=>{
        let status = field.status
        if(status){
          if(status == "Optimal") {
            opt++
          }
          if(status == "Full") {
            full++
          }
          if(status == "Critical") {
            critical++
          }
        }

      })
    });

    let smallStats = [
      {
        state: `${t('Critical')}`,
        icon: <i class="fas fa-tint-slash"></i>,
        label: `${t('fields')}`,
        value: critical,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "6", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(0, 184, 216, 0.1)",
            borderColor: "rgb(0, 184, 216)",
            data: []
          }
        ]
      },
      {
        state: `${t('Optimal')}`,
        icon: <i class={"far fa-check-circle"}></i>,
        label: `${t('fields')}`,
        value: opt,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "6", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(23,198,113,0.1)",
            borderColor: "rgb(23,198,113)",
            data: []
          }
        ]
      },
      {
        state: `${t('Full')}`,
        icon: <i class="fas fa-tint"></i>,
        label: `${t('fields')}`,
        value:full,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      }
    ]

      let online = 0;
      let offline = 0;
      let lowBatt = 0;

    sensorStats.map(item => {
      let sensor = item.id;
      if(sensor){
        online++;
      }
    });

   let smallStats2 = [
      {
        state: `${t('online')}`,
        icon: <SignalCellular4BarIcon />,
        label: `${t('sensors')}`,
        value: online,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `${t('low_batt')}`,
        icon: <i class="fas fa-battery-quarter"></i>,
        label: `${t('sensors')}`,
        value: lowBatt,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `${t('offline')}`,
        icon: <SignalCellularNodataIcon />,
        label: `${t('sensors')}`,
        value: offline,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      }
    ]

    let smallStats3 = [
      {
        state: `20 cm`,
        icon: <img style={{color:"#F6BE47"}} src={drop} alt="" />,
        label: `33%`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `40 cm`,
        icon: <img src={drop2} alt="" />,
        label: `20%`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `60 cm`,
        icon: <img src={drop1} alt="" />,
        label: `10%`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      }
    ]
    let smallStats4 = [
      {
        state: `${t('Temp.')} (°C)`    ,
        icon: <img  src={temp} alt="" />,
        label: `19`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `${t('Humidity')} (%)`,
        icon: <img src={humidity} alt="" />,
        label: `33`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      },
      {
        state: `${t('Pression')} (kPa)`,
        icon: <img src={press} alt="" />,
        label: `99`,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: "4", sm: "6" },
        datasets: [
          {
            label: "Today",
            fill: "start",
            borderWidth: 1.5,
            backgroundColor: "rgba(255,180,0,0.1)",
            borderColor: "rgb(255,180,0)",
            data: []
          }
        ]
      }
    ]

     const  FilterByStatus = async (status) =>{
        let data = {
          status :status
        }
         await api.post('/field/field-status',data)
         .then(response =>{ 
           let farmsData = response.data.farms
           setFarms(farmsData);
         }).catch((err)=>{
          swal({
            title: 'Error',
            icon: "error"
        });
         })
      }

      const ToSensorPage =() => {
        history.push('/Sensors') 
        window.location.reload()
      }

      let sensorsData = useSensorData('/sensor/sensor-update-data')

return(
  <Container fluid className="main-content-container px-4">
    {/* Page Header */}
    <Row noGutters className="page-header py-4">
      <PageTitle title={t('overview')} className="text-sm-left mb-3" />
    </Row>

    <Row>
      <Col lg="12" md="6" sm="6" className="mb-4">
        <h4 style={{ marginBottom: "-15px" }}>{t('stats')}</h4>
      </Col>
    </Row>

    {/* Small Stats Blocks */}
    <Row>
      <Col lg="6" md="6" sm="6" className="mb-4">
        <p style={{ margin: 0 }}>{t('field_stats')}</p>
        <Card small className="stats-small">
          <CardBody className="p-0 d-flex">
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                width: "100%"
              }}
            >
              {/* <Stats /> */}
              {smallStats.map((stats, idx) => (
                <>
                  <SmallStats
                      FilterByStatus={FilterByStatus}
                    style={{
                      borderColor: "blue",
                      borderWidth: 2,
                      borderStyle: "solid"
                    }}
                    id={`small-stats-${idx}`}
                    variation="1"
                    chartData={stats.datasets}
                    chartLabels={stats.chartLabels}
                    label={stats.label}
                    value={stats.value}
                    icon={stats.icon}
                    state={stats.state}
                  />
                  {idx != 2 ? (
                    <hr
                      style={{
                        backgroundColor: "#ebebeb",
                        width: "1px",
                        height: "75%"
                      }}
                    />
                  ) : (
                    <></>
                  )}
                </>
              ))}
            </div>
          </CardBody>
        </Card>
      </Col>
      <Col lg="6" md="6" sm="6" className="mb-4">
        <p style={{ margin: 0 }}>{t('sensor_stats')}</p>
        <Card small className="stats-small">
          <CardBody className="p-0 d-flex">
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                width: "100%"
              }}
            >
              {smallStats2.map((stats, idx) => (
