import React,{useEffect,useState} from 'react'
import { Button, Row, Col, FormGroup, Form, FormInput, Container, Card, FormSelect, Nav, NavItem, NavLink } from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from "react-i18next";
import api from '../api/api';

const Reports = () => {

    const [listFields,setListFields] = useState([])
    const [selectedField,setSelectedField] = useState('')
    const [selectedRange,setSelectedRange] = useState('')
    const [listReports,setListReports] = useState([])
    const [filename,setFileName] = useState('')
    const { t, i18n } = useTranslation();
    useEffect(()=>{

    const getListFields = async () => {
        try {
              await api.get('/field/fields')
              .then(response=>{
                    let fields = [] 
                    let reports = []
                      let result = response.data.farms
                    if(result) {
                        result.map(farms=> {
                            let fieldData = farms.fields 
                                if(fieldData){
                                    fieldData.map(field=>{
                                        fields.push({
                                            id : field.id,
                                            name : field.name,
                                        })
                                       let reportsData = field.reports
                                        if(reportsData){
                                            reportsData.map(item=>{
                                                reports.push({
                                                    id : item.id,
                                                    fieldId : item.field_id,
                                                    file : item.filename
                                                })
                                            })
                                        }
                                    })
                                }
                        })                       
                        setListReports(reports)
                        setListFields(fields)
                        console.log(fields.length)
                        if(fields.length > 0){
                            setSelectedField(fields[0].id)    

                        }
                    }
              }).catch(err=>{
                    console.log(err)
              })
        } catch (error) {
            console.log(error)
        }
    }
            getListFields()
        },[])
        console.log(selectedField)
        const selectSearch = [
            { value: 'week', label: 'Last week' },
            { value: 'custom', label: 'Custom Range' },
        ];

        const downloadReport = async(file) => {
           let data = {
                filename : file
            }
            try {
               await api.post('/report/download',data)
                .then(response=>{
                    console.log(response)
                }).catch(error=>{   
                    console.log(error)
                })
            } catch (error) {

            }
        }
const filterReport = listReports.filter(report=>{
    if(selectedField !== ""){
        return selectedField == report.fieldId
    }
})

    return (
        <Container fluid className="main-content-container p-4">
            <Row noGutters className="page-header py-4">
                <PageTitle
                    sm="4"
                    title='Reports'
                    subtitle={t('overview')}
                    className="text-sm-left"
                />
            </Row>
                <Row>
                    <Col lg='6' md="12" sm="12">
                        <FormGroup>
                            <label htmlFor="">Field</label>
                            <FormSelect value={selectedField} onChange={(e) => setSelectedField(e.target.value)} >
                                <option value="">Select Field</option>
                                    {
                                        listFields.map(field=>(
                                            <option value={field.id}>{field.name}</option>
                                        ))
                                    }
                            </FormSelect>

                        </FormGroup>   
                    </Col>
                    <Col lg='6' md="12" sm="12">

                                <FormGroup>
                                    <label htmlFor="">Report Period</label>
                                    <FormSelect value={selectedRange} onChange={(e) => setSelectedRange(e.target.value)} >
                                        <option value="">Select Period</option>
                                        {
                                            selectSearch.map(({ value, label }) => (
                                                <option key={value} value={value}>{label}</option>
                                            ))
                                        }
                                    </FormSelect>

                                </FormGroup>
                                {
                                    selectedRange === "custom"
                                    ?
                                        <FormGroup >
                                            <p style={{ margin: "0px" }}>{t('start_date')}</p>
                                            <FormInput
                                                type="datetime-local"
                                                placeholder="Start Date"

                                            />
                                            <p style={{ margin: "0px" }}>{t('end_date')}</p>
                                            <FormInput
                                                type="datetime-local"
                                                placeholder="End Date"

                                            />
                                        </FormGroup>
                                    :
                                    null
                                }
                    </Col>

                </Row>
            <Card>
                <Row className="p-2 m-2">
                    <Col lg='12' md='8' sm='8'>

                        <div className='py-4'>
                        {
                            filterReport.map(report=>{
                               return (
                                <div className='d-flex justify-content-between align-items-center' style={{border:"1px solid #eee",borderRadius:12,padding:6}}>
                                    <h4>â–º Field Report {report.file.slice(37,58)}</h4>
                                    <button style={{background:"none",border:"none",outline:"none",padding:8 ,margin:5}} onClick={() => downloadReport(report.file)}> <i className='material-icons' style={{fontSize:"28px"}}>&#xf000;</i> </button>

                                </div>
                               )

                            })
                        }

                        </div>
                    </Col>
                </Row>
            </Card>
        </Container>
    )
}

export default Reports
