import React, { useEffect, useState } from 'react'
import { Container, Row, Col, FormGroup, FormInput, Button, ButtonGroup, Card ,Nav,NavItem,NavLink} from 'shards-react'
import PageTitle from '../components/common/PageTitle';
import { useTranslation } from 'react-i18next';
import api from '../api/api';
import swal from 'sweetalert';
import Pagination from '../views/Pagination';
import { useHistory ,Link} from 'react-router-dom';



const UsersList = () => {


    const [usersPerPage] = useState(8)
    const [currentPage, setCurrentPage] = useState(1);



    const { t, i18n } = useTranslation();

    const [msgServer, setMsg] = useState("");

    const [classMsg, setCmsg] = useState("");
    const [displayMsg, setDispMsg] = useState("hide");
    const [iconMsg, setIconMsg] = useState("info");

    const [toggle, setToggle] = useState(false);

    const [singleUser, setSingleUser] = useState([]);

    const [filteredResult, setFilteredResult] = useState([])

    const [SearchEmail, setSearchEmail] = useState('');
    const [SearchName, setSearchName] = useState('');
    const [SearchRole, setSearchRole] = useState('');

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [city, setCity] = useState('');
    const [nameErr, setNameErr] = useState('');


    let role = JSON.parse(localStorage.getItem('user')).role

    // Get USERS LIST

    const [users, setUsers] = useState([])

    useEffect(() => {
        getUsersList()
    }, [])


    // TO DO Fix search when input empty

    useEffect(() => {
        setUsers(filteredResult)
    }, [filteredResult])

    const getUsersList = async () => {
        let data = await api.get("/admin/users")
            .then(response => {
                let usersData = response.data.users
                setUsers(usersData)
            })
    }

    // Delete Users Handle

    const handleDelete = async userUid => {

        let data = {
            user_uid: userUid,
        }
        await api.delete('/admin/delete-user', { data: data })
            .then(response => {
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        title: `${t('cannot_delete')}`,
                        icon: "warning",
                    });
                }
                if (response.data.type == "success") {
                    getUsersList()
                    setMsg(`${t('delete_success_user')}`)
                    setCmsg("success")
                    setDispMsg("show")
                    setIconMsg("check")
                    hideMsg()
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete')}`,
                    icon: "error",
                    text: 'Error'

                });
            })
    }
    const hideMsg = () => {
        setTimeout(() => {
            setDispMsg("hide")
        }, 3000);

    }

    const confirmDelete = userUid => {

        swal({
            title: `${t('are_you_sure')}`,
            text: `${t('confirm_delete')}`,
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((Delete) => {
                if (Delete) {
                    handleDelete(userUid)
                    swal(`${t('delete_success_user')}`, {
                        icon: "success",
                    });
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete')}`,
                    icon: "error",
                    text: 'Error'

                });
            })

    }

    // Edit  Users Handle / TO DO : FIX Uid to edit selected user 


    const getSingleUser = async (userUid) => {

        let data = {
            user_uid: userUid,
        }

        await api.post('/admin/single-user', data)
            .then(res => {
                let UserData = res.data.user
                setSingleUser(UserData);
                setName(UserData.name);
                setEmail(UserData.email);
                setCity(UserData.city);
            }).catch(error => {
                console.log(error)

            })
        setToggle(!toggle)

    }

    const validate = () => {
        let nameErr = '';

        if (!name) {
            nameErr = 'Cannot be blank!';
            setNameErr(nameErr)
            return false;
        } else {
            setNameErr("")
            return true;
        }

    };

    const handleEdit = (userUid) => {

        let data = {
            name: name,
            email: email,
            user_uid: userUid
        }

        let isValid = validate()

        if (isValid) {

            api.post('/admin/edit-user', data)
                .then(response => {
                    if (response.data.type == "success") {
                        swal(" user has been updated", {
                            icon: "success",
                        });
                        getUsersList();
                        setToggle(false)
                    }
                    if (response.data.type && response.data.type == "danger") {
                        swal({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Error'
                        })
                        return false;
                    }
                }).catch(error => {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'error_edit_user'
                    })
                })
        }

    }




    // Active/Desactive Users Accounts

    const desactivateUser = (userUid) => {
        let data = {
            user_uid: userUid
        }
        api.post('/admin/desactivate-user', data)
            .then(response => {
                if (response.data.type == "success") {
                    swal("user has been desactivated", {
                        icon: "success",
                    });
                    getUsersList();
                    // setActive(false)
                }
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'error_desactivate_user'
                    })
                    return false;
                }
            }).catch(error => {
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'error_desactivate_user'
                })
            })
    }

    const activateUser = (userUid) => {
        let data = {
            user_uid: userUid
        }
        api.post('/admin/activate-user', data)
            .then(response => {
                if (response.data.type == "success") {
                    swal("user has been activated", {
                        icon: "success",
                    });
                    getUsersList();
                    // setActive(false)
                }
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'error_activate_user'
                    })
                    return false;
                }
            }).catch(error => {
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'error_activate_user'
                })
            })
    }

    const filteredUsers = users.filter(user => {
        if (SearchName !== '') {
            return (
                user.name.toLowerCase().includes(SearchName.toLowerCase())
            )
        }
        if (SearchEmail !== '') {
            return (
                user.email.toLowerCase().includes(SearchEmail.toLowerCase())
            )
        }
        if (SearchRole !== '') {
            return (
                user.role.toLowerCase().includes(SearchRole.toLowerCase())
            )
        }
        return user
    })


    const history = useHistory()
    const selectUser = (userUid) => {
        history.push(`/admin/user/${userUid}`)
    }

    // Pagination 

    const indexOfLastPost = currentPage * usersPerPage;
    const indexOfFirstPost = indexOfLastPost - usersPerPage;
    const currentUsers = filteredUsers.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = pageNumber => setCurrentPage(pageNumber);


    return (
        <>
            <Container>
                <Row noGutters className="page-header py-4">
                    <PageTitle
                        sm="4"
                        title={t('list_users')}
                        subtitle={t('list_users')}
                        className="text-sm-left"
                    />
                </Row>
                <Row className="d-flex justify-content-center">
                <Nav justified pills className="bg-white w-25">
              <NavItem>
                <NavLink active>
                 {t('list_users')}  
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink>
                  <Link to={`/admin/all-suppliers`}>{t('all_suppliers')}</Link>
                </NavLink>
              </NavItem>
            </Nav>

                </Row>
                <Row>

                    <PageTitle
                        sm="4"
                        subtitle={t('search_users')}
                        className="text-sm-left"
                    />
                </Row>

                <Row form className="d-flex justify-content-center">
                    <Col md="3" className="form-group">
                        <FormGroup>
                            <div className="d-flex">
                                <FormInput
                                    value={SearchName}
                                    onChange={(e) => setSearchName(e.target.value)}
                                    id="search"
                                    placeholder="Search By name" />

                            </div>
                        </FormGroup>
                    </Col>
                    <Col md="3" className="form-group">
                        <FormGroup>
                            <div className="d-flex">
                                <FormInput
                                    value={SearchRole}
                                    onChange={(e) => setSearchRole(e.target.value)}
                                    id="search"
                                    placeholder="Search By role" />

                            </div>
                        </FormGroup>
                    </Col>
                    <Col md="3" className="form-group">
                        <FormGroup>
                            <div className="d-flex">
                                <FormInput
                                    value={SearchEmail}
                                    onChange={(e) => setSearchEmail(e.target.value)}
                                    id="search"
                                    placeholder="Search By email" />
                            </div>
                        </FormGroup>
                    </Col>
                </Row>
                <Row>
                    <PageTitle
                        sm="4"
                        subtitle={t('my_actions')}
                        className="text-sm-left"
                    />
                </Row>
                <Row form className="d-flex justify-content-center">
                    <ButtonGroup>
                        <Button outline onClick={() => history.push('/admin/add-user')}>Add User</Button>
                        <Button outline onClick={() => history.push('/Supplier')}>Add Company</Button>
                        <Button outline onClick={() => history.push('/admin/sensors')}>Assign Sensor</Button>
                    </ButtonGroup>

                </Row>
                <div className={`mb-0 alert alert-${classMsg} fade ${displayMsg}`}>
                    <i class={`fa fa-${iconMsg} mx-2`}></i> {t(msgServer)}
                </div>
                <Card>
                    <table className="table mb-0 text-center table-responsive-lg">
                        <thead className="bg-light">
                            <tr>
                                <th scope="col" className="border-0">{t('name')}</th>
                                <th scope="col" className="border-0">{t('email')}</th>
                                <th scope="col" className="border-0">{t('city')}</th>
                                {/* <th scope="col" className="border-0">{t('offer_type')}</th> */}
                                <th scope="col" className="border-0">{t('Created at')}</th>


                            </tr>
                        </thead>
                        <tbody>
                            {
                                currentUsers.map(user => {
                                    return (
                                        <tr>
                                            <td>{user.name}</td>
                                            <td>{user.email}</td>
                                            <td>{user.city}</td>
                                            {/* <td>{user.offer_type}</td> */}
                                            <td>{new Date(user.created_at).toLocaleString().slice(0,10)}</td>
                                            <td>
                                                {
                                                    user.deleted_at == null
                                                        ?

                                                        <ButtonGroup size="sm" className="mr-2">
                                                            <Button title="Edit" onClick={() => selectUser(user.uid)} squared><i className="material-icons">&#xe3c9;</i></Button>
                                                            {
                                                                user.is_active == 1
                                                                    ?
                                                                    <Button title="Desactivate" onClick={() => desactivateUser(user.uid)} theme="secondary">
                                                                        <i className="material-icons">&#xe510;</i>
                                                                    </Button>

                                                                    :
                                                                    <Button title="Activate" onClick={() => activateUser(user.uid)} theme="success">
                                                                        <i className="material-icons">&#xe7fd;</i>
                                                                    </Button>
                                                            }
                                                            <Button title="Delete" onClick={() => confirmDelete(user.uid)} squared theme="danger"><i className="material-icons">&#xe872;</i></Button>
                                                        </ButtonGroup>
                                                        :
                                                        null
                                                }
                                            </td>
                                        </tr>

                                    )
                                })
                            }

                        </tbody>
                    </table>

                </Card>
                <Row className="py-4 justify-content-center">
                    <Pagination usersPerPage={usersPerPage} totalUsers={users.length} paginate={paginate} />

                </Row>
            </Container>
        </>
    )
}

export default UsersList
