import React ,{useState,useEffect} from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardFooter,
  Badge,
  CardHeader,
  Button,
  FormSelect
} from "shards-react";

import PageTitle from "../components/common/PageTitle";
import Chart from "../components/blog/Chart";
import "./../assets/Styles.css";
import api from '../api/api';
import LoadingSpinner from "../components/common/LoadingSpinner";
import { useTranslation } from "react-i18next";
import TempChart from "../components/blog/TempChart";
import PressionChart from "../components/blog/PressionChart";
import HumidityChart from "../components/blog/HumidityChart";
import SubSoilHumidityChart from "../components/blog/SubSoilHumidityChart";
import swal from "sweetalert";


const Graph = () => {

  const { t, i18n } = useTranslation();

      const [name,setName] = useState('')
      const [sensors,setSensors ] = useState([])
      const [dataSensor,setDataSensors] = useState([])
      const [value ,setValue ] = useState(null)
      const [isLoading,setIsLoading]= useState(true)

  let Uid = localStorage.getItem('Field')

  useEffect(()=>{
    getDataSensors()
    getSensorsCodes()
    getSelectedField()
  },[])

  const getSelectedField = async () => {

    let dataField = {
        field_uid: Uid,
    }

    await api.post('/field', dataField)
        .then(res => {
            let fieldData = res.data.field
            setName(fieldData.name)                      
        }).catch(error => {
            swal({
                title: "Error",
                icon: "error",

            });

        })
}

  const getDataSensors =  async () => {
    await api.get('/datasensor/get-data')
        .then(res => {
          let dataSensor = res.data.Datasensor
          setDataSensors(dataSensor)
        })
        .catch(err =>{
          console.log(err)
        })
        .finally(() => setIsLoading(false))

  }
  const getSensorsCodes = async () => {
   await api.get(`/field/${Uid}/sensors`)
    .then(response => {
      let sensors = response.data.sensors
        setSensors(sensors)
    })
    .catch(err=>{
      console.log(err)
    })
    .finally(() => setIsLoading(false))
  }

  const filteredGraph = dataSensor.filter(data => {
    if(value === data.code) {
      return (
        data.code.toLowerCase().includes(value.toLowerCase())
        )

    }
    }
);

    return (
      <Container fluid className="main-content-container px-4">
        {/* Page Header */}
        <Row noGutters className="page-header py-4">
          <PageTitle
            sm="4"
            title={name}
            subtitle="Detailed Graphs"
            className="text-sm-left"
          />
        </Row>
        {
          isLoading
          ?
          <LoadingSpinner />
          :
          <>
        <Row className="d-flex justify-content-center py-4">
          <Col lg="4" md="6" sm="6" >
          <FormSelect value={value} onChange={e => setValue( e.target.value)}>
            <option value="">{t('select_sensor')}</option>
            {
              sensors.map(sensor =>{
                return <option value={sensor.code} >{sensor.code}</option>
              })
            }
          </FormSelect>
          </Col>
        </Row>
        {/* First Row of Posts */}
        <Row>
          <Col lg="6" md="12" sm="12" className="mb-4">
            {/* <Chart title={t('Temp.')} dataSensor={dataSensor} /> */}
            <TempChart title={t('Temp.')}  data={filteredGraph} />
          </Col>
          <Col lg="6" md="12" sm="12" className="mb-4">
            {/* <Chart title={t('Pression')} dataSensor={dataSensor} /> */}
             <PressionChart title={t('Pression.')}  data={filteredGraph} />
          </Col>
          <Col lg="6" md="12" sm="12" className="mb-4">
            <HumidityChart title={t('Humidity')}  data={filteredGraph} />
          </Col>

          <Col lg="6" md="12" sm="12" className="mb-4">   
            <SubSoilHumidityChart title='Subsoil Humidity' data={filteredGraph} />
          </Col>
        </Row>

        </>
        }
      </Container>
    );
  }

export default Graph;
