import React, { useState } from 'react'
import { Button, ButtonGroup, Card, CardHeader, CardBody, Modal, ModalBody, ModalHeader, Row, Col, FormInput, FormGroup, Form, FormSelect } from 'shards-react'
import api from '../api/api'
import swal from 'sweetalert'
import { useTranslation } from "react-i18next";
import CompositeSoil from '../components/FieldSettingForms/compositeSoilForm';
import StandardSoil from '../components/FieldSettingForms/standardSoil';




const ZoneList = ({ zonesList, Zones, Fields, state, className = "" }) => {

    const { t, i18n } = useTranslation();
    const [toggle, setToggle] = useState(false);
    const [name, setName] = useState('');
    const [source, setSource] = useState('');
    const [description, setDescription] = useState('');
    const [field, setField] = useState('')
    const [depthLevel, setLevel] = useState([]);
    const [depth, setDepth] = useState([]);
    const [defaultDeth, setDefaultDeth] = useState(0);
    const [defaultSoilProprety, setdefaultSoilProprety] = useState('Standard');
    const [depth_data, setDepthData] = useState([])
    const [msgServer, setMsg] = useState("")
    const [classMsg, setCmsg] = useState("")
    const [displayMsg, setDispMsg] = useState("hide")
    const [iconMsg, setIconMsg] = useState("info")
    const [SingleZone, setSingleZone] = useState([])
    const [soilData,setSoilData] = useState({
        irrigArea: "",
        RUmax: "",
        effPluie: "",
        ruPratique: "",
        effIrrig: "",

    })
    const handleDepth = (e) => {
        //setDepthData(e);
    };

    const getSingleZone = (zoneUid) => {

        setSingleZone([])
        let data = {
            zone_uid: zoneUid,
        }

         api.post('/zone', data)
            .then(res => {
                let ZoneData = res.data.zone
                setSingleZone(ZoneData)
                setName(ZoneData.name)
                setSource(ZoneData.source)
                setDescription(ZoneData.description)
                setSoilData({ruPratique :ZoneData.ruPratique })
                setSoilData({RUmax : ZoneData.RUmax})
                setSoilData({effIrrig : ZoneData.effIrrig})
                setSoilData({effPluie : ZoneData.effPluie})
                setSoilData({irrigArea : ZoneData.irrigArea})
                setDepthData(JSON.parse(ZoneData.depth_data))

                setDepth(Object.keys(JSON.parse(ZoneData.depth_data)))
                if (Object.keys(JSON.parse(ZoneData.depth_data)).length > 0) {
                    setDefaultDeth(Object.keys(JSON.parse(ZoneData.depth_data))[0])
                    setdefaultSoilProprety(JSON.parse(ZoneData.depth_data)[Object.keys(JSON.parse(ZoneData.depth_data))[0]].soil_property)
                }

                Fields.map((fieldData) => {
                    if (ZoneData.field_id == fieldData.Id) {
                        setField(fieldData.Uid)
                    }
                })

            }).catch(error => {
                swal({
                    title: error,
                    icon: "error",

                });
                return false;

            })

        setToggle(!toggle)
        return false;
    }
    const handleEdit = (zoneUid) => {
        let data = {
            name: name,
            source: source,
            description: description,
            depth_data: depth_data,
            zone_uid: zoneUid,
            field_uid: field,
            irrigArea: soilData.irrigArea,
            RUmax: soilData.RUmax,
            effPluie: soilData.effPluie,
            ruPratique: soilData.ruPratique,
            effIrrig: soilData.effIrrig,
        }
        api.post('/zone/edit-zone', data)
            .then(response => {
                if (response.data.type == "success") {
                    swal(`${t('zone_updated')}`, {
                        icon: "success",
                    });
                    setToggle(false)
                    Zones();
                }
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'Error'
                    })
                    return false;
                }
            }).catch(error => {
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'Error'
                })
                return false;
            })
    }
    const handleDelete = async zoneUid => {

        let data = {
            zone_uid: zoneUid,
        }
        await api.delete('/zone/delete-zone', { data: data })
            .then(response => {
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        title: `${t('cannot_delete')}`,
                        icon: "warning",
                    });
                }
                if (response.data.type == "success") {
                    Zones()
                    setMsg(`${t('delete_success_zone')}`)
                    setCmsg("success")
                    setDispMsg("show")
                    setIconMsg("check")
                    hideMsg()
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete')}`,
                    icon: "error",
                    text: 'error_delete_zone'
                });
            })
    }
    const hideMsg = () => {
        setTimeout(() => {
            setDispMsg("hide")
        }, 3000);

    }


    const confirmDelete = zoneUid => {

        swal({
            title: `${t('are_you_sure')}`,
            text: `${t('confirm_delete')}`,
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((Delete) => {
                if (Delete) {
                    handleDelete(zoneUid)
                    swal(`${t('delete_success_zone')}`, {
                        icon: "success",
                    });
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete')}`,
                    icon: "error",
                    text: 'Error'

                });
            })

    }
    const soilTypeForm = () => {

        if (Object.keys(depth_data).length > 0) {
            if (defaultSoilProprety == 'Standard') {
                let soilType = "";
                if (depth_data[defaultDeth]) {
                    soilType = depth_data[defaultDeth].soilType;
                }
                return (
                    null
                    // <StandardSoil
                    //     key={defaultDeth}
                    //     depth={defaultDeth}
                    //     uni={soilType}
                    //     onChange={value => {
                    //         console.log(value)
                    //         depth_data[defaultDeth].soilType = value.uni;
                    //         depth_data[defaultDeth].depth = value.depth;
                    //     }}
                    // />
                );
            } else {
                if (depth_data[defaultDeth]) {
                    return (
                        <CompositeSoil
                            key={defaultDeth}
                            depth={defaultDeth}
                            clay={depth_data[defaultDeth].clay}
                            sand={depth_data[defaultDeth].sand}
                            silt={depth_data[defaultDeth].silt}
                            OM={depth_data[defaultDeth].om}
                            pH={depth_data[defaultDeth].ph}
                            Ecd={depth_data[defaultDeth].ecd}
                            CEC={depth_data[defaultDeth].cec}
                            soil_property={depth_data[defaultDeth].soil_property}
                            onChange={value => {
                                depth_data[defaultDeth] = value;
                            }}
                        />
                    );
                }
            }
        }
    };

    const deleteDepthLevelButton = () => {
        if (Object.keys(depth_data).length > 0) {
            return (
                <Col md="12" className="form-group">
                    <Button
                        theme="danger"
                        onClick={evt => {
                            evt.preventDefault();
                            if (window.confirm("delete_depth")) {
                                delete depth_data[defaultDeth]
                                setDepthData(depth_data)
                            }
                        }}
                    >
                        delete current depth level
                    </Button>
                </Col>
            );
        }
    };

    return (
        <>
            <div className={`mb-0 alert alert-${classMsg} fade ${displayMsg}`}>
                <i class={`fa fa-${iconMsg} mx-2`}></i> {t(msgServer)}
            </div>
            <table className="table mb-4 text-center table-bordered table-responsive-lg">
                <thead className="bg-light">
                    <tr>
                        <th scope="col" className="border-0">{t('name')}</th>
                        <th scope="col" className="border-0">{t('desc')}</th>
                        <th scope="col" className="border-0">{t('source')}</th>
                        <th scope="col" className="border-0">{t('name_field')}</th>
                        <th scope="col" className="border-0"></th>
                    </tr>
                </thead>
                <tbody>
                    {
                        zonesList.map((item, indx) => {
                            let nameField = "";
                            Fields.map((fieldData) => {
                                if (fieldData.Id == item.field_id) {
                                    nameField = fieldData.title
                                }
                            })
                            return (

                                <tr>
                                    <td>{item.name}</td>
                                    <td>{item.description}</td>
                                    <td>{item.source}</td>
                                    <td>{nameField}</td>
                                    <td>
                                        <ButtonGroup size="sm" className="mr-2">
                                            <Button onClick={() => getSingleZone(item.Uid)} squared theme="info"><i className="material-icons">&#xe3c9;</i></Button>
                                            <Button onClick={() => confirmDelete(item.Uid)} squared theme="danger"><i className="material-icons">&#xe872;</i></Button>
                                        </ButtonGroup>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
            {<Modal size='lg' centered={true} className={className} open={toggle} >
                <ModalHeader closeAriaLabel>

                    <h6 className="m-0">{t('edit_zone')}</h6>

                    <div
                        style={{
                            display: "flex",
                            justifyContent: "flex-end",

                        }}
                    >
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-success"
                            onClick={() => handleEdit(SingleZone.uid)}
                        >
                            {t('save')}
                            <i class={`fa fa-check mx-2`}></i>
                        </Button>
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-danger"
                            onClick={() => setToggle(false)}

                        >
                            {t('cancel')}
                            <i class={`fa fa-times mx-2`}></i>
                        </Button>
                    </div>

                </ModalHeader>
                <ModalBody>
                    {<Form>
                        <Row form>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('soil_zone')}</p>
                                <FormInput
                                    placeholder={t('soil_zone')}
                                    required
                                    defaultValue={SingleZone.name}
                                    onChange={(e) => setName(e.target.value)}
                                />
                            </Col>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('name_field')}</p>
                                <FormSelect
                                    value={field}
                                    onChange={(e) => setField(e.target.value)}
                                >
                                    {
                                        Fields.map((field) => (
                                            <option value={field.Uid}>{field.title}</option>
                                        ))
                                    }
                                </FormSelect>
                                <div className="invalid-feedback">{t('no_empty')}</div>

                            </Col>

                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>Source</p>
                                <FormSelect
                                    defaultValue={source}
                                    onChange={(e) => setSource(e.target.value)}
                                >
                                    <option>Manual</option>
                                </FormSelect>

                            </Col>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>Soil Property</p>
                                <FormSelect
                                    value={defaultSoilProprety}
                                    onChange={(e) => console.log(e.target.value)}
                                >
                                    <option>Standard</option>
                                    <option >Composite</option>
                                </FormSelect>
                            </Col>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>Depth Level (cm)</p>
                                <FormSelect
                                    onChange={evt => {
                                        setDefaultDeth(evt.target.value)
                                        setdefaultSoilProprety(depth_data[evt.target.value].soil_property)

                                        depthLevel.push({
                                            depth: evt.target.value,
                                            clay: 0,
                                            sand: 0,
                                            silt: 0,
                                            om: 0,
                                            ph: 2,
                                            ecd: 0,
                                            cec: 0,
                                            uni: "Sand",
                                            soilType: "",
                                            soil_property: 'Standard'
                                        });
                                        handleDepth(depthLevel)
                                    }}
                                >
                                    {
                                        depth.map((item, index) => {
                                            return (
                                                <option value={item}>
                                                    {item}
                                                </option>
                                            );

                                        })}
                                    <option >Add new depth level</option>
                                </FormSelect>
                            </Col>
                            {Object.keys(depth_data).length > 0 ? deleteDepthLevelButton() : ""}
                            {Object.keys(depth_data).length > 0 ? soilTypeForm() : ""}
                        </Row>
                    </Form>}
                    <Row>
                        <Col lg="6" md="8" sm="8">
                            <FormGroup>
                                <p style={{ margin: "0px" }}>{t('surface_irriguée')} (ha)</p>
                                <FormInput value={soilData.irrigArea} onChange={e => setSoilData({irrigArea : e.target.value})} id="surfaceIrrig" placeholder={t('surface_irriguée')}
                                />


                            </FormGroup>

                        </Col>
                        <Col lg="6" md="8" sm="8">
                            <FormGroup>
                                <p style={{ margin: "0px" }}>{t('efficacité_pluie')} (%)</p>
                                <FormInput value={soilData.effPluie} onChange={e => setSoilData({effPluie : e.target.value})} id='effPluie' placeholder={t('efficacité_pluie')}
                                />

                            </FormGroup>
                        </Col>
                        <Col lg="6" md="8" sm="8">
                            <FormGroup>
                                <p style={{ margin: "0px" }}>{t('fraction_pratique')} (%) </p>
                                <FormInput value={soilData.ruPratique} onChange={e => setSoilData({ruPratique : e.target.value})} id='ruPratique' placeholder={t('fraction_pratique')}
                                />
                            </FormGroup>

                        </Col>
                        <Col lg="6" md="8" sm="8">
                            <FormGroup>
                                <p style={{ margin: "0px" }}>{t('efficience_irrigation')} (%) </p>
                                <FormInput value={soilData.effIrrig} onChange={e => setSoilData({effIrrig : e.target.value})} id='effIrrig' placeholder={t('efficience_irrigation')}
                                />

                            </FormGroup>

                        </Col>
                        <Col lg="6" md="8" sm="8">
                            <FormGroup>
                                <p style={{ margin: "0px" }}>RU max (mm/m)</p>
                                <FormInput value={soilData.RUmax} onChange={e => setSoilData({RUmax : e.target.value})} id='ruMax' placeholder="RU max"
                                />

                            </FormGroup>

                        </Col>

                    </Row>
                </ModalBody>
            </Modal>}
        </>
    )
}

export default ZoneList
