import React , {useState,useEffect}from 'react'
import { Line } from  'react-chartjs-2';


const WaterChart = ({data}) => {

    const [state,setState] = useState(null)

    const getChartData = () => {

        let labels = []   
        let dataBilan = []
        for (let i = 0; i <= data.length; i++) {
            labels.push(i)
            dataBilan.push(parseFloat(data[i]).toFixed(2))
        }
        const state = {
            labels: labels,
            datasets: [
              {
                label: 'Water balance',
                fill: false,
                lineTension: 0.5,
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: '#27A6B7',
                borderWidth: 2,
                data: dataBilan
              }
            ]
          }
          setState(state)
    }

    useEffect(()=>{
        getChartData()
    },[data])

    return (

      <Line
      data={state}
          options={{
            title:{
              display:true,
              text:'Bilan hydrique du sol',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
      />
  );
}

export default WaterChart
