import React, { useState,useEffect } from 'react'
import { Button, ButtonGroup, Card, CardBody, CardHeader, Modal, ModalBody, ModalHeader, Row, Col, FormInput, FormGroup, Form, FormSelect } from 'shards-react'
import api from '../api/api'
import swal from 'sweetalert'
import { useTranslation } from "react-i18next";
import RangeDatePicker from '../components/common/RangeDatePicker';



const CropList = ({ cropsList, Crops, Fields, Zones }) => {


    const { t, i18n } = useTranslation();

    const [toggle, setToggle] = useState(false);

    const [type, setType] = useState('');
    const [prevType, setPrevType] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [field, setField] = useState('');
    const [zone,setZone] = useState('');
    const [gdd, setGdd] = useState('');
    const [cropData,setCropData] = useState({
        cropVariety :"",
        days :"",
        plantingDate:"",
        rootDepth :"",
        density : "",
        ecartInter :"",
        ecartIntra :"",
        ruPratique : ""

    })
    const [msgServer, setMsg] = useState("")

    const [classMsg, setCmsg] = useState("")
    const [displayMsg, setDispMsg] = useState("hide")
    const [iconMsg, setIconMsg] = useState("info")


    const [SingleCrop, setSingleCrop] = useState([])



    const getSingleCrop = (cropUid) => {
        console.log(cropUid)
        let data = {
            crop_uid: cropUid,
        }
        try {
             api.post('/crop', data)
            .then(res => {
                let CropData = res.data.crop
                setSingleCrop(CropData)
                cropsList.map(crop=>{
                    if(CropData.croptype_id === crop.croptype.id){
                        setType(crop.croptype.crop)

                    }
                })
                setCropData({rootDepth :CropData.rootDepth })
                setCropData({days : CropData.days})
                setCropData({plantingDate : CropData.plantingDate})
                setCropData({cropVariety : CropData.cropVariety})
                setCropData({density : CropData.density})
                setCropData({ecartInter : CropData.ecart_inter})
                setCropData({ecartIntra : CropData.ecart_intra})
                setCropData({ruPratique : CropData.practical_fraction})
                // let start = CropData.growth_date_start;
                // let end = CropData.growth_date_end;
                // setStartDate(start.slice(0, 19))
                // setEndDate(end.slice(0, 19))
                Fields.map((fieldData) => {
                    if (CropData.field_id == fieldData.Id) {
                        setField(fieldData.Uid)
                    }
                })
                Zones.map((zoneData) => {
                    if (CropData.zone_id === zoneData.Id) {
                        setZone(zoneData.Uid)
                    }
                })
            }).catch(error => {
                console.log(error)
                swal({
                    title: "Error",
                    icon: "error",

                });

            })   
        } catch (error) {
            console.log(error)
            swal({
                title: "Error",
                icon: "error",

            });
        }
        setToggle(!toggle)
    }

    const handleEdit = (cropUid) => {


        let data = {
            type: type,
            cropVariety :cropData.cropVariety,
            days :cropData.days,
            plantingDate:cropData.plantingDate,
            rootDepth :cropData.rootDepth,
            crop_uid: cropUid,
            field_uid: field,
            zone_uid:zone,
            practical_fraction : cropData.ruPratique,
            density : cropData.density,
            ecart_inter : cropData.ecartInter,
            ecart_intra : cropData.ecartIntra
        }


        api.post('/crop/edit-crop', data)
            .then(response => {
                if (response.data.type == "success") {
                    swal(`${t('crop_updated')}`, {
                        icon: "success",
                    });
                    setToggle(false)
                    Crops();
                }
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'Error'
                    })
                    return false;
                }
            }).catch(error => {
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'Error'
                })
            })


    }

    const handleDelete = async cropUid => {

        let data = {
            crop_uid: cropUid,
        }
        await api.delete('/crop/delete-crop', { data: data })
            .then(response => {
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        title: `${t('cannot_delete')}`,
                        icon: "warning",
                    });
                }
                if (response.data.type == "success") {
                    Crops()
                    setMsg(`${t('delete_success')}`)
                    setCmsg("success")
                    setDispMsg("show")
                    setIconMsg("check")
                    hideMsg()
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete_crop')}`,
                    icon: "error",
                    text: 'Error'

                });
            })
    }
    const hideMsg = () => {
        setTimeout(() => {
            setDispMsg("hide")
        }, 3000);

    }

    // useEffect(()=>{
    //     if(cropData.ecartInter !== "" && cropData.ecartIntra !== ""){

    //     let formule  = 10000 / (Number(cropData.ecartInter) * Number(cropData.ecartIntra))
    //       setCropData({ ...cropData,density : formule})
    //   }
    //   },[cropData.ecartInter,cropData.ecartIntra])

    const confirmDelete = cropUid => {

        swal({
            title: `${t('are_you_sure')}`,
            text: `${t('confirm_delete')}`,
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((Delete) => {
                if (Delete) {
                    handleDelete(cropUid)
                    swal(`${t('delete_success')}`, {
                        icon: "success",
                    });
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete_crop')}`,
                    icon: "error",
                    text: 'Error'

                });
            })

    }


    return (
        <>
            <div className={`mb-0 alert alert-${classMsg} fade ${displayMsg}`}>
                <i class={`fa fa-${iconMsg} mx-2`}></i> {t(msgServer)}
            </div>
            <table className="table mb-4 text-center table-bordered tabel-responsive-lg">
                <thead className="bg-light">
                    <tr>
                        <th scope="col" className="border-0">{t('crop_type')}</th>
                        <th scope="col" className="border-0">{t('name_field')}</th>
                        <th scope="col" className="border-0">{t('name_zone')}</th>
                        <th scope="col" className="border-0"></th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cropsList.map((item, indx) => {
                            let croptype = item.croptype
                            let nameField = "";
                            let nameZone = "";
                            let nameCrop = ""
                            Fields.map((fieldData) => {
                                if (fieldData.Id == item.field_id) {
                                    nameField = fieldData.title
                                }

                            })
                            if(croptype){      
                             nameCrop = croptype.crop
                            }
                            Zones.map((zoneData) => {
                                if (zoneData.Id == item.zone_id) {
                                    nameZone = zoneData.name
                                }

                            })
                            return (
                                <tr>
                                    <td>{nameCrop}</td>
                                    <td>{nameField}</td>
                                    <td>{nameZone}</td>
                                    <td>
                                        <ButtonGroup size="sm" className="mr-2">
                                            <Button onClick={() => getSingleCrop(item.Uid)} squared theme="info"><i className="material-icons">&#xe3c9;</i></Button>
                                            <Button onClick={() => confirmDelete(item.Uid)} squared theme="danger"><i className="material-icons">&#xe872;</i></Button>
                                        </ButtonGroup>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
            <Modal size='lg' centered={true} open={toggle} >
                <ModalHeader closeAriaLabel>
                    <h6 className="m-0">{t('edit_crop')}</h6>{" "}
                    <div
                        style={{
                            display: "flex",
                            justifyContent: "flex-end",

                        }}
                    >
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-success"
                            onClick={() => handleEdit(SingleCrop.uid)}
                        >
                            {t('save')}
                            <i class={`fa fa-check mx-2`}></i>
                        </Button>
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-danger"
                            onClick={() => setToggle(false)}

                        >
                            {t('cancel')}
                            <i class={`fa fa-times mx-2`}></i>
                        </Button>
                    </div>
                </ModalHeader>
                <ModalBody>
                    <Form>
                        <Row form>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('name_field')}</p>
                                <FormSelect
                                    value={field}
                                    onChange={(e) => setField(e.target.value)}
                                >
                                    {
                                        Fields.map((field) => (
                                            <option value={field.Uid}>{field.title}</option>
                                        ))
                                    }
                                </FormSelect>
                            </Col>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('name_zone')}</p>
                                <FormSelect
                                    value={zone}
                                    onChange={(e) => setZone(e.target.value)}
                                >
                                    {
                                        Zones.map((zone) => (
                                            <option value={zone.Uid}>{zone.name}</option>
                                        ))
                                    }
                                </FormSelect>
                            </Col>            
                            <Col lg="6" md="12" sm="12" className="form-group">
                                <p style={{ margin: "0px" }}>{t('crop_type')}</p>
                                <FormInput
                                    value={type}
                                    onChange={(e) => setType(e.target.value)}
                                />
                                <div className="invalid-feedback">{t('no_empty')}</div>
                            </Col>
                                {/* <Col lg="6" md="12" sm="12" className="form-group">
                                    <p style={{ margin: "0px" }}>{t('prev_type')}</p>
                                    <FormInput
                                        value={prevType}
                                        onChange={(e) => setPrevType(e.target.value)}
                                        placeholder={t('prev_type')}
                                    />
                                </Col> */}
                            <Row>
                            {/* <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('crop_variety')}</p>
              <FormSelect value={cropData.variety || ""} onChange={handleVarietyPick} id="cropVariety">
                <option value="">{t('crop_type')}</option>
                  {
                      cropData.cropVariety.map(variety => (
                          <option value={variety.id}>{variety.variety}</option>
                      ))
                  }
              </FormSelect>
            </FormGroup>

          </Col> */}
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('profondeur')} (m)</p>
              <FormInput type="number" value={cropData.rootDepth} onChange={e => setCropData({...cropData,rootDepth : e.target.value})} id='z' placeholder={t('profondeur')}
              />

            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('Days')}</p>

              <FormInput type="number" value={cropData.days} id='days' onChange={e => setCropData({...cropData,days :  e.target.value})} placeholder={t('Days')} />

            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('growing_season')}</p>
              <FormInput type="date" value={cropData.plantingDate} onChange={e => setCropData({...cropData,plantingDate :  e.target.value})} id='days' />

            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
              <FormGroup>
                <p style={{ margin: "0px" }}>{t('fraction_pratique')} (%) </p>
                <FormInput type="number" value={cropData.ruPratique} onChange={e => setCropData({...cropData,ruPratique :  e.target.value})} id='ruPratique' placeholder={t('fraction_pratique')}
                />
              </FormGroup>

            </Col>
          <Col lg="4" md="8" sm="8">
              <FormGroup>
                <p style={{ margin: "0px" }}>{t('ecart_inter')} (m)</p>
                <FormInput type="number" value={cropData.ecartInter} onChange={e => setCropData({...cropData,ecartInter :  e.target.value})} id='ecartInter' placeholder={t('ecart_inter')}
                />
              </FormGroup>

            </Col>
            <Col lg="4" md="8" sm="8">
              <FormGroup>
                <p style={{ margin: "0px" }}>{t('ecart_intra')} (m) </p>
                <FormInput type="number" value={cropData.ecartIntra} onChange={e => setCropData({...cropData,ecartIntra:  e.target.value})} id='ecartIntra' placeholder={t('ecart_intra')}
                />
              </FormGroup>

            </Col>
            <Col lg="4" md="8" sm="8">
              <FormGroup>
                <p style={{ margin: "0px" }}>{t('densité')} (plants/ha)</p>
                <FormInput type="number" value={cropData.density} onChange={e => setCropData({...cropData,density:  e.target.value})} id='densité' placeholder={t('densité')}
                />
              </FormGroup>

            </Col>
                            </Row>
                        </Row>
                    </Form>
                </ModalBody>
            </Modal>



        </>
    )
}

export default CropList
