import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import api from '../../api/api'
import Notifications ,{notify} from 'react-notify-toast';
import swal from 'sweetalert';
import { useTranslation } from "react-i18next";

export const Register = ({ setToken }) => {


  const [hasAccepted, setAccepted] = useState(false);



  const [name,setName] = useState("");
  const [email,setEmail] = useState("");
  const [password , setPassword] = useState("");
  const [confirmPass , setConfirmPass] = useState(""); 

  const [sendEmail,setSendEmail] = useState(false); 

  const [nameError,setNameErr] = useState("");
  const [emailError ,setEmailErr] = useState("");
  const [mdpError , setPassErr] = useState("");
  const [confMdpErr , setconfMdpErr] = useState("");
  const [offerType , setOfferType] = useState("");
  const [role , setRole] = useState("ROLE_USER");
  const { t, i18n } = useTranslation();

  // const [user,setUser] = useState({
  //   name : "",
  //   email : "",
  //   password : "" ,
  //   confirmPass : "",
  // })

  // const [errors , setErr] =useState({
    // nameError : "",
    // emailError :"",
    // passErr:"",
  // })



 const validate = () => {
    let nameError = '';
    let emailError = '';
    let mdpError = '';
    let confMdpErr = '';
    if (!name) {
      nameError = 'Username cannot be blank!';
      setNameErr(nameError)
    }
    if (!email) {
      emailError = 'Email cannot be blank!';
      setEmailErr(emailError)
     }
       else if (typeof email !== 'undefined') {
       var mailformat = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (!mailformat.test(email)) {
          emailError = 'Incorrect email format!';
          setEmailErr(emailError)
           }
      }
     if (password.length < 6) {
      mdpError = 'Cannot be less 6 character!';
      setPassErr(mdpError)
     }
     if (password != confirmPass) {
      confMdpErr = `Values don't match`;
      setconfMdpErr(confMdpErr)
     }
     if (emailError ||  nameError || mdpError || confMdpErr ) {
      setEmailErr(emailError);
      setNameErr(nameError);
      setPassErr(mdpError);
      setconfMdpErr(confMdpErr)
      return false;
    }
      return true;
  };

 const handleSubmit = (event) =>{
    event.preventDefault()

    const isValid = validate();
    if(isValid) {
        handleRegister()

    }else{
      return false;
    }
 } 


  const handleRegister = () => {

    let data = {
      name : name,
      email: email,
      password: password,
      confirmPassword : confirmPass ,
      offer_type : offerType,
      role: role
    }

        api.post('/signup' ,data)
        .then(res => {
          if(res.data.type && res.data.type == "danger") {
            swal({
              icon: 'error',
              title: 'Oops...',
              text: res.data.message
            })
            return false;
          }
          if(res.data.type && res.data.type == "warnning") {
            swal({
              icon: 'warning',
              title: 'Oops...',
              text: res.data.message
            })
            setSendEmail(true , resetForm())
          }
          if(res.data.type && res.data.type == "success") {
            swal({
              icon: 'success',
              title: 'OK',
              text: 'Email sent check your inbox to confirm'
            })
            setSendEmail(true , resetForm())

          }
          }).catch(() => {
            swal({
              icon: 'error',
              title: 'Oops...',
              text: 'error_add_user'
            })
            return false;
          })
        }
        const resetForm = () => {
          setName("");
          setEmail("");
          setPassword("");
          setConfirmPass("");
          setOfferType(null);

          setTimeout(() => {
            setSendEmail(false)
          }, 3000);
        }


  // const handleSubmit = async () => {
  //   if (hasAccepted) {
  //     setToken({ token: "aaa" });
  //   }
  // };
  return (
    <div style={{ marginTop: 25 }}>
      <div className="d-flex align-items-center auth px-0 h-100">
        <div className="row w-100 mx-0">
          <div className="col-lg-4 mx-auto">
            <div className="card text-left py-5 px-4 px-sm-5">
              <div className="brand-logo">
                <div
                  style={{
                    height: 60,
                    width: "100%",
                    overflow: "hidden",
                    textAlign: "center"
                  }}
                >
                  <img
                    id="main-logo"
                    className="d-inline-block align-top mr-1"
                    style={{ maxWidth: "200px", marginTop: -40 }}
                    src={require("../../images/Logo smart farm1.jpg")}
                    alt="Smart Farm"
                  />
                </div>
              </div>
              <h5>{t('Sign up')}</h5>
              <form className="pt-3">
                <div className="form-group">
                  <input
                    value={name}
                    type="text"
                    className={`form-control form-control-lg ${nameError ? 'is-invalid' : ""}`} 
                    placeholder={t('name')}
                    onChange={(e) =>setName(e.target.value)}
                  />
                  <div className="invalid-feedback">{nameError}</div>
                </div>
                <div className="form-group">
                  <input
                  value={email}
                    type="email"
                    className={`form-control form-control-lg ${emailError ? "is-invalid" : ""}`}
                    placeholder={t('email')}
                    onChange={(e) =>setEmail(e.target.value)}

                  />
                   <div className="invalid-feedback">{emailError}</div>

                </div>
                <div className="form-group">
                  <input
                  value={password}
                    type="password"
                    className={`form-control form-control-lg ${mdpError ? "is-invalid" : ""}`}
                    placeholder={t('Password')}
                    onChange={(e) =>setPassword(e.target.value)}

                  />
                  <div className="invalid-feedback">{mdpError}</div>
                </div>
                <div className="form-group">
                  <input
                  value={confirmPass}
                    type="password"
                    className={`form-control form-control-lg ${confMdpErr ? "is-invalid" : ""}`}
                    placeholder={t('ConfirmPassword')}
                    onChange={(e) =>setConfirmPass(e.target.value)}

                  />
                  <div className="invalid-feedback">{confMdpErr}</div>
                </div>
                <div className="form-group">
                  <label htmlFor="select">Account type</label>
                  <select className="form-control form-control-lg" id="select" value={role} onChange={e => setRole(e.target.value)}>
                    <option value="ROLE_USER">User</option>
                    <option value="ROLE_SUPPLIER">Supplier</option>
                  </select>
                </div>
                {
                  role === "ROLE_USER"
                  ?
                  <div className="form-group">
                  <select className="form-control form-control-lg" onChange={(e) =>  setOfferType(e.target.value)}>
                    <option value="" selected disabled>{t('select_offer')}</option>
                    <option value="1">Gartuit</option>
                    <option value="2">Payante</option>
                  </select>
                </div>
                  :
                  null
                }

                <div className="mb-4">
                  <div className="form-check">
                    <label className="form-check-label text-muted">
                      <input
                        onClick={() => setAccepted(!hasAccepted)}
                        type="checkbox"
                        className="form-check-input"
                        required
                      />
                      <i className="input-helper"></i>{t('i_agree')}
                    </label>
                  </div>
                </div>
                <div className="mt-3">
                    <button
                      className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn"
                      style={{
                        backgroundColor: "#0daaa2",
                        borderColor: "#0daaa2"
                      }}

                      onClick={
                        handleSubmit
                      }
                    >
                      {t('Sign up')}
                    </button>
                </div>
                <div className="text-center mt-4 font-weight-light">
                  {t('already_account')}{" "}
                  <Link to="/Login" 
style={{ color: "#0daaa2" }}>
                    {t('sign_in')}
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
      <Notifications />
    </div>
  );
};

Register.propTypes = {
  setToken: PropTypes.func.isRequired
};

export default Register;
