import React from "react";
import {
  Container,
  Row,
  Col,
  CardBody,
  CardHeader,
  Card,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Tooltip,
  FormInput,
  FormSelect,
  FormGroup,
  Form
} from "shards-react";
import moment from 'moment';

export default function SatellitePage() {
  return (
    <Container fluid className="main-content-container px-3 pb-2">
      <Row className="page-header py-2 mb-4">
        <PageTitle
          subtitle={t("overview")}
          title={t("Satellite Images")}
          className=" mb-1"
        />
      </Row>

      <Container className="main-content-container p-3 border bg-light rounded">
        <Row>
          <Col lg="8" md="12" sm="12">
            <Row className="pt-4">
              <Col lg="8" md="12" sm="12" className="py-2 d-md-block d-lg-none">
                {/* Select input on mobile view */}
                <Card className="mt-0" style={{ height: "100%" }}>
                  <CardHeader className="border-bottom">
                    <FormSelect
                      value={selectedField}
                      onChange={getSelectedField}
                    >
                      <option value="">{t("select_field")}</option>
                      {fields.map(field => {
                        return (
                          <option key={field.Id} value={field.Id}>
                            {field.title}
                          </option>
                        );
                      })}
                    </FormSelect>
                  </CardHeader>
                </Card>
              </Col>
              <Col lg="12" md="12" sm="12">
                م
                <SatteliteMap
                  farms={farms}
                  data={selectedField}
                  satellitesImages={selectedImages}
                  selectedData={dataDisplayed}
                  drawn={polygonDisplayed}
                  draw={mapConfig.draw}
                  edit={mapConfig.edit}
                  zoom={coords.zoom}
                  center={coords.center}
                  fromAction={coords.fromAction}
                />
              </Col>
            </Row>
          </Col>

          <Col lg="4" md="12" sm="12" className="my-2">
            <Card className="mt-0" style={{ height: "100%" }}>
              {fields.length === 0 ? (
                <LinearProgress />
              ) : (
                <>
                  <CardHeader className="border-bottom d-none d-lg-block">
                    <FormSelect
                      value={selectedField}
                      onChange={getSelectedField}
                    >
                      <option value="">{t("select_field")}</option>
                      {fields.map(field => {
                        return (
                          <option key={field.Id} value={field.Id}>
                            {field.title}
                          </option>
                        );
                      })}
                    </FormSelect>
                  </CardHeader>
                  <CardBody className="p-1">
                    {selectedField.length > 0 &&
                      selectedField.map(field => {
                        return (
                          <>
                            <Row>
                              <Col lg="12" md="12" sm="12">
                                <h5
                                  style={{ fontSize: 14, textAlign: "center" }}
                                >
                                  {t("lat")}{" "}
                                  <span
                                    style={{
                                      fontWeight: "bold",
                                      textAlign: "center"
                                    }}
                                  >
                                    {field.Latitude}
                                  </span>
                                </h5>
                              </Col>
                              <Col lg="12" md="12" sm="12">
                                <h5
                                  style={{ fontSize: 14, textAlign: "center" }}
                                >
                                  {t("lon")}{" "}
                                  <span
                                    style={{
                                      fontWeight: "bold",
                                      textAlign: "center"
                                    }}
                                  >
                                    {field.Longitude}
                                  </span>
                                </h5>
                              </Col>
                            </Row>
                            <Row className="border-bottom">
                              <Col lg="12" md="12" sm="12">
                                <h6
                                  style={{ fontSize: 14, textAlign: "center" }}
                                >
                                  {t("name_field")} <br />{" "}
                                  <span
                                    style={{
                                      fontWeight: "bold",
                                      textAlign: "center"
                                    }}
                                  >
                                    {field.title}
                                  </span>
                                </h6>
                              </Col>
                            </Row>
                          </>
                        );
                      })}
                    {selectedField.length === 0 && (
                      <div
                        style={{
                          color: "#bebebe",
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          flexDirection: "column",
                          padding: "10px"
                        }}
                      >
                        <p style={{ color: "#bebebe", textAlign: "center" }}>
                          {t("message_image")}
                        </p>
                        <i
                          className="fas fa-satellite"
                          style={{ fontSize: "40px" }}
                        ></i>
                      </div>
                    )}
                    <Row className="my-2">
                      <Col lg="12" md="12" sm="12">
                        ا
                        {selectedField.length > 0 && (
                          <>
                            <h5 style={{ fontSize: 14, textAlign: "center" }}>
                              {" "}
                              Satellite Images List
                            </h5>
                            <Calendar
                              onChange={date =>
                                handleDateClick(
                                  moment(date).format("D MMM YYYY")
                                )
                              }
                              value={
                                selectedDate
                                  ? new Date(moment(selectedDate, "D MMM YYYY"))
                                  : null
                              }
                              locale={
                                localStorage.getItem("local")
                                  ? `${localStorage.getItem(
                                      "local"
                                    )}-${localStorage
                                      .getItem("local")
                                      .toUpperCase()}`
                                  : "en-EN"
                              }
                              tileContent={({ date, view }) => {
                                const formattedDate = moment(date).format(
                                  "D MMM YYYY"
                                );
                                const hasSatelliteImages = satellitesImages.some(
                                  data =>
                                    moment(data.created_at).format(
                                      "D MMM YYYY"
                                    ) === formattedDate
                                );

                                return view === "month" &&
                                  hasSatelliteImages ? (
                                  <span
                                    style={{
                                      fontWeight: "bold",
                                      color: "#29B2C4"
                                    }}
                                  >
                                    •
                                  </span>
                                ) : null;
                              }}
                            />
                          </>
                        )}
                      </Col>
                    </Row>
                  </CardBody>
                </>
              )}
            </Card>
          </Col>
        </Row>
      </Container>
    </Container>
  );
}
