import React ,{useEffect,useState,useRef} from 'react'
import { Button, Row, Col, FormGroup, Form, FormInput, Container, Card,CardBody,CardHeader ,FormSelect, Nav, NavItem, NavLink } from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from "react-i18next";
import RangeDatePicker from '../components/common/RangeDatePicker';
import api from '../api/api';
import PaginateHistory from './PaginateHistory';
import { useHistory, useParams   } from 'react-router-dom';
import moment from 'moment';
import { DownloadTableExcel } from "react-export-table-to-excel";


const SensorHistory = () => {
    const table = useRef(null)
    const { t, i18n } = useTranslation();
    const history = useHistory()
    const [codeSensor,setCodeSensor] = useState('')
    const [historyData,setHistoryData] = useState('')
    const [sensorList,setSensorList] = useState([])
    const [dateValue,setDateValue] = useState({
        startDate : null,
        endDate : null
    })
    const [currentPage, setCurrentPage] = useState(1);
    const [historyPerPage] = useState(50);
    const paginate = pageNumber => setCurrentPage(pageNumber);
    let { id } = useParams();
   useEffect(()=>{


        console.log(id)


       const getSensorsList = async () => {

            await api.get(`/sensor/sensors`)
            .then(response=>{
                let sensors = response.data
                setSensorList(sensors)
            }).catch(err=>{
                console.log(err)
            })


        }
        getSensorsList()
   },[]) 

   const getHistorySensor = () => {
    if(codeSensor !== "" ){
        api.get(`/sensor/sensor-history/${codeSensor}`)
        .then(response=>{
            let historyResult = response.data.history
            setHistoryData(historyResult)
        })
        .catch(err=>{
            console.log(err)
        })

    }
   }
    useEffect(()=>{
        if(typeof id !== 'undefined'){
            setCodeSensor(id)
        }
        getHistorySensor()
    },[codeSensor])

   const filteredHistory = historyData && historyData.filter(history => {
    let dates = new Date(history.time)
        let filterHistory = true
        if (dateValue.startDate) {
          filterHistory = filterHistory && (new Date(dateValue.startDate) < dates)
        }
        if (dateValue.endDate) {
          filterHistory = filterHistory && (new Date(dateValue.endDate) > dates)
        }
        return filterHistory

})

const indexOfLastRecord = currentPage * historyPerPage;
const indexOfFirstRecord = indexOfLastRecord - historyPerPage;
const currentHistory = filteredHistory.slice(indexOfFirstRecord, indexOfLastRecord);
const nPages = Math.ceil(historyData.length / historyPerPage)

    console.log(historyData)

  return (
    <Container fluid className="main-content-container p-4">
        <Row noGutters className="page-header d-flex justify-content-between align-items-center py-4">
            <PageTitle 
                 sm="4"
                 subtitle={t('View your sensors data history')}
                 title='My Sensors History'
                 className="text-sm-left"
            />
            <div>
                <Button theme="info" onClick={() => history.push('/AddSensor')}><i className='material-icons'>&#xe145;</i> Add Sensor</Button>
            </div>
        </Row>
        <Row className="d-flex justify-content-around align-items-center">
            {
           (typeof id === 'undefined')
           ?
            <Col lg='4' md="12" sm="12">
            <FormGroup>
                    <FormSelect
                        onChange={(e) => setCodeSensor(e.target.value)}
                        value={codeSensor}
                    >
                        <option value="">{t('select_sensor')}</option>
                            {
                                    sensorList.map(sensor=>{
                                        return(

                                            <option style={{cursor :"pointer"}} value={sensor.code}>{sensor.code}</option>


                                            )
                                        })
                            }
                    </FormSelect>
            </FormGroup>
            </Col>
            :
            ''
            }
            <Col className="mb-3" lg='4' md="12" sm="12">
                <RangeDatePicker

                onChange={value =>
                    setDateValue({endDate : value.endDate , startDate : value.startDate})
                }
                EndingDatePlaceHolder=" Date"
                StartingDatePlaceHolder=" Date"
            />

            </Col>
        </Row>
        <Row>
            <Col lg='12' md="12" sm="12">
                <Card>
                    <CardHeader className="border-bottom">All Sensors</CardHeader>
                    <CardBody>
                        <Row>
                            <Col lg='12'md="12" sm="12">
                                {/* <PaginateHistory nPages={nPages} currentPage={currentPage} setCurrentPage={setCurrentPage} /> */}
                                <div className='d-flex justify-content-end'>
                                    <DownloadTableExcel
                                        filename="history table"
                                        sheet="history"
                                        currentTableRef={table.current}
                                    >
                                    <Button theme="info" ><i className='material-icons'>&#xf090;</i></Button>

                                    </DownloadTableExcel>

                                </div>
                                <div id="table-wrapper">
                                    <div id="table-scroll">
                                        <table ref={table} className="table mb-4 table-hover table-bordered  tabel-responsive-lg " style={{overflowX : "scroll"}}>
                            <thead className="bg-light text-center">
                                <tr>
                                    <th scope="col" className="border-0">{t('Date')}</th>
                                    <th scope="col" className="border-0">{t('Temp.')} (Â°C)</th>
                                    <th scope="col" className="border-0">{t('Humidity')} (%)</th>
                                    <th scope="col" className="border-0">{t('Pressure')} (Pa)</th>
                                    <th scope="col" className="border-0">{t('Humidity 1')} (%)</th>
                                    <th scope="col" className="border-0">{t('Humidity 2')} (%)</th>
                                    <th scope="col" className="border-0">{t('Humidity 3')} (%)</th>

                                </tr>
                            </thead>
                                <tbody>
                                            {
                                            filteredHistory && filteredHistory.map(sensorData=>{
                                                return(
                                                        <tr>
                                                            <td>{sensorData.time}</td>
                                                            <td>{sensorData.temperature}</td>
                                                            <td>{sensorData.humidity}</td>
                                                            <td>{sensorData.pressure}</td>
                                                            <td>{sensorData.mv1}</td>
                                                            <td>{sensorData.mv2}</td>
                                                            <td>{sensorData.mv3}</td>
                                                        </tr>

                                                        )
                                                    })
                                                }

                                </tbody>
                                        </table>


                                    </div>

                                </div>

                            </Col>
                        </Row>
                    </CardBody>
                </Card>

            </Col>
        </Row>
    </Container>
  )
}

export default SensorHistory
