import React from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardHeader,
  Button
} from "shards-react";

import PageTitle from "../components/common/PageTitle";
import "./../assets/Styles.css";
import "./Styles.css";
import FieldSetupForm from "../components/FieldSettingForms/FieldSetupForm";
import FieldSoilForm from "../components/FieldSettingForms/FieldSoilForm";
import FieldCropForm from "../components/FieldSettingForms/FieldCropForm";
import FieldIrrigationForm from "../components/FieldSettingForms/FieldIrrigationForm";

import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import Map from "./leafletMap";

class FieldSettings extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      setupCardSave: false,
      soilCardSave: false,
      cropsCardSave: false,
      irrigationCardSave: false,
      data: {
        fieldName: "a",
        farmName: "b",
        desc: "c",
        soilProp: "d",
        depth: [
          {
            depthLevel: "",
            soilType: "",
            clay: "",
            sand: "",
            silt: "",
            cec: "",
            ph: "",
            om: "",
            ecd: ""
          },
          {
            depthLevel: "",
            soilType: "",
            clay: "",
            sand: "",
            silt: "",
            cec: "",
            ph: "",
            om: "",
            ecd: ""
          },
          {
            depthLevel: "",
            soilType: "",
            clay: "",
            sand: "",
            silt: "",
            cec: "",
            ph: "",
            om: "",
            ecd: ""
          }
        ],
        crop: "",
        prevCrop: "",
        gdd: "",
        startDate: "",
        endDate: "",
        irrigationType: "",
        flowRate: "",
        irrigatedPercentage: ""
      }
    };
  }

  componentDidMount() {
    const fetchData = async () => {
      const response = await fetch(`http://127.0.0.1:9000/getFieldData`);
      const newData = await response.json();
      this.setState({ data: newData });
    };

    //fetchData();
  }

  render() {
    return (
      <Container fluid className="main-content-container px-4">
        {/* Page Header */}
        <Row noGutters className="page-header py-4">
