                    <IrrigationList
                      irrigationsList={this.state.irrigationsData}
                      Irrigations={this.getDataIrrigations}
                      Crops={this.state.cropsData}
                      Zones={this.state.zonesData}
                    />
                  </Row>
                  {/* <div className="d-flex justify-content-center align-items-center">
