                    <NavLink id="irrig" onClick={(e) => this.setState({ elemValue: e.target.id })} className={`${this.state.elemValue === "irrig" ? "bg-info rounded text-dark" : 'rounded text-dark'}`} href="#">{t('Irrigation_info')}</NavLink>
