 const validate = () => {
    let nameError = '';
    let emailError = '';
    let mdpError = '';
    let confMdpErr = '';
    if (!name) {
      nameError = 'Username cannot be blank!';
      setNameErr(nameError)
    }
    if (!email) {
      emailError = 'Email cannot be blank!';
      setEmailErr(emailError)
     }
       else if (typeof email !== 'undefined') {
       var mailformat = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (!mailformat.test(email)) {
          emailError = 'Incorrect email format!';
          setEmailErr(emailError)
           }
      }
     if (password.length < 6) {
      mdpError = 'Cannot be less 6 character!';
      setPassErr(mdpError)
     }
     if (password != confirmPass) {
      confMdpErr = `Values don't match`;
      setconfMdpErr(confMdpErr)
     }
     if (emailError ||  nameError || mdpError || confMdpErr ) {
      setEmailErr(emailError);
      setNameErr(nameError);
      setPassErr(mdpError);
      setconfMdpErr(confMdpErr)
      return false;
    }
      return true;
  };

 const handleSubmit = (event) =>{
    event.preventDefault()

    const isValid = validate();
    if(isValid) {
        handleRegister()

    }else{
      return false;
    }
 } 


  const handleRegister = async () => {



    let data = {
      name : name,
      email: email,
      phone_number:num, 
      password: password,
      confirmPassword : confirmPass ,
      offer_type : offerType,
      role: role
    }
    if(role === "ROLE_SUPPLIER"){
      data.offer_type = '2'
    }
      await  api.post('/signup' ,data)
        .then(res => {
          if(res.data.type && res.data.type == "danger") {
            swal({
              icon: 'error',
              title: 'Oops...',
              text: res.data.message
            })
            return false;
          }
          if(res.data.type && res.data.type == "warnning") {
            swal({
              icon: 'warning',
              title: 'Oops...',
              text: res.data.message
            })
            setSendEmail(true , resetForm())
          }
          if(res.data.type && res.data.type == "success") {
            swal({
              icon: 'success',
              title: 'OK',
              text: 'Email sent check your inbox to confirm'
            })
            setSendEmail(true , resetForm())

          }
          }).catch(() => {
            swal({
              icon: 'error',
              title: 'Oops...',
              text: 'error_add_user'
            })
            return false;
          })
        }
        const resetForm = () => {
          setNameErr('')
          setEmailErr('')
          setPassErr('')
          setName("");
          setEmail("");
          setNum('')
          setPassword("");
          setConfirmPass("");
          setOfferType(null);

          setTimeout(() => {
            setSendEmail(false)
          }, 3000);
        }


        const changeLanguageHandler = async (e) => {
          const languageValue = e.target.value
          i18n.changeLanguage(languageValue);
          localStorage.setItem("local",languageValue)
          setLocal(languageValue)
        }
  return (
