                  />
                  <div className="invalid-feedback">{mdpError}</div>
                </div>
                <div className="form-group">
                  <input
                  value={confirmPass}
                    type="password"
                    className={`form-control form-control-lg ${confMdpErr ? "is-invalid" : ""}`}
                    placeholder={t('ConfirmPassword')}
                    onChange={(e) =>setConfirmPass(e.target.value)}

                  />
                  <div className="invalid-feedback">{confMdpErr}</div>
                </div>
                <div className="form-group">
                  <label htmlFor="select">{t('account_type')}</label>
                  <select className="form-control form-control-lg" id="select" value={role} onChange={e => setRole(e.target.value)}>
                  <option value="" selected disabled>{t('select_type')}</option>
                    <option value="ROLE_USER">{t('user')}</option>
                    <option value="ROLE_SUPPLIER">{t('supplier')}</option>
                  </select>
                </div>
                {
                  role === "ROLE_USER"
                  ?
                  <div className="form-group">
                  <select className="form-control form-control-lg" value={offerType} onChange={(e) =>  setOfferType(e.target.value)}>
                    <option value="" selected disabled>{t('select_offer')}</option>
                    <option value="1">{t('gratuit')}</option>
                    <option value="2">{t('payante')}</option>
                  </select>
                </div>
                  :
                  null
                }

                <div className="mb-4">
                  <div className="form-check">
                    <label className="form-check-label text-muted">
                      <input
                        onClick={() => setAccepted(!hasAccepted)}
                        type="checkbox"
                        className="form-check-input"
                        required
                      />
                      <i className="input-helper"></i>{t('i_agree')}
                    </label>
                  </div>
                </div>
                <div className="mt-3">
                    <button
                      className="btn btn-block  btn-lg font-weight-medium auth-form-btn"
                      style={{color : 'white', backgroundColor : '#27A6B7'}}
                      onClick={
                        handleSubmit
                      }
