                  <Link to="/Privacy" 
                    style={{ color: "#0daaa2" }}>
                    {t('Privacy Policy')}
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
      <Notifications />
    </div>
  );
};

