import moment from 'moment';
import React , {useState,useEffect}from 'react'
import { Line } from  'react-chartjs-2';
import { useTranslation } from "react-i18next";
import { Col } from 'shards-react';


const WaterChart = ({data}) => {
    const [state,setState] = useState(null)
    const { t, i18n } = useTranslation();


    const getChartData = () => {

        let labels = []   
        let dataBilan = []
        let RuMax = []
        let RuMin = []

        for (let i = 0; i <= data.length; i++) {
          if(data[i] && data[i].dates){
            let allDate = data[i].dates
              let date = moment(allDate).locale('En').format('MMM DD YYYY')
               labels.push(date)
              dataBilan.push(parseFloat(data[i].bilan).toFixed(2))
              RuMax.push(data[i].RUmax)
              RuMin.push(data[i].RUmin)

          }

        }
        const state = {
            labels: labels,
            datasets: [
              {
                label: `${t('water_balance')}`,
                fill: false,
                lineTension: 0.5,
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: '#27A6B7',
                borderWidth: 2,
                data: dataBilan,
              },
              {
                label: `${t('Ru max (mm)')}`,
                fill: false,
                lineTension: 0.5,
                backgroundColor: '#c0724b',
                borderColor: '#b77627',
                borderWidth: 2,
                data: RuMax
              },
              {
                label: `${t('Ru min (mm)')}`,
                fill: false,
                lineTension: 0.5,
                backgroundColor: '#e5331b',
                borderColor: '#e5331b',
                borderWidth: 2,
                data: RuMin
              }
            ]
          }
          setState(state)
    }

    useEffect(()=>{
        getChartData()
    },[data])

    return (
      <Line
      data={state}
          options={{
            title:{
              display:false,
              text:`${t('water_balance')}`,
              fontSize:16
            },
            legend:{
              display:true,
              position:'bottom'
            },
            scales: {
              yAxes: [{
                  ticks: {
                      beginAtZero: true,
                      max: 180,
                      min: 0
                  }
              }]
          },
          }}
      />

  );
}

export default WaterChart
