import React from 'react'

const PaginateHistory = ({ page, limit, totalItems, onPageChange }) => {
    const totalPages = Math.ceil(totalItems / limit);
    const pages = [...Array(totalPages).keys()].map((p) => p + 1);
    const handlePageSelect = (event) => {
        onPageChange(parseInt(event.target.value));
      };
    return (
                <nav>
            <ul className='pagination justify-content-center'>
                <li className="page-item">
                    <button className="page-link" 
                         disabled={page === 1}
                        onClick={() => onPageChange(page - 1)} 
                        href='#'>

                        Previous
                    </button>
                </li>
                {/* <span>{page} of {totalPages}</span> */}
                {/* <select style={{outline:"none"}} value={page} onChange={handlePageSelect}>
                    {pages.map((p) => (
                    <option style={{outline:"none"}} key={p} value={p}>{p}</option>
                    ))}
                </select> */}
                <li className="page-item">
                    <button className="page-link" 
                        disabled={page === totalPages}
                        onClick={() => onPageChange(page + 1)}
                        href='#'>

                        Next
                    </button>
                </li>
            </ul>
        </nav>
    );
  };

