import React, { useEffect, useState } from "react";
import { useHistory, Link } from 'react-router-dom'
import { Container, Row, Col, CardBody, Card, Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Tooltip, FormInput, FormSelect, FormGroup, Form } from "shards-react";
import PageTitle from "../components/common/PageTitle";
import SmallStats from "../components/common/SmallStats";
import "./../assets/Styles.css";
import "./Styles.css";
import LeafletMap from "./map";
import SignalCellularNodataIcon from "@mui/icons-material/SignalCellularNodata";
import SignalCellular4BarIcon from "@mui/icons-material/SignalCellular4Bar";
import { Modal, ProgressBar } from "react-bootstrap";
import api from '../api/api';
import { useTranslation } from "react-i18next";
import FilterFields from './FilterFields'
import swal from "sweetalert";
import useSensorData from "../utils/useSensorData";
import AddField from "./AddField";
import FieldSetupForm from "../components/FieldSettingForms/FieldSetupForm";
import FieldSoilForm from "../components/FieldSettingForms/FieldSoilForm";
import FieldCropForm from "../components/FieldSettingForms/FieldCropForm";
import CompositeSoil from "../components/FieldSettingForms/compositeSoilForm";
import DripForm from "../components/FieldSettingForms/dripForm";
import LateralForm from "../components/FieldSettingForms/lateralForm";
import PivotForm from "../components/FieldSettingForms/pivotForm";
import clap from "../images/applause.png";


const Overview = (props) => {
  const [toggle, setToggle] = useState(false)
  const [farms, setFarms] = useState([]);
  const [draw, setDraw] = useState({
    polygon: false,
    circle: false,
    rectangle: false,
    polyline: false,
    marker: false,
    circlemarker: false
  })
  const history = useHistory();
  const [steps, setSteps] = useState(0)
  // const [offer,setOffer] = useState(null)
  const [configMap, setConfigMap] = useState({
    draw: {
      polygon: true,
      circle: false,
      rectangle: false,
      polyline: true,
      marker: true,
      circlemarker: false,
    },
    edit: {
      delete: false,
      edit: false
    }
  })
  const [layer, setLayer] = useState('')

  const [coords, setCoords] = useState({
    Latitude: "",
    Longitude: "",
    zoom: "",
    center: [],
    fromAction: false
  })
  const _onCreated = e => {
    let type = e.layerType;

    let layer = e.layer;
    if (type === "polygon") {
      let coords = layer._latlngs[0];
      const Coordinates = coords.map((coord) => ({
        Lat: coord.lat,
        Long: coord.lng,
      }));
      if (Coordinates)
        setLayer(JSON.stringify(Coordinates))
    } else {
      setCoords({ Latitude: layer.getLatLng().lat, Longitude: layer.getLatLng().lng });
    }

  };
  // let user = JSON.parse(localStorage.getItem("user"));
  // let offerType = user.offer_type;
  const { t, i18n } = useTranslation();

  const [fieldStats, setFS] = useState([])
  const [sensorStats, setSensorStats] = useState([])
  const [open, setOpen] = useState(false)
  const [userMapDetails, setUserMapDetails] = useState("#")
  const [layerFarm, setLayerFarm] = useState([])
  const [mapConfig, setMapConfig] = useState({
    zoom: "",
    center: [],
    fromAction: false,
  })



  let sensorsData = useSensorData('/sensor/sensor-update-data')

  const toggleDropDown = () => {
    setToggle(!toggle)
  }

  const toggleToolTip = () => {
    setOpen(!open)
  }

  const getFieldStats = async () => {
    const response = await api.get('/dashboard/fields');
    setFS(response.data.farms);
    setUserMapDetails(response.data.user_map_details.map_link_details)
  }

  const getSensorsStats = async () => {
    const response = await api.get('/dashboard/sensors');
    setSensorStats(response.data.sensors);
  }
  const [crops, setCrops] = useState([])
  const [dataCrops, setDataCrops] = useState([]);

  // const fetchDataCrops = async () => {
  //   await api.get('/crop/crops')
  //     .then(response => {
  //       let dataCrops = response.data.farms;
  //       setDataCrops(dataCrops)
  //       let Crops = [];
  //       dataCrops.map(farm => {
  //         let fields = farm.fields;
  //         if (fields) {
  //           fields.map(field => {
  //             let crops = field.crops
  //             if (crops) {
  //               crops.map(crop => {
  //                 Crops.push({
  //                   type: crop.type,
  //                   id: crop.id,
  //                   fieldId: crop.field_id
  //                 })
  //               })
  //             }
  //           })
  //         }
  //       })
  //       setCrops(Crops)
  //     }).catch(err => {
  //       console.log(err)
  //     })
  // }

  const getLayerFarm = async () => {
    await api.get('/farm/farms').then(res => {
      const DataFarm = res.data.farms;
      setLayerFarm(DataFarm);
    })
  }

  useEffect(() => {
    // getLastDataSensor()
    getLayerFarm()
    getFieldStats();
    getSensorsStats();
    // fetchDataCrops()
  }, [])
  let critical = 0;
  let full = 0;
  let opt = 0;

  fieldStats.map(item => {
    item.fields.map(field => {
      let status = field.status
      if (status) {
        if (status == "Optimal") {
          opt++
        }
        if (status == "Full") {
          full++
        }
        if (status == "Critical") {
          critical++
        }
      }

    })
  });




  let smallStats = [
    {
      state: `${t('Critical')}`,
      icon: <i class="fas fa-tint-slash"></i>,
      label: `${t('fields')}`,
      value: critical,
      chartLabels: [null, null, null, null, null, null, null],
      attrs: { md: "6", sm: "6" },
      datasets: [
        {
          label: "Today",
          fill: "start",
          borderWidth: 1.5,
          backgroundColor: "rgba(0, 184, 216, 0.1)",
          borderColor: "rgb(0, 184, 216)",
          data: []
        }
      ]
