          />;
      case `${t('Lateral')}`:
          return <LateralForm
              handleLateral={(e) => setIrrigData({...irrigData , lateral: e.target.value})}
              handlePivotLength={(e) => setIrrigData({...irrigData , pivot_length : e.target.value})}
              handleRunTime={(e) => setIrrigData({...irrigData , full_runtime :e.target.value})}
