import React, { useContext, useEffect, useState } from "react";
import { Link, Redirect ,useHistory} from "react-router-dom";
import { Form } from "react-bootstrap";
import PropTypes from "prop-types";
import { Buffer } from "buffer";
import { useTranslation } from "react-i18next";
import swal from 'sweetalert';



const Login = ({ setToken }) => {
  const [credentials, setCredentials] = useState({ email: "", pwd: "" });
  const [rememberMe, setRM] = useState(false);
  const [msgError, setMsgError] = useState({msg:"", displayMsg:"hide"});
  const [JWT, setJWT] = useState(null);
  const [RfreshJWT, setRefreshJWT] = useState(null);
  const [ local, setLocal ] =  useState(localStorage.getItem("local")||"en");
  const { t, i18n } = useTranslation();



  const handleSubmit = async (e) => {
    e.preventDefault();
    LoginApi();
  };
  const history = useHistory();
  const LoginApi = async() => {
    var requestOptions = {
      method: "POST",
      headers: { 'Content-Type': 'application/json' },
      redirect: "follow",
      body: JSON.stringify({ password: credentials.pwd, email: credentials.email })
    };
    await fetch(`${process.env.REACT_APP_BASE_URL}/login`, requestOptions)
      .then(response => response.json())
      .then(result => {

        //setJWT(result.token);
        var data = result.result;
        if(result.errors) {
          let errorsData = result.errors
          errorsData.map((item, indx) => {
            setMsgError({msg:item.msg, displayMsg:"show"})
          })
        }
        if(result.type && result.type == "danger") {
          setMsgError({msg:result.message, displayMsg:"show"})
        }
        if(data){
          localStorage.setItem("user", JSON.stringify({"email":data.email, "name": data.name, "id": data.uid, "role": data.role, "offer_type" : data.offer_type,"avatar": data.upload_file_name}));          
          localStorage.setItem("token",JSON.stringify({"token":result.token}))
          localStorage.setItem("refreshToken",result.refreshToken)
          if(data.role == "ROLE_ADMIN"){
            history.push("/admin/users")

          }else if (data.role == "ROLE_USER" && data.offer_type == '2'){
            history.push("/")
          } else if (data.role == "ROLE_USER" && data.offer_type == '1'){
            history.push("/Bilan")

          }
          window.location.reload();
        }

      })
      .catch(error => {
        console.log("error JWT", error);
      });
  };
    const changeLanguageHandler = async (e) => {
      const languageValue = e.target.value
      i18n.changeLanguage(languageValue);
      localStorage.setItem("local",languageValue)
      setLocal(languageValue)
    }
  return (
    <div style={{ marginTop: 25 }}>
      <div className="d-flex align-items-center auth px-0">
        <div className="row w-100 mx-0">
        <div className="col-lg-12 text-right">
          <select value={local} className="custom-select" style={{width: 200}} onChange={changeLanguageHandler}>
            <option value="en">English</option>
            <option value="fr">Frensh</option>
          </select>
        </div>
          <div className="col-lg-4 mx-auto">
            <div className="card text-left py-5 px-4 px-sm-5">
              <div className="brand-logo">
                <div
                  style={{
                    height: 60,
                    width: "100%",
                    overflow: "hidden",
                    textAlign: "center"
                  }}
                >
                  <img
                    id="main-logo"
                    className="d-inline-block align-top mr-1"
                    style={{ maxWidth: "200px", marginTop: -40 }}
                    src={require("../../images/Logo smart farm1.jpg")}
                    alt="Smart Farm"
                  />
                </div>
              </div>
              <div className={`mb-0 alert alert-danger fade ${msgError.displayMsg}`} role="alert"><i class="fa fa-info mx-2"></i>{msgError.msg}</div>
              <h5>{t('sign_in')}</h5>
              <form className="pt-3">
                <div className="form-group">
                  <input
                    type="email"
                    className="form-control form-control-lg"
                    placeholder={t('email')}
                    onChange={e =>
                      setCredentials({ ...credentials, email: e.target.value })
                    }
                  />
                </div>
                <div className="form-group">
                  <input
                    type="password"
                    className="form-control form-control-lg"
                    placeholder={t('Password')}
                    onChange={e =>
                      setCredentials({ ...credentials, pwd: e.target.value })
                    }
                  />
                </div>
                <div className="mt-3">
                  <button
                      style={{color : 'white', backgroundColor : '#27A6B7'}}
                      className="btn btn-block  btn-lg font-weight-medium auth-form-btn"

                      onClick={
                        handleSubmit
                      }
                    >
                      {t('sign_in')}
                  </button>
                </div>
                <div className="my-2 d-flex justify-content-between align-items-center">
                  <div className="form-check">
                    <label className="form-check-label text-muted">
                      <input
                        onClick={() => {
                          localStorage.setItem("RememberMe", !rememberMe);
                          setRM(!rememberMe);
                        }}
                        type="checkbox"
                        className="form-check-input"
                      />
                      <i className="input-helper"></i>
                      {t('keep_me')}
                    </label>
                  </div>
                  <Link to="/ForgotPwd" style={{ color: "#0daaa2" }}>{t('forgot_pass')} </Link>
                </div>
                <div className=" mt-4 font-weight-light">
                  {t('have_account')} 
                  <Link to="/Register" 
                  style={{ color: "#0daaa2"}}>
                    {t('Sign up')}
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

Login.propTypes = {
  setToken: PropTypes.func.isRequired
};

export default Login;
