import React, { useEffect, useState } from 'react'
import { Link, useParams, useHistory } from 'react-router-dom'
import api from '../api/api'
import { Container, Card, Row,Col, CardHeader ,Nav,NavItem,NavLink ,CardBody,Button} from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from "react-i18next";
import FieldList from '../views/FieldList'
import AddField from '../views/AddField'
import { Carousel } from "react-responsive-carousel";
import CropList from '../views/CropList'
import ZoneList from '../views/ZoneList'
import IrrigationList from '../views/IrrigationList'
import swal from 'sweetalert'
import FieldSetupForm from '../components/FieldSettingForms/FieldSetupForm'
import FieldCropForm from '../components/FieldSettingForms/FieldCropForm'
import FieldSoilForm from '../components/FieldSettingForms/FieldSoilForm'
import FieldIrrigationForm from '../components/FieldSettingForms/FieldIrrigationForm'


const FieldsList = () => {

    const { t, i18n } = useTranslation();
    const history = useHistory()

    const params = useParams();
    const [fields, setFields] = useState([]);
    const [crops, setCrops] = useState([]);
    const [zones, setZones] = useState([]);
    const [irrigations, setIrrig] = useState([]);
    const [farms, setFarms] = useState([])
    let Uid = params.uid;
    const [elemValue,setView] = useState('field')
    const [dataField,setDataField] = useState({
        name : "",
        farm_uid :"",
        description :""
    })
    const [errors,setErrors] = useState({
        farmError :"",
        nameError :""
    })

    const getFieldsByUser = async () => {
        let url = `/admin/user/${Uid}/fields`;
        await api.get(url)
            .then(response => {
                if (response.data.type === "success") {
                    let FarmsData = response.data.farms
                    console.log(FarmsData)
                    setFarms(FarmsData);
                }

            }).catch(error => {
                console.log(error)
            })
    }

    const getFields = () => {
        let Fields = [];
        farms.map(item => {
            let fields = item.fields;
            if (fields) {
                fields.map(field => {
                    Fields.push({
                        title: field.name,
                        status: field.status,
                        description: field.description,
                        Uid: field.uid,
                        farm_id: field.farm_id,
                        Id: field.id
                    })
                })
            }
        })
        setFields(Fields)
    }

    const getZones = () => {
        let Zones = [];
        farms.map(item => {
            let fields = item.fields;
            if (fields) {
                fields.map(itemZone => {
                    let zones = itemZone.zones;
                    if (zones) {
                        zones.map(i => {
                            Zones.push({
                                Id: i.id,
                                name: i.name,
                                Uid: i.uid,
                                source: i.source,
                                description: i.description,
                                field_id: i.field_id

                            });
                        });
                    };
                });
            };
        })
        setZones(Zones)
    }

    const getCrops = () => {
        let Crops = [];
        farms.map(item => {
            let fields = item.fields;
            if (fields) {
                fields.map(itemCrop => {
                    let crops = itemCrop.crops;
                    if (crops) {
                        crops.map(i => {
                            Crops.push({
                                type: i.type,
                                address: i.address,
                                Uid: i.uid,
                                irrigations: i.irrigations,
                                Id: i.id,
                                field_id: i.field_id,
                                zone_id: i.zone_id

                            });
                        });
                    };
                });
            };
        })
        setCrops(Crops)
    }

    const getIrrig = () => {
        let Irrigations = [];
        farms.map(item => {
            let fields = item.fields;
            if (fields) {
                fields.map(itemCrop => {
                    let crops = itemCrop.crops;
                    if (crops) {
                        crops.map(i => {
                            let irrigations = i.irrigations
                            if (irrigations) {
                                irrigations.map(itemIrrig => {
                                    Irrigations.push({
                                        type: itemIrrig.type,
                                        address: itemIrrig.address,
                                        pivotShape: itemIrrig.pivot_shape,
                                        flowrate: itemIrrig.flowrate,
                                        lateral: itemIrrig.lateral,
                                        Uid: itemIrrig.uid,
                                        crop_id: itemIrrig.crop_id,
                                        zone_id: itemIrrig.zone_id,

                                    });

                                })
                            }
                        });
                    };
                });
            };
        })
        setIrrig(Irrigations)
    }


    useEffect(() => {
        getFieldsByUser();
    }, [])


    useEffect(() => {
        getFields();
        getCrops();
        getZones();
        getIrrig();
    }, [farms])

    const goProfile = () => {
        history.push(`/admin/user/${Uid}`);
        window.location.reload();
    }
    const goFarms = () => {
        history.push(`/admin/user/${Uid}/farms`);
        window.location.reload();
    }
    const goSensor = () => {
        history.push(`/admin/user/${Uid}/sensors`);
        window.location.reload();
    }

    const handleName = (e) => {
        setDataField({
          name: e.target.value,
        });
      };
     const handleUidFarm = (e) => {
        setDataField({
          farm_uid: e.target.value,
        });
      };

     const handleDescription = (e) => {
        setDataField({
          description: e.target.value,
        });
      };

     const validate = () => {
        let nameError = '';
        let farmError = '';
        if (!dataField.name) {
          nameError = 'not_empty';
          setErrors({ ...errors , nameError :nameError })
          return false
        } else {
          setErrors({nameError :""})
        }
        if (dataField.farm_uid == '') {
          farmError = 'not_empty';
          setErrors({ farmError })
          return false
        }
        if (dataField.farm_uid) {
          setErrors({ farmError: "" })
        }
        return true;
      };


    const  addField = () => {

        let data = {
          name: dataField.name,
          description: dataField.description,
          farmName: dataField.farmName,
          farm_uid: dataField.farm_uid,
        //   Latitude: this.state.Latitude,
        //   Longitude: this.state.Longitude
        }

        api.post('/field/add-field', data)
          .then(res => {

            if (res.data.type && res.data.type == "danger") {
                swal(`Error`, {
                    icon: "error",
                });
            }
            if (res.data.type && res.data.type == "success") {
                swal(`${t('field_added')}`, {
                    icon: "success",
                });
            }

          })
          .catch(() => {
            swal(`Error`, {
                icon: "error",
            });

          });
      }
    const  handleSubmit = (event) => {
    event.preventDefault()

    const isValid = validate();
    if (isValid) {
        addField()
    }
    }
   const resetForm = () => {
        setDataField({
          name: "",
          description: "",
          farm_uid: ""
        });
      };
    const renderAddSetup = () => {
        switch (elemValue) {
          case 'field':
            return (
              <div>
                <Card small className="h-100">
                  <CardHeader className="border-bottom">
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "flex-start",
                        width: "auto",
                        float: "left"
                      }}
                    >
                      <div>
                        <h6 className="m-0" style={{ textAlign: "left" }}>{t("field_setup")}</h6>{" "}
                      </div>
                    </div>
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "flex-end"
                      }}
                    >
                      <Button
                        onClick={handleSubmit}
                        theme="info"
                        className="mb-2 mr-1 btn btn-success"
                      >
                        <i class={`fa fa-check mx-2`}></i>
                        {t('save')}
                      </Button>
                      <Button
                        onClick={resetForm}
                        // theme="success"
                        className="mb-2 mr-1 btn btn-danger"
                      >
                        <i class={`fa fa-times mx-2`}></i>
                        {t('cancel')}
                      </Button>
                    </div>
                  </CardHeader>
                  <CardBody className="pt-0">
                    <div
                      style={{
                        display: "flex",
                        marginTop: "20px",
                        flexWrap: "wrap"
                      }}
                    >
                      <FieldSetupForm
                        handleDescription={handleDescription}
                        handleName={handleName}
                        handleUidFarm={handleUidFarm}
                        nameError={errors.nameError}
                        farmError={errors.farmError}
                        name={dataField.name}
                        farm_uid={dataField.farm_uid}
                        description={dataField.description}

                      />

                    </div>
                    <Row noGutters className="page-header py-4">
                      <PageTitle
                        sm="4"
                        title={t('my_fields')}
                        subtitle={t('my_fields')}
                        className="text-sm-left"
                      />
                    </Row>
                    {/* <Row className='px-2'>
                      <FieldList
                        Fields={getDataFields}
                        FieldsList={farmsData}
                      />

                    </Row> */}
                  </CardBody>
                </Card>
              </div>
            )
        //   case 'soil':
        //     return (
        //       <div style={{ position: "relative" }}>
        //         <Card small className="h-100">
        //           <CardHeader className="border-bottom">
        //             <div
        //               style={{
        //                 display: "flex",
        //                 justifyContent: "space-between",
        //                 alignItems: "center"
        //               }}
        //             >
        //               <h6 className="m-0">{t('soil_info')}</h6>
        //               <div
        //                 style={{
        //                   display: "flex",
        //                   justifyContent: "flex-end"
        //                 }}
        //               >
        //                 <Button
        //                   onClick={this.zoneHandleSubmit}
        //                   theme="info"
        //                   className="mb-2 mr-1 btn btn-success"
        //                 >
        //                   <i class={`fa fa-check mx-2`}></i>
        //                   {t('save')}
        //                 </Button>
        //                 <Button
        //                   onClick={this.resetFormSoil}
        //                   // theme="success"
        //                   className="mb-2 mr-1 btn btn-danger"
        //                 >
        //                   <i class={`fa fa-times mx-2`}></i>
        //                   {t('cancel')}
        //                 </Button>
        //               </div>
        //             </div>
        //           </CardHeader>
        //           <CardBody className="pt-0">
        //             <div
        //               style={{
        //                 display: "flex",
        //                 marginTop: "20px",
        //                 flexWrap: "wrap"
        //               }}
        //             >
        //               <FieldSoilForm
        //                 handleZoneName={this.handleZoneName}
        //                 handleSource={this.handleSource}
        //                 handleDepth={this.handleDepth}
        //                 handleSoilProprety={this.handleSoilProprety}
        //                 handleUidField={this.handleUidField}
        //                 handleUidZone={this.handleUidZone}
        //                 source={this.state.source}
        //                 zones={this.state.zonesData}
        //                 ZoneFunction={this.getDataZones}
        //                 fields={this.state.farmsData}
        //                 onChange={value => console.log(value)}
        //                 saved={this.state.soilCardSave}
        //                 nameError={this.state.nameError}
        //                 modal={false}
        //                 zoneName={this.state.zoneName}
        //                 listSoils={this.state.listSoils}
