import React, { useState, useEffect } from 'react'
import api from '../api/api'
import { Container, Row, Col, CardBody, CardHeader, CardFooter, FormGroup, FormInput, ButtonGroup, FormSelect } from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from 'react-i18next';
import { Modal } from "react-bootstrap";
import swal from 'sweetalert';
import { useHistory, useLocation } from 'react-router-dom/cjs/react-router-dom.min';
// import {Button, IconButton} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import { Recommend } from '@mui/icons-material';
import SmsIcon from '@mui/icons-material/Sms';
import SummarizeIcon from '@mui/icons-material/Summarize';
import HistoryIcon from '@mui/icons-material/History';
import PreviewIcon from '@mui/icons-material/Preview';
import DownloadIcon from '@mui/icons-material/Download';
import AddIcon from '@mui/icons-material/Add';
import Pagination from '../views/Pagination';
import { TextField, Button, Card, CardContent, Typography } from '@mui/material';
const CalculSimulation = () => {
    const { t, i18n } = useTranslation();
    const history = useHistory()
    const location = useLocation();
    // const {calculData } = location.state;

    const calculData = location.state ? location.state.calculData : null;
  if (!calculData) {
    return <div>No data available</div>;
  }
  return (
    <>
        <Container className="p-4">
            <Row noGutters className="page-header py-4">
            <PageTitle
                sm="4"
                title={t('Water Balance Simulation')}
                // subtitle={t('Water Balance Simulation')}
                className="text-sm-left"
            />
            </Row>
            {calculData && calculData.map((data, index) => (
                <Card key={index} className="my-3">
                    <CardContent>
                        <Typography variant="h6" gutterBottom>
                            {t(`Simulation ${index + 1}`)}
                        </Typography>
                        <Row>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="RU Pratique"
                                    value={data.ruPratique}
                                    // onChange={(e) => handleChange(index, 'ruPratique', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="RU Max"
                                    value={data.RUmax}
                                    // onChange={(e) => handleChange(index, 'RUmax', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="Effective Pluie"
                                    value={data.effPluie}
                                    // onChange={(e) => handleChange(index, 'effPluie', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="Effective Irrigation"
                                    value={data.effIrrig}
                                    // onChange={(e) => handleChange(index, 'effIrrig', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="Irrigation Area"
                                    value={data.irrigArea}
                                    // onChange={(e) => handleChange(index, 'irrigArea', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="Profondeur"
                                    value={data.profondeur}
                                    // onChange={(e) => handleChange(index, 'profondeur', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="number"
                                />
                            </Col>
                            <Col xs={12} sm={6} md={4}>
                                <TextField
                                    label="Planting Date"
                                    value={data.plantingDate}
                                    // onChange={(e) => handleChange(index, 'plantingDate', e.target.value)}
                                    fullWidth
                                    margin="normal"
                                    type="date"
                                    InputLabelProps={{ shrink: true }}
                                />
                            </Col>
                        </Row>
                    </CardContent>
                </Card>
            ))}

        </Container>
    </>
  )
}

export default CalculSimulation
