import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardHeader,
  CardFooter,
  Button,
  FormSelect,
} from "shards-react";
import "../assets/range-date-picker.css";
import rainSVG from "../images/icons/rain.svg";
import "react-calendar/dist/Calendar.css";
import PageTitle from "../components/common/PageTitle";
import Chart from "./../components/blog/Chart";
import "./../assets/Styles.css";
import { sideBarFields } from "../data/sideBarField";
import Progress from "react-circle-progress-bar";
import { useHistory} from "react-router-dom";
import "./Styles.css";
import evapo from "../images/evapo.png";
import api from '../api/api';
import { useTranslation } from "react-i18next";
import LoadingSpinner from '../components/common/LoadingSpinner'
import moment from "moment";
import useSensorData from "../utils/useSensorData";

import socketIOClient from "socket.io-client";
import momentTimezone from "moment-timezone";


const Field = () => {
  const ENDPOINT = process.env.REACT_APP_SOCKET;
  let userUid = JSON.parse(localStorage.getItem('user')).id
  const [cropState, setCS] = useState([]);
  const [sensors, setSensor] = useState([]);
  const fieldIndex = 0;
  const Uid = localStorage.getItem("Field");
  const [toggle, setToggle] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const { t, i18n } = useTranslation();
  const history = useHistory();
  const [data, setData] = useState(sideBarFields);
  const [selectedField, setSelectedField] = useState([]);
  const [sensorCode,setSensorCode] = useState('')
  const [msg,setMsg] = useState('')
  //const [allDataSensor, setAllDataSensor] = useState([]);
  const [weatherData, setWeatherData] = useState([]);
  const [agendaData, setAgendaData] = useState([
    {
      desc: "s",
      svg: "f",
      date: "d"
    },
    {
      desc: "s",
      svg: "f",
      date: "d"
    },
    {
      desc: "s",
      svg: "f",
      date: "d"
    },
    {
      desc: "s",
      svg: "f",
      date: "d"
    },
    {
      desc: "s",
      svg: "f",
      date: "d"
    },
    {
      desc: "s",
      svg: "f",
      date: "d"
    }
  ]);

 let allDataSensor = useSensorData('/sensor/sensor-update-data')

  const [recommendations, setRecmnd] = useState([])

  const [gradient, setGradient] = useState([
    { stop: 0.0, color: "#00bc9b" },
    { stop: 1, color: "#5eaefd" }
  ]);
  const [fieldState, setFS] = useState({
    state : '',
    resultState : 0
  });


  const [coord,setCoord] = useState({lat : '36.799746', lon : '10.186000'})


  // const getDataBySensorCode = () => {
  //   let data = {
  //     code : sensorCode
  //   }
  //   if(sensorCode !== ''){
  //     api.post('/datasensor/get-bycode',data)
  //     .then(response=>{
  //       let dataSensor = response.data.Datasensor 
  //       if(response && response.data.type === "success"){
  //         setSensorData(dataSensor)
  //         if (parseInt(dataSensor.charge * 100) < 40) {
  //                   setGradient([
  //                     { stop: 0.0, color: "red" },
  //                     { stop: 1, color: "#f70" }
  //                   ]);
  //                   setFS("Critical");
  //                 }
  //                 if (
  //                   parseInt(dataSensor.charge * 100) > 40 &&
  //                   parseInt(dataSensor.charge * 100) < 60
  //                 ) {
  //                   setGradient([
  //                     { stop: 0.0, color: "#0f7" },
  //                     { stop: 1, color: "#0d5" }
  //                   ]);
  //                   setFS("Optimal");
  //                 }
  //                 if (parseInt(dataSensor.charge * 100) > 60) {
  //                   setGradient([
  //                     { stop: 0.0, color: "#00bc9b" },
  //                     { stop: 1, color: "#5eaefd" }
  //                   ]);
  //                   setFS("Full");
  //                 }

  //       }
  //     }).catch(error=>{
  //       console.log(error)
  //     })

  //   }

  // }

useEffect(()=>{
      setInterval(() => {
          return allDataSensor
      }, 30000);

  },[])



   /*useEffect(()=>{

    const socket = socketIOClient(ENDPOINT);

    socket.on("sensorData", data => {
		console.log(data)
      //if(data.socketid == userUid){
      let sensorsData = data.data;
      let allDataSensorFromSocket = [];
      sensorsData.map(item => {
        let sensorsData = item.sensorsData
        if(sensorsData.length > 0){
          sensorsData.map(data => {
          allDataSensorFromSocket.push({
              id : data.id,
            code: data.code,
            uid : data.uid,
            time :data.time,
            temperature :data.temperature,
            humidity :data.humidity,
            pressure :data.pressure,
            charge :data.charge,
            adc :data.adc,
            ts :data.ts,
            mv1 : data.mv1,
            mv2 : data.mv2,
            mv3 :data.mv3,
            altitude :data.altitude
          });
          })
        }
      });

      setAllDataSensor(allDataSensorFromSocket)
    //}
  });



  },[])*/


  useEffect(() => {
    const fetchData = async () => {
      //TODO GET FROM URL
      const response = await api.get(`${process.env.REACT_APP_BASE_URL}/field/${Uid}/crops`)
      const newData = response;
      setCS(newData.data.crops);
      setSelectedField(newData.data.field)

      //if (newData.data.crops.length > 0) {}
      const dataSensor = await api.get(`${process.env.REACT_APP_BASE_URL}/field/${Uid}/sensors`)
      const result = dataSensor;
      setSensor(result.data.sensors);
      let sensor = result.data.sensors

      if (sensor.length > 0) {
        setSensorCode(sensor[0].code)
      }

    };


    const weather = async () => {
      await api.post("/weather/get-data",{type : 'day' ,lat : coord.lat , lon : coord.lon})

      .then((response) => {
        var today = {};
        let weatherData = response.data.data
        today.city = weatherData.name
        today.wind = weatherData.wind.speed;
        // today.rain = weatherData.rain['3h'];
        today.windDirection = weatherData.wind.deg;
        today.date = new Date(weatherData.dt*1000);
        today.temp = ((weatherData.main.temp)).toFixed(1);
        today.main = weatherData.weather[0].main;
        today.description = weatherData.weather[0].description;
        today.icon = weatherData.weather[0].icon;
        today.humidity = weatherData.main.humidity;
        today.pressure = weatherData.main.pressure;
        today.visibility = weatherData.visibility;

        setWeatherData(today)
      })
      .catch(function(error) {
        console.log(error);
      })
    }
    weather()
    fetchData();
  }, []);


  const getRecommendation = async () => {
    let field_id = selectedField.id;
    if(field_id !== undefined){
      await api.get(`/recommendation/recommendations/${field_id}`)
      .then(response => {
        let recommendations = response.data.recommendations
        setRecmnd(recommendations)
        setIsLoading(false)
      }).catch(err => {
        console.log(err)
      })
    }
  }

  useEffect(() => {
    getRecommendation();

  }, [selectedField])
  const filteredSensors =  allDataSensor.filter((sensor)=>{

    if (sensorCode !== "" ) {
      return (
        sensor.code.toLowerCase().includes(sensorCode.toLowerCase())

      )
    }

    // return sensor

})

useEffect(()=>{
  localStorage.setItem('code',sensorCode)

},[sensorCode])
  console.log(sensorCode)

  useEffect(()=>{
    allDataSensor.map(sensors=>{
      let MV1 = sensors.mv1;
      let MV2 = sensors.mv2;
      let MV3 = sensors.mv3;
      let result = (Number(MV1) +  Number(MV2) + Number(MV3)) / 3
      if((MV1 !== '0.000') && (MV2 !== '0.000') && (MV3 !== '0.000')){
        setFS({resultState : result, state : 'Full'})
      }
      if((MV1 !== '0.000') && (MV2 !== '0.000') && (MV3 === '0.000')){
        setFS({resultState : result, state : 'Optimal'})
      }
      if((MV1 === '0.000') && (MV2 === '0.000') && (MV3 === '0.000')){
        setFS({resultState : result, state :'Critical'})
      }
      if((MV1 !== '0.000') && (MV2 === '0.000') && (MV3 === '0.000')){
        setFS({resultState : result, state : 'Critical'})
      }
      if((MV1 === '0.000') && (MV2 !== '0.000') && (MV3 === '0.000')){
        setFS({resultState : result, state : 'Critical'})
      }
      if((MV1 === '0.000') && (MV2 === '0.000') && (MV3 !== '0.000')){
        setFS({resultState : result, state : 'Critical'})
      }
    })

  },[allDataSensor])



  return (
    <Container fluid className="main-content-container px-4">
      {/* Page Header */}
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          //title={data[fieldIndex].fieldName}
          title={selectedField.name}
          subtitle={t('overview')}
          className="text-sm-left"
        />
      </Row>

      {/* First Row of Posts */}
      <Row className="border-bottom mb-4">
        <Col lg="8" md="12" sm="12">
          <Row>
            <Col lg="6" md="12" sm="12" className="mb-4">
              {
               allDataSensor || sensors.length > 0
                ?
              <Card fluid style={{ minHeight: "330px" }}>
                {
                  isLoading
                    ?
                    <LoadingSpinner />
                    :
                    <>
                      <CardHeader className="border-bottom bg-light">
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "space-between",
                            height: "100%",
                            alignItems: "center"
                          }}
                        >
                          <h6 className="m-0">{t('active_sensors')}</h6>
                          <FormSelect style={{ width: "50%" }} value={sensorCode} onChange={evt => {setSensorCode(evt.target.value) }}>
                            <option value="">{t('select_sensor')}</option>
                            {
                              sensors.map((sensor, i) => {

                                return (
                                  <option key={i} value={sensor.code}>{sensor.code}</option>
                                )
                              })
                            }
                          </FormSelect>

                        </div>

                        {
                         filteredSensors && filteredSensors.map((data,indx)=>(
                           <div
                            key={indx}
                              style={{
                                display: "flex",
                                flexDirection: "row",
                                justifyContent: "space-evenly",
                                paddingTop: 10,
                                fontSize: 13
                              }}
                            >
                              <div style={{ textAlign: "center" }}>
                                <i
                                  class="fas fa-thermometer-three-quarters"
                                  style={{ fontSize: "20px" }}
                                ></i>
                                <p style={{ margin: 0 }}>{t('Temp.')} (°C)</p>
                                <p style={{ margin: 0 }}>{data.temperature}</p>
                              </div>
                              <div style={{ textAlign: "center" }}>
                                <i className="material-icons" style={{ fontSize: "20px" }}>&#xe798;</i>
                                <p style={{ margin: 0 }}>{t('Humidity')} (%) </p>
                                <p style={{ margin: 0 }}>{data.humidity}</p>

                              </div>
                              <div style={{ textAlign: "center" }}>
                                <i className="material-icons" style={{ fontSize: "20px" }}>&#xe94d;</i>
                                <p style={{ margin: 0 }}>{t('Pression')} (Pa)</p>
                                <p style={{ margin: 0 }}>{data.pressure}</p>
                              </div>
                              <div style={{ textAlign: "center" }}>
                                <i className="material-icons" style={{ fontSize: "20px" }}>&#xe94d;</i>
                                <p style={{ margin: 0 }}>{t('Altitude')} (Pa)</p>
                                <p style={{ margin: 0 }}>{data.altitude}</p>
                              </div>
                            </div>

                          ))
                        }
                      </CardHeader>

                          <CardBody className="pt-0">
                            <div style={{ paddingTop: 10 }}>
                            {
                                 filteredSensors && filteredSensors.map(data=>(
                              <table
                                //class="border-none"
                                style={{
                                  textAlign: "center",
                                  width: "100%",
                                  gap: 15
                                }}
                              >
                                <thead>
                                  <tr>
                                    <th><i className="material-icons">&#xe1ff;</i> {t('depth')} (cm)</th>
                                    <th><i className="material-icons">&#xe798;</i> {t('Humidity')} (%)</th>
                                  </tr>
                                </thead>

                                  <tbody>
                                    <tr>
                                      <td>20</td>
                                      <td>{data.mv1}</td>
                                    </tr>
                                    <tr>
                                      <td>40</td>
                                      <td>{data.mv2}</td>
                                    </tr>
                                    <tr>
                                      <td>60</td>
                                      <td>{data.mv3}</td>
                                    </tr>
                                  </tbody>


                              </table>
                                  ))
                                }
                            </div>
                          </CardBody>


                      <CardFooter className="p-0 d-flex justify-content-center align-items-center">
                        {
                          filteredSensors.map(data=>{
                            return <h6 className="font-weight-bold" style={{color :"#0daaa2"}}>

                              Last reading :  {moment(data.time).tz("Europe/Berlin").locale('En').format("MMM Do YYYY hh:mm a")}

                            </h6> 
                          })
                        }
                      </CardFooter> 
                    </>
                }
              </Card>
                :
                <Card  fluid style={{ minHeight: "330px" }}>
                  <CardHeader >

                  </CardHeader>
                  <CardBody>
                    <h5 style={{textAlign:'center',color:'#0daaa2'}}>Please use the app to install/register a sensor.</h5>

                  </CardBody>
                </Card>
              }
            </Col>
            <Col lg="6" md="12" sm="12" className="mb-4">
              <Card>
                <CardHeader className="border-bottom">
                  <h6 className="m-0">{t('soil_status')}</h6>
                </CardHeader>
                <CardBody className="pt-0">
                  <div
                    style={{
                      width: "100%",
                      height: "100%",
                      display: "flex",
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center"
                    }}
                  >
                    {
                      isLoading
                        ?
                        <LoadingSpinner />
                        :
                        <Progress
                          ballStrokeWidth={10}
                          //subtitle={cropState}
                          subtitle={`State : ${fieldState.state}`}
                           progress={parseFloat(fieldState.resultState).toFixed(0)}
                          suffix={"mm"}
                          gradient={gradient}
                        />

                    }
                    <Button
                      onClick={() => history.push("/AddField")}
                      //theme="success"
                      style={{ backgroundColor: "#0daaa2" }}
                      className="mb-2 mr-1"
                    >
                      {t('update_btn')}
                    </Button>
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Row>
            <Col lg="12" md="12" sm="12" className="mb-4">
              <Chart
                history={history}
                key={data[fieldIndex].id}
                title={data[fieldIndex].title}
                hasButton={data[fieldIndex].hasButton}
                sensorLevels={data[fieldIndex].RCLevels}
                dataSet={data[fieldIndex].dataSet}
                labels={data[fieldIndex].labels}
                chartName={data[fieldIndex].chartName}
              />
            </Col>

          </Row>
          <Row>
            <Col lg="12" md="12" sm="12" className="mb-4">
              <Card fluid>
                <CardHeader className="border-bottom">
                  <h6 className="m-0">{t('agenda')}</h6>
                </CardHeader>
                <CardBody className="pt-2 d-flex justify-content-center align-items-center">
                  {agendaData.map(element => {
                    return (
                      <>
                        <div
                          style={{
                            fontSize: 13,
                            display: "flex",
                            justifyContent: "center",
                            flexDirection: "column",
                            alignItems: "center"
                          }}
                        >
                          <img
                            src={rainSVG}
                            style={{ width: "20%", height: "100%" }}
                            alt="svg"
                          />
                          <div style={{ marginLeft: 5 }}>
                            <p style={{ margin: 0 }}>23/01/2022</p>
                            <p style={{ margin: 0 }}>Rainy day</p>
                          </div>
                        </div>
                      </>
                    );
                  })}
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Col>
        <Col lg="4" md="12" sm="12" >
          <Card className="mb-4" fluid style={{ minHeight: "330px" }} >
            <CardHeader className="border-bottom">
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                  alignItems: "center"
                }}
              >
                <h6 className="m-0">{t('today_weather')}</h6>
              </div>
            </CardHeader>
            <CardBody className="pt-4 d-flex justify-content-between align-items-center flex-column">
              <div
                style={{
                  width: "100%",
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-evenly",
                  paddingTop: 10
                }}
              >
                <div style={{ textAlign: "center" }}>
                  <i
                    class="fas fa-thermometer-three-quarters"
                    style={{ fontSize: "32px" }}
                  ></i>
                  <p style={{ margin: 0 }}>{t('Temp.')}</p>
                  <p style={{ margin: 0 }}>
                    {weatherData.temp}°C 
                  </p>
                </div>
                <div style={{ textAlign: "center" }}>
                  <i
                    class="fas fa-cloud-showers-heavy"
                    style={{ fontSize: "32px" }}
                  ></i>
                  <p style={{ margin: 0 }}>{t('Rain')}</p>
                  <p style={{ margin: 0 }}>
                    {weatherData.rain ? weatherData.rain : 0}mm
                  </p>
                </div>
                <div style={{ textAlign: "center" }}>
                  <img
                    src={evapo}
                    alt="evapoIcon"
                    style={{ width: "33px", height: "33px" }}
                  />
                  <p style={{ margin: 0 }}>ET0</p>
                  <p style={{ margin: 0 }}>{weatherData.dewPoint}1.8 W/m²</p>
                </div>
              </div>
              <Button
                onClick={() => {
                  history.push("/Weather");
                }}
                className="mb-2 mr-1"
                style={{ backgroundColor: "#0daaa2", margin: 0, fontSize: 12, cursor: "pointer", textAlign: "center" }}
              >
                {t('weather_forecast')}
              </Button>
            </CardBody>
          </Card>
          {
            recommendations.map(recomnd => {
              let dateTime = recomnd.created_at;
              let recmndDate = moment(dateTime).format('MMMM Do YYYY')
              return (

                <Card className="mb-4" style={{ minHeight: "470px" }}>
                  <CardHeader className="border-bottom d-flex justify-content-between align-items-center">
                    <p
                      style={{
                        color: '#0daaa2',
                        textTransform: 'uppercase',
                        margin: 0,
                        padding: 3,
                        fontSize: "14px",
                        backgroundColor: "#ebebeb",
                        borderRadius: 20

                      }}
                    > <i className="material-icons">&#xe88e;</i> {recomnd.title}
                    </p>
                    <div>
                      {recmndDate}
                    </div>
                  </CardHeader>
                  <CardBody
                    className="pt-0 d-flex flex-column justify-content-around align-items-center"
                    style={{ height: "100%" }}
                  >
                    <Row>
                      <div className='detail' style={{ paddingTop: 10 }}>
                        <p>{recomnd.description}</p>
                      </div>

                    </Row>
                    <Row>
                      <Col lg='12' md='12' sm='12'>
                        <Button
                          onClick={() => history.push(`/recommendations/${selectedField.id}`)}
                          style={{ backgroundColor: "#0daaa2" }}
                          className="mb-2 mr-1"
                        >
                          {t('View all')}
                        </Button>

                      </Col>
                    </Row>
                    <Row>

                    </Row>
                  </CardBody>
                </Card>
              )
            })
          }
        </Col>
      </Row>
      <Row>
        {
          cropState.map((item, indx) => {
            let msg = ""
            if (item.irrigations == 0) msg = "NO IRRIGATION FOUNDED"
            return (
              <Col lg="4" md="12" sm="12" className="mb-4">
                <Card small className="h-100">
                  <CardHeader className="border-bottom">
                    <h6 className="m-0">{t('field_status')}</h6>
                  </CardHeader>
                  <CardBody className="pt-0">
                    <Row className="border-bottom py-2 bg-light">
                      <Col style={{ textAlign: "center" }}>
                        <p className="m-0">{data[fieldIndex].crop}{t('crop_type')} : {item.type}</p>
                      </Col>
                    </Row>
                    <Row className="py-2">
                      <Col>
                        <p style={{ margin: 0 }}>
                          Coordinates : {data[fieldIndex].Latitude}
                        </p>
                        <p style={{ margin: 0 }}></p>
                        <p style={{ margin: 0 }}>
                          Area : {item.zone} m²
                        </p>
                      </Col>
                    </Row>
                    <p style={{ color: "#EB493B" }}>
                      {msg}
                    </p>
                    {
                      item.irrigations.map(irrigation => (
                        <Row className="border-bottom border-top py-2">
                          <Col>
                            <p style={{ margin: 0 }}>{t('Irrigation_system_type')}: <span style={{ textTransform: 'capitalize' }}>{ irrigation.type}</span></p>
                            <p style={{ margin: 0 }}>
                              {t('FlowRate')} : {irrigation.flowrate}
                            </p>
                          </Col>
                        </Row>
                      ))
                    }


                  </CardBody>
                </Card>
              </Col>
            )
          })
        }
      </Row>


    </Container>
  );
};

export default Field;
