import moment from 'moment';
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { useTranslation } from "react-i18next";

const SensorHistoryChart = ({ data }) => {
  const [state, setState] = useState(null);
  const { t, i18n } = useTranslation();

  const getChartData = () => {
    let labels = [];
    let dataMv1 = [];
    let dataMv2 = [];
    let dataMv3 = [];
    let temperature = [];
    let humidity = [];
    let tempSubSoil = [];
    let altitude = [];
    let pressure = [];
    let array = []

    data &&
      data.forEach(sensorData => {
        const dateTime = moment(sensorData.time); // use the full timestamp
              const hour = dateTime.format('MMM Do HH:00'); // format date as "MMM Do HH:00"
        dataMv1.push(parseFloat(sensorData.mv1).toFixed(2));
        dataMv2.push(parseFloat(sensorData.mv2).toFixed(2));
        dataMv3.push(parseFloat(sensorData.mv3).toFixed(2));
        temperature.push(parseFloat(sensorData.temperature).toFixed(2));
        altitude.push(parseFloat(sensorData.altitude));
        pressure.push(parseFloat(sensorData.pressure).toFixed(2) / 1000);
        tempSubSoil.push(parseFloat(sensorData.ts).toFixed(2));
        humidity.push(parseFloat(sensorData.humidity).toFixed(2));
        labels.push(hour);

      });
      labels.reverse();
      dataMv1.reverse();
      dataMv2.reverse();
      dataMv3.reverse();
      temperature.reverse();
      altitude.reverse();
      pressure.reverse();
      tempSubSoil.reverse();
      humidity.reverse();
    const state = {
      labels: labels,
      datasets: [
        {
          label: 'Temperature',
          lineTension: 0.5,
          borderWidth: 2,
          data: temperature,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#f46835"
        },
        {
          label: 'Humidity',
          lineTension: 0.5,
          borderWidth: 2,
          data: humidity,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#2D40E5"
        },
        {
          label: 'Pressure',
          lineTension: 0.5,
          borderWidth: 2,
          data: pressure,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#4BC0C0"
        },
        {
          label: 'Altitude',
          lineTension: 0.5,
          borderWidth: 2,
          data: altitude,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#B7990D"
        },
        {
          label: 'Temperature subsoil',
          lineTension: 0.5,
          borderWidth: 2,
          data: tempSubSoil,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#8621e4"
        },
        {
          label: 'Niveau 1',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv1,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#FC5566"
        },
        {
          label: 'Niveau 2',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv2,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#3CB371"
        },
        {
          label: 'Niveau 3',
          lineTension: 0.5,
          borderWidth: 2,
          data: dataMv3,
          fill: false,
          backgroundColor: "rgba(75,192,192,0.2)",
          borderColor: "#1E90FF"
        },
      ]
    };
    setState(state);
  };

  useEffect(() => {
    getChartData();
  }, [data]);


  const legendCallback = (chart) => {
    const legendItems = chart.legend.legendItems || chart.getLegendItems();
    legendItems.forEach((item) => {
      const checkbox = document.createElement('input');
      checkbox.type = 'checkbox';
      checkbox.checked = true; // By default, checkboxes are checked
      checkbox.id = item.datasetIndex; // Use dataset index as id
      checkbox.addEventListener('change', () => {
        chart.data.datasets[item.datasetIndex].hidden = !chart.data.datasets[item.datasetIndex].hidden;
        chart.update();
      });
      const label = document.createElement('label');
      label.htmlFor = item.datasetIndex;
      label.appendChild(document.createTextNode(item.text));
      const container = document.createElement('div');
      container.appendChild(checkbox);
      container.appendChild(label);
      item.text = ''; // Remove default label
      item.line = ''; // Remove default line
      item.hidden = false; // Make sure they're visible by default
      item.textAlign = 'left'; // Align checkbox and label to the left
      item.lineWidth = 0; // Remove the line in the legend
      item.pointStyle = 'rect'; // Use rectangle for points
      item.render = (args) => {
        return container;
      };
    });
  };

  return (
    <Line
      data={state}
      options={{
        title: {
          display: true,
          text: `${t('Sensor History')}`,
          fontSize: 20
        },
       legend: {
          display: true,
          position: 'bottom',
          align: 'start', // Align legend items to the start
          labels: {
            usePointStyle: true, // Use point style instead of lines
            generateLabels: (chart) => {
              if (chart.legend.legendItems && chart.legend.legendItems.length > 0) {
                legendCallback(chart);
              }
              return chart.legend.legendItems;
            }
          }
        },
        scales: {
        yAxes: [{
          ticks: {
            beginAtZero: true,
            max: 100,
            min: 0,
            stepSize: 25,
            fontColor: '#000',
            fontSize: 14,
          },
          gridLines: {
            color: 'rgba(0,0,0,0.2)',
            lineWidth: 1,
          },
          scaleOverride: true,
          scaleSteps: 4,
          scaleStepWidth: 25,
          scaleStartValue: 0
        }],
        xAxes: [{
          ticks: {
            autoSkip: false,
            maxRotation: 90,
            fontColor: '#000',
            fontSize: 10,
          },
          gridLines: {
            color: 'rgba(0,0,0,0.2)',
            lineWidth: 1,
          },
        }]
      }
      }}
    />
  );
};

export default SensorHistoryChart;
