import React , {useState,useEffect} from 'react'
import api from '../api/api'
import { Container, Row, Col, Card,CardBody,CardHeader,CardFooter, FormGroup, FormInput, ButtonGroup, Button, FormSelect } from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from 'react-i18next';
import { Modal } from "react-bootstrap";
import swal from 'sweetalert';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';


const FieldsManagement = () => {
    const { t, i18n } = useTranslation();

    const [fields,setFields] = useState([])
    const [farms,setFarms] = useState([])
    const [sensors,setSensors] = useState([])
    const [users,setUsers] = useState([])

    const [reports,setReports] = useState([])
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const getFields = async () => {
        await api('/admin/fields')
        .then(response => {
            let fieldsData = response.data.fields
            let farmsData = response.data.farms
            let sensorsData = response.data.sensors
            let usersData = response.data.users

            setFields(fieldsData)
            setFarms(farmsData)
            setSensors(sensorsData)
            setUsers(usersData)
        }).catch(error => {
            console.log(error)
        })
    }

    const generateReport = async () => {
      await api.get('/createBulletin')
        .then(result=>{
          if(result){
              swal({
                  icon: "success",
              });

          }
        }).catch(error =>{
            console.log(error)
        })
    }

    const getSingleField =  async (fieldId) => {
        await api.post('/admin/fields/single-field',{fieldId})
        .then(response=>{
            if(response.data.type === "success"){
                let fieldData = response.data.field
                let allReports = fieldData.reports
                setReports(allReports)
                handleShow(true)
            }
        })
        .catch(error =>{
                console.log(error)
        })
    }

    useEffect(()=>{
        getFields()
    },[])

    const handleClick = async (file) => {

        try {
            let data = {filename : file}    
          const response = await api.post('/report/download',data);
          const blob = await response.blob();
          const url = URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = 'historique.pdf';
          document.body.appendChild(a);
          a.click();
        } catch (error) {
          console.error(error);
        }
      };
      const downloadLastReport = async (file) => {

        try {
            let data = {filename : file}    
          const response = await api.post('/report/download',data);
          const blob = await response.blob();
          const url = URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = 'historique.pdf';
          document.body.appendChild(a);
          a.click();
        } catch (error) {
          console.error(error);
        }
      };

      const history = useHistory()
      const goToField = (uid) => {
        if(uid){
        localStorage.setItem(
            "Field",
            uid
          );
        } else {
          localStorage.setItem("Field", 0);
        }
            history.push(`/Fields/${uid}`)
      }
      const goToHistory = (code) => {

            history.push(`/my-history/${code}`)
      }

  return (
    <>
    <Container className="p-4">
    <Row noGutters className="page-header py-4">
                    <PageTitle
                        sm="4"
                        title={t('list_fields')}
                        subtitle={t('list_fields')}
                        className="text-sm-left"
                    />
                </Row>
                <Row className="d-flex justify-content-center p-4">
                    <Col lg="3" md="12" sm="12">
                        <Button outline onClick={() => generateReport()}><i className='material-icons'>&#xe145;</i> Generate Reports</Button>

                    </Col>
                </Row>
                <Card>
                <table className="table mb-0 text-center table-responsive-lg table-bordered">
                        <thead className="bg-light">
                            <tr>                                    
                                <th scope="col" className="border-0">{t('user')}</th>

                                <th scope="col" className="border-0">{t('farms')}</th>
                                <th scope="col" className="border-0">{t('list_fields')}</th>
                                <th scope="col" className="border-0">{t('crop_type')}</th>
                                <th scope="col" className="border-0">{t('crop_variety')}</th>
                                <th scope="col" className="border-0">{t('sensor_code')}</th>
                                <th scope="col" className="border-0">{t('All Reports')}</th>
                                <th scope="col" className="border-0">{t('History')}</th>
                                {/* <th scope="col" className="border-0"></th> */}

                            </tr>
                        </thead>
                        <tbody>
                           {

                              fields && fields.map(field=>{
                                let croptype = "-"
                                let variety  = "-"
                                let farmName = "-"
                                let sensorCode = "-"
                                let User = "-"

                                let crops = field.crops
                                crops.forEach(crop=>(
                                    croptype = crop.croptypes.crop
                                    ))
                                    crops.forEach(crop=>{
                                        if(crop.varieties && Object.keys(crop.varieties).length !== 0){
                                             variety = crop.varieties.crop_variety

                                        }
                                    })
                                    farms && farms.map(farm =>{
                                        if(farm.id === field.farm_id){
                                            farmName = farm.name
                                        }

                                    })

                                    sensors && sensors.map(sensor=>{
                                        if(sensor.field_id === field.id){
                                            sensorCode = sensor.code
                                        }
                                    })

                                  return (
                                      <tr>
                                          <td>{User}</td>
                                          <td>{farmName}</td>
                                          <td>{field.name}</td>
                                          <td>{croptype}</td>
                                          <td>{variety}</td>
                                          <td>{sensorCode}</td>

                                       {/* <td><Button outline onClick={() => downloadLastReport()}><i className='material-icons'>&#xf090;</i></Button></td> */}
                                        <td>

                                            <Button outline onClick={() => getSingleField(field.id)}><i className='material-icons'>&#xe8f4;</i></Button>
                                            {/* <Button outline onClick={() => goToField(field.uid)}><i className='material-icons'>&#xe8f4;</i></Button> */}

                                            </td>
                                            <td>
                                            <Button outline onClick={() => goToHistory(sensorCode)}><i className='material-icons'>&#xe889;</i></Button>

                                            </td>
                                        </tr>
                                  )
                               })
                            }

                        </tbody>
                    </table>

                </Card>
    </Container>
    <Modal size="lg" show={show} onHide={handleClose}>
          <Modal.Header className="d-flex flex-column" >
            <Modal.Title>List Reports</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Row>
          {
                                        reports.map(report=>{

                                        return (
                                            <Col lg="4" md="12" sm="12">
                                                <div className='d-flex justify-content-between align-items-center m-1' style={{border:"1px solid #eee",borderRadius:12,padding:6}}>
                                                    <h5>► Field Report {report.filename.slice(37,58)}</h5>
                                                    <button style={{background:"none",border:"none",outline:"none",padding:8 ,margin:5}} onClick={() => handleClick(report.filename)}> <i className='material-icons' style={{fontSize:"28px"}}>&#xf000;</i> </button>

                                                </div>

                                            </Col>
                                        )

                                        })
                                    }

            </Row>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>

          </Modal.Footer>
        </Modal>                     
    </>
  )
}

export default FieldsManagement
