    switch (steps) {
      case 0:
        return (
          addFarm()
        )
      case 1:
        return (
          addField()
        )
      case 2:
        return (
          addZone()
        )
      case 3:
        return (
          addCrop()
        )
      case 4:
        return (
          addIrrigation()
        )
      default:
        break;
    }
  }
  return (
    <Container fluid className="main-content-container px-3">
      <>
        <Modal size="lg" show={show} onHide={handleClose}>
          <Modal.Header className="d-flex flex-column" >
            <Modal.Title>Farm Configuration</Modal.Title>
            <p style={{ textDecoration: "underline", fontSize: 12, fontWeight: "bold" }}>Each step of this form is dependent on the previous step, so please fill out the fields in the correct order to avoid errors or an incomplete submission.</p>
            <div style={{ width: "100%", height: 25 }}>
              <ProgressBar animated variant="success" now={percentageComplete} label={`${percentageComplete}%`} />

            </div>

          </Modal.Header>
          <Modal.Body>
            {configProcess()}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            {
              steps !== 5
                ?
                <Button disabled={steps === 1 && coords.Latitude === ""} variant="primary" onClick={handleSubmit}>
                  Save & continue process
                </Button>
                :
                null

            }
            {
              steps > 0 && steps < 5
                ?
                <Button variant="primary" onClick={prevStep}>
                  Previous
                </Button>
                :
                null
            }
          </Modal.Footer>
        </Modal>
      </>
      {/* Page Header */}
      <Row noGutters className="page-header py-2 mb-4 d-flex justify-content-between flex-nowrap align-items-center border-bottom">
        <PageTitle title={t('overview')} className=" mb-1" />
        <Dropdown open={toggle} toggle={toggleDropDown} className="d-table mr-5">
          <div className="d-flex justify-content-center align-items-center ">
            <Button id="TooltipExample" theme="info" className="rounded-circle" style={{ height: 50, width: 50 }} onClick={toggleDropDown}  >
              <i className="material-icons" style={{ fontSize: 30, display: "flex", justifyContent: "center", alignItems: "center", color: "#fff" }}>&#xe145;</i>
              <Tooltip
                placement="left"

                open={open}
                target="#TooltipExample"
                toggle={toggleToolTip}
              >
                {t('add_element')}
              </Tooltip>
            </Button>

          </div>
          <DropdownMenu right style={{ zIndex: "10" }} >
            <Link to="#" onClick={handleShow}>
              <DropdownItem>
                {t('farms')}
              </DropdownItem>

            </Link>
            <Link  to="#" onClick={() => ToAddSensorPage()}>
              <DropdownItem>
                {t('sensors')}
              </DropdownItem>
            </Link>
            <Link  to="#" onClick={() => ToWaterBalancePage()}>
              <DropdownItem>
                {t('water_balance')}
              </DropdownItem>
            </Link>
          </DropdownMenu>
        </Dropdown>
      </Row>
      {/* Small Stats Blocks */}
      <Row className="mt-4">
        <Col lg="4" md="6" sm="6" className="mb-4">
          <p style={{ margin: 0 }}>{t('field_stats')}</p>
          <Card small className="stats-small h-100">
            <CardBody className="p-2 d-flex  justify-content-center align-items-center">
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flexWrap: "wrap",
                  width: "100%"
                }}
              >
                {/* <Stats /> */}
                {smallStats.map((stats, idx) => (
                  <>
                    <SmallStats
                      FilterByStatus={FilterByStatus}
                      style={{
                        borderColor: "blue",
                        borderWidth: 2,
                        borderStyle: "solid"
                      }}
                      id={`small-stats-${idx}`}
                      variation="1"
                      chartData={stats.datasets}
                      chartLabels={stats.chartLabels}
                      label={stats.label}
                      value={stats.value}
                      icon={stats.icon}
                      state={stats.state}
                    />
                    {idx != 2 ? (
                      <hr
                        style={{
                          backgroundColor: "#ebebeb",
                          width: "1px",
                          height: "75%"
                        }}
                      />
                    ) : (
                      <></>
                    )}
                  </>
                ))}
              </div>
            </CardBody>
          </Card>
        </Col>
        <Col lg="4" md="6" sm="6" className="mb-4">
          <p style={{ margin: 0 }}>{t('sensor_stats')}</p>
          <Card small className="stats-small h-100">
            <CardBody className="p-2 d-flex justify-content-center align-items-center">
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flexWrap: "wrap",
                  width: "100%"
                }}
              >
                {smallStats2.map((stats, idx) => (
                  <>
                    <SmallStats
                      ToSensorPage={ToSensorPage}
                      style={{
                        borderColor: "blue",
                        borderWidth: 2,
                        borderStyle: "solid"
                      }}
                      id={`small-stats-${idx}`}
                      variation="1"
                      chartData={stats.datasets}
                      chartLabels={stats.chartLabels}
                      label={stats.label}
                      value={stats.value}
                      icon={stats.icon}
                      state={stats.state}
                    />
                    {idx != 2 ? (
                      <hr
                        style={{
                          backgroundColor: "#ebebeb",
                          width: "1px",
                          height: "75%"
                        }}
                      />
                    ) : (
                      <></>
                    )}
                  </>
                ))}
              </div>
            </CardBody>
          </Card>
        </Col>
        <Col lg="4" md="12" sm="12" className="">
          {/* <h4 style={{ marginBottom: "-15px" }}>{t('map')}</h4> */}
          <LeafletMap data={layerFarm} sensor={sensorStats} draw={configMap.draw} zoom={mapConfig.zoom} center={mapConfig.center} fromAcrion={mapConfig.fromAction} />
        </Col>
      </Row>
      <Row className="d-flex justify-content-center align-items-center">
      </Row>
      <Row>
        <FilterFields
          smallStats={smallStats}
          sensorsData={sensorsData}
          crops={crops}
          filteredByStatus={farms}
          sensorStats={sensorStats}
          fieldStats={fieldStats}
        />
      </Row>
    </Container>
  )
}


export default Overview;
