import React, { useState, useEffect ,useRef} from 'react'
import { Container, Row, Col, Card, FormGroup, FormInput, ButtonGroup, Button, Modal, ModalHeader, ModalBody, FormSelect } from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import api from '../../src/api/api'
import { useTranslation } from 'react-i18next';
import Pagination from '../views/Pagination';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';


const SensorsManagement = () => {

    const history = useHistory()

    const [toggle, setToggle] = useState(false)

    const [sensorsPerPage] = useState(8)
    const [currentPage, setCurrentPage] = useState(1);
    const [SearchCode, setSearchCode] = useState('')

    const [code ,setCode] = useState('')
    const [sensorId ,setSensorId] = useState('')

    const [singleSensor,setSingleSensor] = useState('')

    const [userUid,setUserUid] = useState('')
    const [users,setUsers] = useState([])

    const [allSensors, setAllSensors] = useState([])
    const [existUsers,setExistUsers] = useState([])

    const [existSuppliers,setExistSuppliers] = useState([])
    const [supplierUid,setSupplierUid] = useState('')

    const [supplier,setSupplier] = useState(false)


    const { t, i18n } = useTranslation();

    const getAllSensors = async () => {
        await api.get('/admin/all-sensors')
            .then(response => {
                let sensorsData = response.data.sensors
                setAllSensors(sensorsData)
            }).catch(err => {
                console.log(err)
            })
    }

    const getExistUsers = async () => {
        await api.get('/admin/exist-users')
            .then(response => {
                var Data = response.data.users
                setExistUsers(Data)
                setSupplier(false)
                setSupplierUid('')
            }).catch(err => {
                console.log(err)
            })
    }

    const getExistSuppliers = async () => {
        await api.get('/admin/exist-suppliers')
            .then(response => {
                var Data = response.data.suppliers
                setExistSuppliers(Data)
                setSupplierUid(Data.uid)
                setSupplier(true)
                setUserUid('')
            }).catch(err => {
                console.log(err)
            })
    }
    const getUsersList = async () => {
        let data = await api.get("/admin/users")
            .then(response => {
                let usersData = response.data.users
                setUsers(usersData)
            })
    }

    useEffect(() => {
        getAllSensors()
        getUsersList()
    }, [])

    const filteredSensors = allSensors.filter(sensor => {
        if (SearchCode !== '') {
            return (
                sensor.code.toLowerCase().includes(SearchCode.toLowerCase())
            )
        }
        return sensor
    })

    // Pagination 

    const indexOfLastPost = currentPage * sensorsPerPage;
    const indexOfFirstPost = indexOfLastPost - sensorsPerPage;
    const currentSensors = filteredSensors.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = pageNumber => setCurrentPage(pageNumber);

    const assignSensorToUser = (userUid,supplierUid) =>{
        let data ={
            code : code,
            user_uid : userUid,
            supplier_uid : supplierUid
        }
        api.post('/admin/edit-sensor',data)
        .then(response =>{
            if(response.data.type === 'success'){
                swal(`${t('sensor_updated')}`, {
                    icon: "success",
                });
                setToggle(false)
                getAllSensors()
            }
            if(response.data.type === 'danger'){
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'Error'
                })

            }
        }).catch(err=>{
            swal({
                icon: 'error',
                title: 'Oops...',
                text: 'Error'
            })
        })
    }

    const getSingleSensor = async (sensorid,type) => {

        await api.get(`/admin/single-sensor/${sensorid}`)
            .then(res => {
                let sensorData = res.data.sensor
                setSingleSensor(sensorData) 
                setCode(sensorData.code)  
                    existUsers.map(user=>{
                        if(user.id === sensorData.user_id){
                            setUserUid(user.uid)
                        }
                    })
                    existSuppliers.map(supplier=>{
                        if(supplier.id === sensorData.supplier_id){
                            setSupplierUid(supplier.uid)
                        }
                    })
            }).catch(error => {
                swal({
                    title: "Error",
                    icon: "error",

                });

            })
        setToggle(!toggle)
        if(type === "User") {
            getExistUsers()
        } if(type === 'Supplier') {
            getExistSuppliers()
        }
    }

    // const editSensorIdUser = (userSelectedUid) => {
    //     let hasNotSensor = users.length;


    //     existUsers.map(user =>{
    //         if(user.id === singleSensor.user_id){
    //             console.log(user.id +'==='+ singleSensor.user_id)
    //             setUserUid(user.uid)
    //             hasNotSensor = hasNotSensor - 1 ;
    //         }
    //     })
    //     if(hasNotSensor == users.length){
    //         setUserUid("")
    //     }

    // }

    const resetForm = () => {
        setTimeout(()=>{
            setUserUid('')
            setSupplierUid('')

        },1000)
    }


   const  handleCalculSensor = () => {
    api.post('/calcul/add-sensor-calcul')
    .then(response=>{
        console.log(response.data.message)
        if(response.data.type === 'success'){
            swal(`${t('calcul_added')}`, {
                icon: "success",
            });
        }
        if(response.data.type === 'danger'){
            swal({
                icon: 'error',
                title: 'Oops...',
            })

        }
    }).catch(err=>{
        swal({
            icon: 'error',
            title: 'Oops...',
        })
    })
   }

   const handleDelete = async sensorUid => {
    console.log(sensorUid)

    let data = {
        sensor_uid: sensorUid,
    }
    await api.delete('/admin/delete-sensor', data)
        .then(response => {
            if (response.data.type && response.data.type == "danger") {
                swal({
                    title: "Cannot Delete Sensor",
                    icon: "warning",
                });
            }
            if (response.data.type == "success") {
                getAllSensors();

            }
        }).catch(error => {
            swal({
                title: "Cannot Delete Sensor",
                icon: "Error",
                text: 'Error'

            });
        })
    }

const confirmDelete = sensorUid => {

    swal({
        title: "Are you sure?",
        text: "Once deleted, you will not be able to recover this sensor!",
        icon: "warning",
        buttons: true,
        dangerMode: true,
    })
        .then((Delete) => {
            if (Delete) {
                handleDelete(sensorUid)
                swal(" Sensor has been deleted!", {
                    icon: "success",
                });
            }
        }).catch(error => {
            swal({
                title: "Cannot Delete sensor",
                icon: "Error",
                text: 'Error'

            });
        })

}

    return (
        <>
            <Container>
                <Row noGutters className="page-header py-4">
                    <PageTitle
                        sm="4"
                        title={t('list_sensors')}
                        subtitle={t('list_sensors')}
                        className="text-sm-left"
                    />
                </Row>
                <Row>
                    <PageTitle
                        sm="4"
                        subtitle={t('search_sensors')}
                        className="text-sm-left"
                    />
                </Row>
                <Row form className="d-flex justify-content-center">
                    <Col md="3" className="form-group">
                        <FormGroup>
                            <div className="d-flex">
                                <FormInput
                                    value={SearchCode}
                                    onChange={(e) => setSearchCode(e.target.value)}
                                    id="search"
                                    placeholder="Search By code" />

                            </div>
                        </FormGroup>
                    </Col>
                </Row>
                <Row>
                    <PageTitle
                        sm="4"
                        subtitle={t('my_actions')}
                        className="text-sm-left"
                    />
                </Row>
                <Row form className="py-2 d-flex justify-content-center">
                    <ButtonGroup>
                        <Button outline onClick={() => history.push('/admin/add-sensor')}>Add Sensor</Button>
                        <Button outline onClick={() => handleCalculSensor()}>Calcul</Button>

                    </ButtonGroup>

                </Row>
                <Card>
                    <table className="table mb-0 text-center table-responsive-lg">
                        <thead className="bg-light">
                            <tr>
                                <th scope="col" className="border-0">{t('code')}</th>
                                <th scope="col" className="border-0">{t('sensor')}</th>
                                <th scope="col" className="border-0">{t('user')}</th>
                                <th scope="col" className="border-0">{t('zone')}</th>
                                <th scope="col" className="border-0">{t('supplier')}</th>
                                <th scope="col" className="border-0"></th>
                                <th scope="col" className="border-0"></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                currentSensors.map(sensor => {
                                    let nameUser = ''
                                    users.map(user=>{
                                        if(user.id=== sensor.user_id){
                                            nameUser = user.name
                                        }
                                    })
                                    return (
                                        <tr>
                                            <td>{sensor.code}</td>
                                            <td>{sensor.id}</td>
                                            <td>{nameUser}</td>
                                            <td>{sensor.zone_id}</td>
                                            <td>{sensor.supplier_id}</td>
                                            <td>
                                                {
                                                    sensor.deleted_at === null
                                                        ?
                                                        <ButtonGroup size="sm" className="mr-2">
                                                            <Button title="Edit" onClick={() => { history.push(`/admin/edit-sensor/${sensor.id}`) }} squared><i className="material-icons">&#xe3c9;</i></Button>
                                                            <Button title="History" onClick={() => { history.push(`/my-history/${sensor.code}`)}} squared theme="info"><i className="material-icons">&#xe889;</i></Button>
                                                            <Button title="Delete" onClick={() => {confirmDelete(sensor.uid) }} squared theme="danger"><i className="material-icons">&#xe872;</i></Button>

                                                        </ButtonGroup>
                                                        :
                                                        null
                                                }

                                            </td>
                                            {
                                                sensor.deleted_at === null
                                                    ?
                                                    <td>
                                                        <ButtonGroup size="sm" className="mr-2">
                                                            <Button  outline onClick={() => getSingleSensor(sensor.id,'User')}>Assign to user</Button>
                                                            <Button  outline onClick={() => getSingleSensor(sensor.id,'Supplier')}>Assign to supplier</Button>
                                                        </ButtonGroup>

                                                    </td>

                                                    :
                                                    null
                                            }
                                            <td></td>

                                        </tr>

                                    )
                                })
                            }

                        </tbody>
                    </table>

                </Card>
                <Row className="py-4 justify-content-center">
                    <Pagination usersPerPage={sensorsPerPage} totalUsers={allSensors.length} paginate={paginate} />

                </Row>

            </Container>
                    <Modal centered={true} open={toggle}>
                        <ModalHeader className="d-flex justify-content-between align-items-center">
                            <div>
                                Sensor code : {code}
                            </div>
                            <div
                                style={{
                                    display: "flex",
                                    justifyContent: "flex-end",

                                }}
                            >
                                <Button
                                    // theme="success"
                                    onClick={()  => assignSensorToUser(userUid ,supplierUid)}
                                    className="mb-2 mr-1 btn btn-success"
                                >
                                    <i class={`fa fa-check mx-2`}></i>
                                    {t('save')}
                                </Button>
                                <Button
                                    // theme="success"
                                    className="mb-2 mr-1 btn btn-danger"
                                    onClick={() => {resetForm() ;setToggle(false)} }
                                >
                                    <i class={`fa fa-times mx-2`}></i>
                                    {t('cancel')}
                                </Button>
                            </div>
                        </ModalHeader>
                        <ModalBody>
                            <Row className='d-flex justify-content-center'>
                                <Col lg='6' md='8' sm='8'>
                                    <FormGroup>
                                        <label htmlFor="users">Select a user to assign sensor</label>
                                        {
                                            supplier === true
                                            ?
                                            <FormSelect
                                            id="users"
                                            value={supplierUid}
                                            onChange={(e) => setSupplierUid(e.target.value) }
                                        >
                                            <option value="">Select Supplier</option>

                                            {
                                                existSuppliers.map(supplier =>{
                                                    return <option value={supplier.uid}>{supplier.name}</option>

                                                })
                                            }
                                        </FormSelect>

                                            :
                                            <FormSelect
                                            id="users"
                                            value={userUid}
                                            onChange={(e) => {setUserUid(e.target.value)} }
                                        >
                                            <option value="">Select User</option>

                                            {
                                                existUsers.map(user =>{

                                                    return <option value={user.uid}>{user.name}</option>

                                                })
                                            }
                                        </FormSelect>
                                        }
                                    </FormGroup>
                                </Col>
                            </Row>


                        </ModalBody>
                    </Modal>

        </>
    )
}

export default SensorsManagement
