import React from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardHeader,
  Button,
  Form,
  FormGroup,
  FormInput,
  FormCheckbox
} from "shards-react";
import PageTitle from "../components/common/PageTitle";
import "./../assets/Styles.css";
import Map from "./leafletMap";
import "./Styles.css";
import api from '../api/api';
import { withTranslation  } from "react-i18next";
import FarmList from "./FarmList";
import LeafletMap from "./map";

class AddFarm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name_group: null,
      name: null,
      description: null,
      added : false,
      nameError : "",
      classInputError: "",
      msgServer:"",
      classMsg:"",
      displayMsg:"hide",
      iconMsg:"info",
      farmList :[],
      Latitude : "",
      Longitude :"",
      positionError : "",
      layer : [],
      draw : {
        polygon: true,
        circle: false,
        rectangle: false,
        polyline: false,
        marker: true,
        circlemarker: true
      },
      edit :{
        delete: true,
        edit: true
      },
      layerType : "",
      checked: false

    };
  }

    componentDidMount = async () => {
      this.getFarms()
    }

    getFarms =async () => {
      await api.get('/farm/farms').then(res =>{
        const DataFarm = res.data;
        this.setState({farmList: DataFarm.farms});

    })
    }

    _onCreated = e => {
      let type = e.layerType;
      this.setState({layerType : type})
      let layer = e.layer;
      if (type === "polygon") {
        let coords = layer._latlngs[0];
        const Coordinates = coords.map((coord) => ({
          Lat: coord.lat,
          Long: coord.lng,
        }));
        this.setState({layer : JSON.stringify(Coordinates)})
      } else {
        this.setState({Latitude : layer.getLatLng().lat}) ;
        this.setState({Longitude : layer.getLatLng().lng}) ;
      }

    };

    checkPositon = () => {
      if(this.state.checked === true){
        navigator.geolocation.getCurrentPosition((position) => {
         this.setState({Latitude : position.coords.latitude}) ;
         this.setState({Longitude : position.coords.longitude}) ;
        })
      }
      if(this.state.checked === false){
         this.setState({Latitude : ""}) ;
         this.setState({Longitude : ""}) ;
      }
    }

    handleChange = () => {
      this.setState({
        checked: !this.state.checked
      },
      () => this.checkPositon()
      );

    }

  validate = () => {
    let nameError = '';
    let classInputError = '';
    let positionError = ""
    if (!this.state.Latitude || !this.state.Longitude) {
      positionError = 'Please select a farm position';
      this.setState({positionError});
      return false;
    }
    if (!this.state.name || this.state.name == "") {
      nameError = 'not_empty';
      classInputError = 'is-invalid'
      this.setState({nameError});
      this.setState({classInputError});
      return false;
    }
    return true

  };


  handleSubmit = (event) => {
    event.preventDefault()
    const isValid = this.validate();
    if (isValid) {
       this.addFarm()
    }else{
      return false;
    }
   }

  addFarm = async () => {

    const name = this.state.name
    const name_group = this.state.name_group
    const description = this.state.description
    let Coordinates = this.state.layer
    let Latitude = this.state.Latitude
    let Longitude = this.state.Longitude

    let user = JSON.parse(localStorage.getItem('user'));
    let user_uid = user.id

    await api.post('/farm/add-farm', { name,name_group,description,user_uid,Coordinates,Latitude,Longitude })
      .then(res => {
        if(res.data.errors) {
          let errorsData = res.data.errors
          errorsData.map((item, indx) => {
            this.setState({msgServer:item.msg})
          })
          this.setState({classMsg:"danger"})
          this.setState({displayMsg:"show"})
        }
        if(res.data.type && res.data.type == "danger") {
          this.setState({msgServer:res.data.message})
          this.setState({classMsg:"danger"})
          this.setState({displayMsg:"show"})
        }
        if(res.data.type && res.data.type == "success") {
          this.getFarms()
          this.setState({added : true} , this.resetForm())
          this.setState({msgServer:"farm_added"})
          this.setState({classMsg:"success"})
          this.setState({displayMsg:"show"})
          this.setState({iconMsg:"check"})
        }

      }).catch((error)=>{
        this.setState({msgServer:"fail_add_farm"})
        this.setState({classMsg:"danger"})
        this.setState({displayMsg:"show"})
      })
    }

    resetForm = () => {
      this.setState({
        name: "",
        name_group : "",
        description: "",
      });

      setTimeout(() => {
        this.setState({displayMsg:"hide"})
      }, 5000);
    };


    render() {
      const { t } = this.props;
      return (
      <Container fluid className="main-content-container px-4">
        {/* Page Header */}
        <Row noGutters className="page-header py-4">
          <PageTitle
            sm="4"
            title={t('add_farm')}
            subtitle={t('add_farm')}
            className="text-sm-left"
          />
        </Row>

        <Row>
          <Col lg="12" md="12" sm="12" className="mb-4">
          <div className={`mb-0 alert alert-${this.state.classMsg} fade ${this.state.displayMsg}`}>
          <i class={`fa fa-${this.state.iconMsg} mx-2`}></i> {t(this.state.msgServer)}
          </div>
            <Card small className="h-100">
              <CardHeader className="border-bottom">
                <div
                  style={{
                    display: "flex",
                    justifyContent: "flex-start",
                    width: "auto",
                    float: "left"
                  }}
                >
                  <h6 className="m-0">{t('farm_setup')}</h6>{" "}
                  </div>
                  <div
                  style={{
                    display: "flex",
                    justifyContent: "flex-end",

                  }}
                >
                  <Button
                    onClick={this.handleSubmit}
                   // theme="success"
                    className="mb-2 mr-1 btn btn-success"
                  >
                    <i class={`fa fa-check mx-2`}></i>
                    {t('save')}
                  </Button>
                  <Button
                    onClick={this.resetForm}
                   // theme="success"
                    className="mb-2 mr-1 btn btn-danger"
                  >
                    <i class={`fa fa-times mx-2`}></i>
                    {t('cancel')}
                  </Button>
                  </div>
              </CardHeader>
              <CardBody className="pt-0">
                <div
                  style={{
                    display: "flex",
                    marginTop: "20px",
                    flexWrap: "wrap"
                  }}
                >
                  <Col lg="6" sm="12" md="12" >
                    <Form>
                      <Row form>
                        <Col md="6" className="form-group">
                          <p style={{ margin: "0px" }}> {t('name_farm')}</p>
                          <FormInput
                            className={this.state.classInputError} 
                            value={this.state.name}
                            placeholder={t('name_farm')}
                            required
                            onChange={evt => {
                              this.setState({
                                name: evt.target.value
                              });
                            }}

                            />
                            <div className="invalid-feedback" >{this.state.nameError}</div>
                        </Col>
                        <Col md="6" className="form-group">
                          <p style={{ margin: "0px" }}>{t('group_name')}</p>
                          <FormInput
                            value={this.state.name_group}
                            placeholder={t('group_name')}
                            onChange={evt => {
                              this.setState({
                                name_group: evt.target.value
                              });
                            }}
                          />
                        </Col>
                      </Row>
                      <FormGroup>
                        <p style={{ margin: "0px" }}>{t('desc')}</p>
                        <textarea
                          value={this.state.description}
                          onChange={evt =>
                            this.setState({
                              description: evt.target.value
                            })
                          }
                          style={{ height: "275px" }}
                          class="form-control"
                          placeholder={t('desc')}
                        ></textarea>
                      </FormGroup>
                      <FormGroup className="my-4">

                  <FormCheckbox
                  checked={this.state.checked}
                  onChange={this.handleChange}
                    toggle
                     >
                    Assign farm position to my current position
                 </FormCheckbox>
                </FormGroup>  
                    </Form>
                  </Col>
                  <Col lg="6" sm="12" md="12" >
                    <Row className="d-flex justify-content-center flex-column">
                  <div style={{ textAlign:"center" ,margin:"5px" }}>
                     <i className='material-icons mx-2'>&#xe88e;</i>
                      You need to add farm position on the map
                  </div>
                           <h6 style={{color:"tomato",textAlign:'center'}}> {this.state.positionError}</h6>
                  <LeafletMap _onCreated={this._onCreated} draw={this.state.draw} edit={this.state.edit} data={this.state.farmList}/>

                    </Row>

                  </Col>
                </div>
              </CardBody>
            </Card>
          </Col>
        </Row>
          <Row noGutters className="page-header py-4">
            <PageTitle
              sm="4"
              title={t('my_farms')}
              subtitle={t('my_farms')}
              className="text-sm-left"
            />
            </Row>
            <Row>
              <Col lg="12" md="8" sm="12" className="mb-4">
                 <Card small>
                   <CardHeader>{t('active_farms')}</CardHeader>
                   <CardBody>
                      <FarmList 
                        farmList={this.state.farmList}
                        Farms={this.getFarms}
                      />

                   </CardBody>
                 </Card>
              </Col>
          </Row>
      </Container>
    );
  }
}

export default withTranslation()(AddFarm);
