import api from '../api/api'
import swal from 'sweetalert'
import { useTranslation } from "react-i18next";
import RangeDatePicker from '../components/common/RangeDatePicker';



const CropList = ({ cropsList, Crops, Fields, Zones }) => {


    const { t, i18n } = useTranslation();

    const [toggle, setToggle] = useState(false);

    const [type, setType] = useState('');
    const [prevType, setPrevType] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [field, setField] = useState('');
    const [zone,setZone] = useState('');
    const [gdd, setGdd] = useState('');

    const [msgServer, setMsg] = useState("")

    const [classMsg, setCmsg] = useState("")
    const [displayMsg, setDispMsg] = useState("hide")
    const [iconMsg, setIconMsg] = useState("info")


    const [SingleCrop, setSingleCrop] = useState([])



    const getSingleCrop = async (cropUid) => {


        let data = {
            crop_uid: cropUid,
        }

        await api.post('/crop', data)
            .then(res => {
                let CropData = res.data.crop
                setSingleCrop(CropData)
                setType(CropData.type)
                setPrevType(CropData.previous_type)
                setGdd(CropData.ggd_maturity)
                let start = CropData.growth_date_start;
                let end = CropData.growth_date_end;
                setStartDate(start.slice(0, 19))
                setEndDate(end.slice(0, 19))
                Fields.map((fieldData) => {
                    if (CropData.field_id == fieldData.Id) {
                        setField(fieldData.Uid)
                    }
                })
                Zones.map((zoneData) => {
                    if (CropData.zone_id === zoneData.Id) {
                        setZone(zoneData.Uid)
                    }
                })
            }).catch(error => {
                swal({
                    title: "Error",
                    icon: "error",

                });

            })
        setToggle(!toggle)
    }

    const handleEdit = (cropUid) => {


        let data = {
            type: type,
            previous_type: prevType,
            ggd_maturity: gdd,
            growth_date_start: startDate,
            growth_date_end: endDate,
            crop_uid: cropUid,
            field_uid: field,
            zone_uid:zone
        }


        api.post('/crop/edit-crop', data)
            .then(response => {
                if (response.data.type == "success") {
                    swal(`${t('crop_updated')}`, {
                        icon: "success",
                    });
                    setToggle(false)
                    Crops();
                }
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'error_edit_crop'
                    })
                    return false;
                }
            }).catch(error => {
                swal({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'error_edit_crop'
                })
            })


    }

    const handleDelete = async cropUid => {

        let data = {
            crop_uid: cropUid,
        }
        await api.delete('/crop/delete-crop', { data: data })
            .then(response => {
                if (response.data.type && response.data.type == "danger") {
                    swal({
                        title: `${t('cannot_delete')}`,
                        icon: "warning",
                    });
                }
                if (response.data.type == "success") {
                    Crops()
                    setMsg(`${t('delete_success')}`)
                    setCmsg("success")
                    setDispMsg("show")
                    setIconMsg("check")
                    hideMsg()
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete_crop')}`,
                    icon: "error",
                    text: 'Error'

                });
            })
    }
    const hideMsg = () => {
        setTimeout(() => {
            setDispMsg("hide")
        }, 3000);

    }


    const confirmDelete = cropUid => {

        swal({
            title: `${t('are_you_sure')}`,
            text: `${t('confirm_delete')}`,
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((Delete) => {
                if (Delete) {
                    handleDelete(cropUid)
                    swal(`${t('delete_success')}`, {
                        icon: "success",
                    });
                }
            }).catch(error => {
                swal({
                    title: `${t('cannot_delete_crop')}`,
                    icon: "error",
                    text: 'Error'

                });
            })

    }

    return (
        <>
            <div className={`mb-0 alert alert-${classMsg} fade ${displayMsg}`}>
                <i class={`fa fa-${iconMsg} mx-2`}></i> {t(msgServer)}
            </div>
            <table className="table mb-4 text-center tabel-responsive-lg">
                <thead className="bg-light">
                    <tr>
                        <th scope="col" className="border-0">{t('crop_type')}</th>
                        <th scope="col" className="border-0">{t('address')}</th>
                        <th scope="col" className="border-0">{t('name_field')}</th>
                        <th scope="col" className="border-0">{t('name_zone')}</th>
                        <th scope="col" className="border-0"></th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cropsList.map((item, indx) => {
                            let nameField = "";
                            let nameZone = "";
                            Fields.map((fieldData) => {
                                if (fieldData.Id == item.field_id) {
                                    nameField = fieldData.title
                                }

                            })
                            Zones.map((zoneData) => {
                                if (zoneData.Id == item.zone_id) {
                                    nameZone = zoneData.name
                                }

                            })
                            return (
                                <tr>
                                    <td>{item.type}</td>
                                    <td>{item.address}</td>
                                    <td>{nameField}</td>
                                    <td>{nameZone}</td>
                                    <td>
                                        <ButtonGroup size="sm" className="mr-2">
                                            <Button onClick={() => getSingleCrop(item.Uid)} squared><i className="material-icons">&#xe3c9;</i></Button>
                                            <Button onClick={() => confirmDelete(item.Uid)} squared theme="danger"><i className="material-icons">&#xe872;</i></Button>
                                        </ButtonGroup>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
            <Modal size='lg' centered={true} open={toggle} >
                <ModalHeader closeAriaLabel>
                    <h6 className="m-0">{t('edit_crop')}</h6>{" "}
                    <div
                        style={{
                            display: "flex",
                            justifyContent: "flex-end",

                        }}
                    >
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-success"
                            onClick={() => handleEdit(SingleCrop.uid)}
                        >
                            {t('save')}
                            <i class={`fa fa-check mx-2`}></i>
                        </Button>
                        <Button
                            // theme="success"
                            className="mb-2 mr-1 btn btn-danger"
                            onClick={() => setToggle(false)}

                        >
                            {t('cancel')}
                            <i class={`fa fa-times mx-2`}></i>
                        </Button>
                    </div>
                </ModalHeader>
                <ModalBody>
                    <Form>
                        <Row form>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('name_field')}</p>
                                <FormSelect
                                    value={field}
                                    onChange={(e) => setField(e.target.value)}
                                >
                                    {
                                        Fields.map((field) => (
                                            <option value={field.Uid}>{field.title}</option>
                                        ))
                                    }
                                </FormSelect>
                            </Col>
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('name_zone')}</p>
                                <FormSelect
                                    value={zone}
                                    onChange={(e) => setZone(e.target.value)}
                                >
                                    {
                                        Zones.map((zone) => (
                                            <option value={zone.Uid}>{zone.name}</option>
                                        ))
                                    }
                                </FormSelect>
                            </Col>            
                            <Col md="6" className="form-group">
                                <p style={{ margin: "0px" }}>{t('crop_type')}</p>
                                <FormInput
                                    value={type}
                                    onChange={(e) => setType(e.target.value)}
                                />
                                <div className="invalid-feedback">{t('no_empty')}</div>
                            </Col>
                            <Row form>
                                <Col md="6" className="form-group">
                                    <p style={{ margin: "0px" }}>{t('prev_type')}</p>
                                    <FormInput
                                        value={prevType}
                                        onChange={(e) => setPrevType(e.target.value)}
                                        placeholder={t('prev_type')}
                                    />
                                </Col>
                                <Col md="6" className="form-group">
                                    <p style={{ margin: "0px" }}>{t('gdd')}</p>
                                    <FormInput
                                        value={gdd}
                                        onChange={(e) => setGdd(e.target.value)}
                                    />
                                </Col>
                            </Row>
                            <Row form>

                                <Col md="6" className="form-group">
                                    <p style={{ margin: "0px" }}>{t('start_date')}</p>
                                    <FormInput
                                        type="datetime-local"
                                        placeholder="Start Date"
                                        value={startDate}
                                        onChange={(e) => setStartDate(e.target.value)}
                                    />
                                </Col>
                                <Col md="6" className="form-group">
                                    <p style={{ margin: "0px" }}>{t('end_date')}</p>
                                    <FormInput
                                        type="datetime-local"
                                        placeholder="End Date"
                                        value={endDate}
                                        onChange={(e) => setEndDate(e.target.value)}
                                    />
                                </Col>
                            </Row>
                        </Row>
                    </Form>
                </ModalBody>
            </Modal>



        </>
    )
}

export default CropList
