import React, { useEffect, useState } from 'react'
import { Link, useParams, useHistory } from 'react-router-dom'
import api from '../api/api'
import { Container, Card, Row,Col, CardHeader ,Nav,NavItem,NavLink ,CardBody,Button,Form,FormInput,FormSelect,FormGroup} from 'shards-react'
import PageTitle from '../components/common/PageTitle'
import { useTranslation } from "react-i18next";
import FieldList from '../views/FieldList'
import AddField from '../views/AddField'
import { Carousel } from "react-responsive-carousel";
import CropList from '../views/CropList'
import ZoneList from '../views/ZoneList'
import IrrigationList from '../views/IrrigationList'
import swal from 'sweetalert'
import FieldSetupForm from '../components/FieldSettingForms/FieldSetupForm'
import FieldCropForm from '../components/FieldSettingForms/FieldCropForm'
import FieldSoilForm from '../components/FieldSettingForms/FieldSoilForm'
import FieldIrrigationForm from '../components/FieldSettingForms/FieldIrrigationForm'
import DripForm from '../components/FieldSettingForms/dripForm'
import PivotForm from '../components/FieldSettingForms/pivotForm'
import LateralForm from '../components/FieldSettingForms/lateralForm'


const FieldsList = () => {

    const { t, i18n } = useTranslation();
    const history = useHistory()

    const params = useParams();
    const [fields, setFields] = useState([]);
    const [listSoil,setListSoil] = useState([])
    const [listCrop,setListCrop] = useState([])
    const [listIrrigations,setListIrrigations] = useState([])

    const [crops, setCrops] = useState([]);
    const [zones, setZones] = useState([]);
    const [irrigations, setIrrig] = useState([]);
    const [farms, setFarms] = useState([])
    let Uid = params.uid;
    const [elemValue,setView] = useState('field')
    const [dataField,setDataField] = useState({
        name : "",
        farm_uid :"",
        description :""
    })
    const [errors,setErrors] = useState({
        farmError :"",
        nameError :""
    })

    const [checked,setChecked] = useState(false)
    const [cropData,setCropData] = useState({
      field_uid :"",
      zone_uid :"",
      cropType:"",
      variety: '',
      cropVariety: [],
      Profondeur: "",
      days: "",
      plantingDate: "",
      rootDepth:"",
      ecartInter :"",
      ecartIntra :"",
      density :"",
      ruPratique :"",
      kcList: []
    })  
    const [soilData,setSoilData] = useState({
      field_uid :"",
      zone_uid :"",
      zoneName:"",
      source :"1",
      soilProperty:"",
      soilType :"",
      RUmax: "",
      effPluie: "",
    })

    const [irrigData,setIrrigData] = useState({
       irrigType : "",
          zone_uid: "",
          crop_uid: "",
          flowrate: "",
          irrigated_already: "" ,
          name: "",
          pivot_shape:"",
          irrigation_syst:"", 
          pivot_length: "",
          pivot_coord: "",
          full_runtime: "",
          lateral:"",
          drippers : "",
          effIrrig: "",
          pumpFlow : ""
    })


    const getFieldsByUser = async () => {
        let url = `/admin/user/${Uid}/fields`;
        await api.get(url)
            .then(response => {
                if (response.data.type === "success") {
                    let FarmsData = response.data.farms
                    console.log(FarmsData)
                    setFarms(FarmsData);
                }

            }).catch(error => {
                console.log(error)
            })
    }

    const getFields = () => {
        let Fields = [];
        farms.map(item => {
            let fields = item.fields;
            if (fields) {
                fields.map(field => {
