import React, { useEffect, useState } from "react";
import {
  Row,
  Col,
  Form,
  FormInput,
  FormSelect,
  FormGroup
} from "shards-react";
import RangeDatePicker from "../common/RangeDatePicker";
import axios from "axios";
import api from '../../api/api';

import { useTranslation } from "react-i18next";

function FieldCropForm(props) {
  const { t, i18n } = useTranslation();


  const [listCrop, setListCrop] = useState([])
  const [allVarieties,setAllVarieties] = useState([])
  const [cropData,setCropData] = useState({
    cropType:"",
    variety: '',
    cropVariety: [],
    Profondeur: "",
    days: "",
    plantingDate: "",
    kcList: []
  })
  const [otherInfo, setOI] = useState({
    type: props.cropType,
    plantDate:props.plantingDate,
    rootDepth:props.rootDepth,
    days:props.days,
    cropVariety:props.cropVariety,
    //  previous_type: props.previous_type,
    startDate: props.startDate,
    endDate: props.endDate,
    field_uid: props.field_uid
    // GDD: props.gdd
  });
  // useEffect(()=>{
  //   const getZones= async () => {
  //     const response = await api.get('/zones')
  //     const newData = await response.data;
  //     setData(newData.farms.fields);
  //     console.log(newData.farms.fields)
  //   };
  //   getZones();

  // },[])

  useEffect(() => {


    let CardValues = JSON.stringify(otherInfo);
    props.onChange(CardValues);
  }, [props.saved]);


  useEffect(() => {
    const getCropType = async () => {
      try {
        await api.get('/croptype/list-crop')
          .then(response => {
            if (response) {
              let dataCrop = response.data.Croptype
              setListCrop(dataCrop)
            }
          })

      } catch (error) {
        console.log(error)
      }
    }
    const getVarieties = async () => {
      try {
          await api.get('/varieties/get-varieties')
              .then(response => {
                  if (response.data.type === "success") {
                      let listVarieties = response.data.Varieties
                      setAllVarieties(listVarieties)

                  }
              }).catch(error => {
                  console.log(error)
              })

      } catch (error) {
          console.log(error)
      }
  }
    getVarieties()
    getCropType()
  }, [])

  const handleCropPick = (e) => {
    e.preventDefault()
    props.handleCropType(e)
    const crop = listCrop.find(
      (crop) => crop.crop == e.target.value
      );
      setOI({...otherInfo, type : crop.crop})
      // setOI({...otherInfo, days : crop.total})
      // setOI({...otherInfo, plantDate : crop.plant_date})
      // setOI({...otherInfo, rootDepth : crop.root_max})
      props.handleDays(crop.total)
      props.handleRootDepth(crop.root_max)
      props.handlePlantingDate(crop.plant_date.slice(0, 11).replace('T', ''))
      let varieties = []
      if (crop) {
        const variety = allVarieties.map((variety) => {
          if (variety.crop_id === crop.id) {
            varieties.push({
              variety: variety.crop_variety
            })
          }

        });
        setCropData({
          ...cropData,
            cropType: crop.crop,
            variety: '',
            cropVariety: varieties,
            Profondeur: crop.root_max,
            days: crop.total,
            plantingDate: crop.plant_date.slice(0, 11).replace('T', ''),
            kcList: crop.all_kc
          });


    }
};
const handleVarietyPick = (e) => {
  e.preventDefault();
  const variety = allVarieties.find(
      (variety) => variety.crop_variety === e.target.value

  )
  props.handleCropVariety(e)
  props.handleDays(variety.total)
  props.handleRootDepth(variety.root_max)
  props.handlePlantingDate(variety.plant_date.slice(0, 11).replace('T', ''))
  if (variety) {
      setCropData({
          ...cropData,
          cropType: "",
          variety: variety.crop_variety,
          Profondeur: variety.root_max,
          days: variety.total,
          plantingDate: variety.plant_date.slice(0, 11).replace('T', ''),
          kcList: variety.all_kc
      });
  }
};

console.log(props)
  return (
    <Col lg="12" sm="12" md="6">
      <Form>
        <Row form>
          <Col lg='6' md="12" sm="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('crop_zone')}</p>
            <FormSelect
              onChange={props.handleZone}
              placeholder={t('crop_zone')}
            >
              <option>{t('select_zone')}</option>

              {
                props.zones.map((item, indx) => {
                  return <option value={item.Uid}>{item.name}</option>
                })
              }
            </FormSelect>
          </Col>
          <Col lg='6' md="12" sm="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('crop_field')}</p>
            <FormSelect
              value={otherInfo.field_uid}
              onChange={props.handleUidField}
              placeholder={t('crop_zone')}
            >
              <option>{t('select_field')}</option>
              {
                props.fields.map((item, indx) => {
                  return <option value={item.Uid}>{item.title}</option>
                })
              }
            </FormSelect>
          </Col>
        </Row>
        <Row form>
          <Col lg='6' md="12" sm="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('crop_type')}</p>
            <FormSelect
              onChange={handleCropPick}
              placeholder={t('crop_type')}
              value={otherInfo.type}
            >
              <option value="">Select Crop</option>
              {
                listCrop.map(crop => {
                  return (
                    <option value={crop.crop}>{crop.crop}</option>

                  )
                })
              }
            </FormSelect>
          </Col>
          <Col lg='6' md="12" sm="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('prev_type')}</p>
            <FormInput
              onChange={props.handlePrevType}
              placeholder={t('prev_type')}
            />
          </Col>
        </Row>
        <Row form>
          <Col lg='4' sm="12" md="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('gdd')}</p>
            <FormInput
              value={otherInfo.GDD}
              placeholder={t('gdd')}
              required
              onChange={props.handleGGD}
            />
          </Col>
          {/* <Col lg='4' md="12" sm="12" className="form-group">
            <p style={{ margin: "0px" }}>{t('growth_date')}</p>
            <RangeDatePicker

              onChange={value =>
              props.handleDate(value)
              }
              EndingDatePlaceHolder="Harvest Date"
              StartingDatePlaceHolder="Planting Date"
            />
          </Col> */}
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('crop_variety')}</p>
              <FormSelect value={cropData.variety || ""} onChange={handleVarietyPick} id="cropVariety">
                <option value="">{t('crop_type')}</option>
                  {
                      cropData.cropVariety.map(variety => (
                          <option value={variety.id}>{variety.variety}</option>
                      ))
                  }
              </FormSelect>
            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('profondeur')} (m)</p>
              <FormInput value={props.rootDepth} onChange={e => props.handleRootDepth(e.target.value)} id='z' placeholder={t('profondeur')}
              />

            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('Days')}</p>

              <FormInput value={props.days} id='days' onChange={e => props.handleDays(e.target.value)} placeholder={t('Days')} />

            </FormGroup>

          </Col>
          <Col lg="4" md="8" sm="8">
            <FormGroup>
              <p style={{ margin: "0px" }}>{t('growing_season')}</p>
              <FormInput type="date" value={props.plantingDate} onChange={e => props.handlePlantingDate(e.target.value)} id='days' />

            </FormGroup>

          </Col>
        </Row>
      </Form>
    </Col>
  );
}

FieldCropForm.defaultProps = {
  crop: "Crop A",
  prevCrop: "Crop B"
};

export default FieldCropForm;
